UPDATE script_texts SET content_loc4='文森特！你和你可怜的家族跟肯瑞托那些贫穷的幸存者一样，是不可能打败我和我儿子的。' WHERE entry=-1033010; /* content: Vincent!  You and your pathetic ilk will find no more success in routing my sons and I than those beggardly remnants of the Kirin Tor. */
UPDATE script_texts SET content_loc4='如果你不愿为我的主人服务，不愿提供敌人的消息的话……' WHERE entry=-1033011; /* content: If you will not serve my Master with your sword and knowledge of his enemies... */
UPDATE script_texts SET content_loc4='谁敢打扰阿鲁高的仆人？准备面对你的末日吧！' WHERE entry=-1033013; /* content: Who dares interfere with the Sons of Arugal? */
UPDATE script_texts SET content_loc4='%s消失了。' WHERE entry=-1033014; /* content: %s vanishes. */
