UPDATE script_texts SET content_loc4='我必须去和其它信徒汇合了。在我尝试用其他方法治愈贫瘠之地之前还有很多工作要做。后会有期，勇士们！' WHERE entry=-1043016; /* content: We must go and gather with the other Disciplies. There is much work to be done before I can make another attempt to restore the Barrens. Farewell, brave souls! */
UPDATE script_texts SET content_loc4='我受到攻击！快让这个$N离我远点！' WHERE entry=-1043017; /* content: Attacked! Help get this $N off of me! */
UPDATE script_texts SET content_loc4='快帮帮我！' WHERE entry=-1043018; /* content: Help! */
