UPDATE script_texts SET content_loc4='炸弹将在5秒内爆炸。清理通道！向后撤！' WHERE entry=-1090011; /* content: Incoming blast in 5 seconds. Clear the tunnel! Stay back! */
UPDATE script_texts SET content_loc4='手雷来啦！' WHERE entry=-1090012; /* content: FIRE IN THE HOLE! */
UPDATE script_texts SET content_loc4='干得好！没有你的帮助，我是无法抵挡穴居人的！' WHERE entry=-1090013; /* content: Well done! Without your help I would have never been able to thwart that wave of troggs. */
UPDATE script_texts SET content_loc4='你听见什么了吗？' WHERE entry=-1090014; /* content: Did you hear something? */
UPDATE script_texts SET content_loc4='我听到那里有点动静。' WHERE entry=-1090015; /* content: I heard something over there. */
UPDATE script_texts SET content_loc4='穴居人！挡住它们，我要准备爆破了！' WHERE entry=-1090016; /* content: More troggs! Ward them off as I prepare the explosives! */
