UPDATE db_script_string SET content_loc4='我不敢相信爸爸不让我留下你妹妹。' WHERE entry=2000005315; /* content: I can\'t believe dad won\'t let me keep your sister. */
UPDATE db_script_string SET content_loc4='别担心，我会帮你找到个舒适的家。' WHERE entry=2000005316; /* content: Don\'t worry, I\'ll find a good home for ya. */
