UPDATE script_texts SET content_loc2='Faites place !' WHERE entry=-1000845; /* content: Make way! */
UPDATE script_texts SET content_loc2='Un moment dont je me souviendrai toute ma vie.' WHERE entry=-1000847; /* content: A moment I shall remember for always. */
UPDATE script_texts SET content_loc2='Vous êtes une inspiration pour nous tous.' WHERE entry=-1000848; /* content: You are an inspiration to us all, sir. */
UPDATE script_texts SET content_loc2='Soyez braves, mes amis. Le reptile se battra sauvagement. Avec l\'énergie du désespoir... Quand vous serez prêts, faites-le-moi savoir.' WHERE entry=-1000849; /* content: Be brave, friends. The reptile will thrash wildly. It is an act of desperation. When you are ready, give me the word. */
UPDATE script_texts SET content_loc2='EN AVANT !' WHERE entry=-1000850; /* content: Onward! */
UPDATE script_texts SET content_loc2='Courez tant que vous pouvez, Majesté. Elle n\'est pas ce que vous croyez...' WHERE entry=-1000851; /* content: Majesty, run while you still can. She is not what you think her to be... */
