UPDATE script_texts SET content_loc2='Youhou ! On sort enfin d\'ici. Mais ça va être difficile. Ouvrez l\'œil.' WHERE entry=-1047000; /* content: Woo hoo! Finally getting out of here. It\'s going to be rough though. Keep your eyes peeled for trouble. */
UPDATE script_texts SET content_loc2='C\'est là-haut que Charlga Trancheflanc a établi sa résidence. Satanée vieille mégère.' WHERE entry=-1047001; /* content: Up there is where Charlga Razorflank resides. Blasted old crone. */
UPDATE script_texts SET content_loc2='Il y a des racines de feuillebleue dans cet endroit ! C\'est comme un filon d\'or attendant d\'être exploité !' WHERE entry=-1047002; /* content: There\'s blueleaf tuber in this trench! It\'s like gold waiting to be mined I tell you! */
UPDATE script_texts SET content_loc2='Il peut y avoir du danger à chaque coin ici.' WHERE entry=-1047003; /* content: There could be danger around every corner here. */
UPDATE script_texts SET content_loc2='Je ne comprends pas comment ces animaux immondes peuvent vivre dans cet endroit... Pouah, ça empeste !' WHERE entry=-1047004; /* content: I don\'t see how these foul animals live in this place... sheesh it smells! */
UPDATE script_texts SET content_loc2='Je crois que j\'ai trouvé un moyen de sortir de ce fichu roncier.' WHERE entry=-1047005; /* content: I think I see a way for us to get out of this big twisted mess of a bramble. */
UPDATE script_texts SET content_loc2='Bien content d\'être sorti de ce fossé nauséabond. Quoique ce ne soit pas vraiment mieux ici non plus !' WHERE entry=-1047006; /* content: Glad to be out of that wretched trench. Not much nicer up here though! */
UPDATE script_texts SET content_loc2='Enfin ! Je ne serai pas fâché de quitter cet endroit.' WHERE entry=-1047007; /* content: Finally! I\'ll be glad to get out of this place. */
UPDATE script_texts SET content_loc2='Je crois que je vais me reposer quelques instants pour reprendre mon souffle avant de rentrer à Cabestan. Merci de votre aide !' WHERE entry=-1047008; /* content: I think I\'ll rest a moment and catch my breath before heading back to Ratchet. Thanks for all the help! */
