UPDATE script_texts SET content_loc2='Serviteurs, valets et soldats des ténèbres glaciales, répondez à l\'appel de Kel\'Thuzad !' WHERE entry=-1533105; /* content: Minions, servants, soldiers of the cold dark, obey the call of Kel\'Thuzad! */
UPDATE script_texts SET content_loc2='Vos magies insignifiantes ne sauraient défier la puissance du Fléau !' WHERE entry=-1533106; /* content: Your petty magics are no challenge to the might of the Scourge! */
UPDATE script_texts SET content_loc2='Assez ! Je me lasse de ses passe-temps !' WHERE entry=-1533107; /* content: Enough! I grow tired of these distractions! */
UPDATE script_texts SET content_loc2='Imbéciles, vous avez éparpillés vos forces. Libérez-vous, mes serviteurs !' WHERE entry=-1533108; /* content: Fools, you have spread your powers too thin. Be free, my minions! */
UPDATE script_texts SET content_loc2='Vous êtes à moi maintenant !' WHERE entry=-1533109; /* content: You are mine now! */
UPDATE script_texts SET content_loc2='Je vous vois !' WHERE entry=-1533110; /* content: I see you! */
UPDATE script_texts SET content_loc2='Tu es... le suivant !' WHERE entry=-1533111; /* content: You...are next! */
UPDATE script_texts SET content_loc2='Ferme les yeux... dors !' WHERE entry=-1533112; /* content: Close your eyes... sleep! */
UPDATE script_texts SET content_loc2='Les peuples de ce monde vont disparaître. Ce n\'est qu\'une question de temps.' WHERE entry=-1533113; /* content: The races of the world will perish. It is only a matter of time. */
UPDATE script_texts SET content_loc2='Je vois la souffrance sans fin. Je vois les tourments. Je vois la rage. Je vois toutes choses.' WHERE entry=-1533114; /* content: I see endless suffering, I see torment, I see rage. I see... everything! */
UPDATE script_texts SET content_loc2='Bientôt... le monde tremblera !' WHERE entry=-1533115; /* content: Soon... the world will tremble! */
UPDATE script_texts SET content_loc2='Votre fin est venue.' WHERE entry=-1533116; /* content: The end is upon you. */
UPDATE script_texts SET content_loc2='Vos chairs pourrissantes feront le délice des vers affamés !' WHERE entry=-1533117; /* content: Hungry worms will feast on your rotten flesh! */
UPDATE script_texts SET content_loc2='Noo... on...' WHERE entry=-1533118; /* content: Noo... o... */
UPDATE script_texts SET content_loc2='%s place un Zombie à proximité pour dévorer !' WHERE entry=-1533119; /* content: %s spots a nearby Zombie to devour! */
UPDATE script_texts SET content_loc2='Hah hah, je suis juste entrain de m\'échauffer !' WHERE entry=-1533120; /* content: Hah hah, I\'m just getting warmed up! */
UPDATE script_texts SET content_loc2='Debout et bats-toi !' WHERE entry=-1533121; /* content: Stand and fight! */
UPDATE script_texts SET content_loc2='Montre-moi ce que tu as dans le ventre !' WHERE entry=-1533122; /* content: Show me what you\'ve got! */
