UPDATE db_script_string SET content_loc2='Venez faire un tour au Solitaire bleu. Retrouvez-y tous vos amis !' WHERE entry=2000005198; /* content: Head on over to the Blue Recluse. Where everybody knows your name! */
UPDATE db_script_string SET content_loc2='La meilleure adresse de Hurlevent !' WHERE entry=2000005199; /* content: Best drinks in Stormwind! */
UPDATE db_script_string SET content_loc2='Hé, vous aimez la sauce ? La sauce est bonne, non ?' WHERE entry=2000005214; /* content: Hey! Do you like the sauce? The sauce is good, no? */
UPDATE db_script_string SET content_loc2='Porte quoi ? Oh, Porte-CENDRES ? Jamais entendu parler... Quoi ? Arrêtez de m\'ennuyer !' WHERE entry=2000005215; /* content: The what-bringer? Oh, ASHbringer? Never heard of it... What? Stop bothering me! */
UPDATE db_script_string SET content_loc2='Hé, que quelqu\'un m\'aide à rattraper cette chose !' WHERE entry=2000005218; /* content: Hey, someone help me catch this thing! */
UPDATE db_script_string SET content_loc2='J\'aurais dû prendre la pilule translucide.' WHERE entry=2000005228; /* content: I should have taken the translucent pill. */
UPDATE db_script_string SET content_loc2='Je ne suis pas assez payé pour ça !' WHERE entry=2000005229; /* content: I don\'t get paid enough for this bull excrement! */
UPDATE db_script_string SET content_loc2='Je refuse ces impôts ! Allez vous faire voir !' WHERE entry=2000005234; /* content: I can\'t take these taxes! F.I.C.A. me? F.I.C.A. YOU! */
UPDATE db_script_string SET content_loc2='Soyez témoins de la puissance de votre chef de guerre ! Soyez transportés par le cri de ralliement de vos tueurs de dragons !' WHERE entry=2000005237; /* content: Bear witness to the undeniable power of your Warchief! Be lifted by the rallying cry of your dragon slayers! */
UPDATE db_script_string SET content_loc2='Citoyens et alliés de Hurlevent, ce jour est historique. $n a neutralisé celle qui avait tenté d\'usurper le pouvoir dans ce royaume. Rassemblez-vous et joignez-vous à moi pour honorer nos héros.' WHERE entry=2000005238; /* content: Citizens and allies of Stormwind, on this day, history has been made. $N has laid waste to that which had attempted to usurp the rule of the kingdom. Gather round and join me in honoring our heroes. */
UPDATE db_script_string SET content_loc2='Admirez la puissance de l\'Alliance ! La terrible Onyxia est accrochée à ces arches ! Que le cri de ralliement des tueurs de dragons vous donne du cœur et du courage !' WHERE entry=2000005239; /* content: Behold the might of the Alliance! The dread lady, Onyxia, hangs from the arches! Let the rallying cry of the dragon slayer lift your spirits! */
UPDATE db_script_string SET content_loc2='Le nombre de mes compagnons diminue, déesse, et mon propre pouvoir ne suffira bientôt plus à maîtriser les démons de Gangrebois.' WHERE entry=2000005240; /* content: The numbers of my companions dwindles, goddess, and my own power shall soon be insufficient to hold back the demons of Felwood. */
UPDATE db_script_string SET content_loc2='Déesse, donnez-moi le pouvoir de vaincre mes ennemis ! Écoutez-moi, je vous en prie, mon besoin est immense et ma cause est juste !' WHERE entry=2000005241; /* content: Goddess, grant me the power to overcome my enemies! Hear me, please, my need is desperate and my cause is just! */
UPDATE db_script_string SET content_loc2='J\'ai échoué... J\'ai manqué à mon devoir... déçu mon peuple… Les worgens sont déchaînés et la Faux de ma déesse est perdue.' WHERE entry=2000005243; /* content: I have failed... I have failed in my duty... failed my people... The worgen run rampant, and the Scythe of my goddess is lost. */
UPDATE db_script_string SET content_loc2='Ce mal que j\'ai libéré... Dans ses mains réside désormais le pouvoir que m\'avait concédé ma déesse… Par quelle main est-il dirigé ?' WHERE entry=2000005244; /* content: This evil I have unleashed... In whose hand does the power my goddess granted to me now lie... By whose hand is it directed? */
UPDATE db_script_string SET content_loc2='Maçons... euuhhh... Défias, soyez sur vos gardes : ce soir, l\'ancre rouillée coulera.' WHERE entry=2000005302; /* content: Stonemasons. . .errr. . Defias be warned:  The rusty anchor sinks tonight. */
UPDATE db_script_string SET content_loc2='Par les étoiles ! Un esprit a été invoqué !' WHERE entry=2000005304; /* content: By the stars! A spirit has been summoned! */
UPDATE db_script_string SET content_loc2='Qui a invoqué Aman ?' WHERE entry=2000005305; /* content: Who hath summoned forth Aman? */
UPDATE db_script_string SET content_loc2='Ah, je vois que vous avez du mal avec les reliques du passé.' WHERE entry=2000005306; /* content: Ah, I see you toil with relics of the past. */
UPDATE db_script_string SET content_loc2='Prenez garde, même vos créateurs sont faillibles.' WHERE entry=2000005307; /* content: Be warned that even your creators are fallible. */
UPDATE db_script_string SET content_loc2='À fouiller trop loin dans votre passé, vous risquez de voir votre avenir prendre fin d\'une manière abrupte.' WHERE entry=2000005308; /* content: Digging too deep into your past might bring an abrupt end to your future. */
UPDATE db_script_string SET content_loc2='C\'est un mystère du passé, pour sûr ! Mais aussi une clé pour notre avenir !' WHERE entry=2000005310; /* content: It\'s a mystery of the past indeed! But a key to our future! */
UPDATE db_script_string SET content_loc2='Sacrebleu, cette bière brune de la Griffe noire tape dur !' WHERE entry=2000005311; /* content: My, that Black Claw stout packs quite a punch! */
UPDATE db_script_string SET content_loc2='Allergique ? Ça veut dire quoi ? Et quel rapport avec mes minous ?' WHERE entry=2000005314; /* content: What does allergic mean anyway? And what does it have to do with either of my kitties? */
UPDATE db_script_string SET content_loc2='Je n\'arrive pas à croire que papa m\'interdise de garder ta sœur.' WHERE entry=2000005315; /* content: I can\'t believe dad won\'t let me keep your sister. */
UPDATE db_script_string SET content_loc2='Pas d\'inquiétude, nous allons te trouver un bon foyer.' WHERE entry=2000005316; /* content: Don\'t worry, I\'ll find a good home for ya. */
UPDATE db_script_string SET content_loc2='Il n\'y a personne pour offrir un foyer à mon adorable petit chaton ?' WHERE entry=2000005317; /* content: Can anyone give my adorable, extra little kitty a home? */
UPDATE db_script_string SET content_loc2='Est-ce que quelqu\'un pourrait donner un bon foyer à mon chaton ?' WHERE entry=2000005318; /* content: Will someone please give my little kitten a good home? */
UPDATE db_script_string SET content_loc2='Chaton à vendre, cherchant un bon foyer.' WHERE entry=2000005319; /* content: Kitten for sale, looking for a good home. */
UPDATE db_script_string SET content_loc2='Est-ce que je peux aller chercher plus de cailloux pour mon collier, Papa ?' WHERE entry=2000005320; /* content: Can I go collect more rocks for my necklace, Papa? */
UPDATE db_script_string SET content_loc2='Dabu ! Je ferai attention, papa !' WHERE entry=2000005322; /* content: Dabu! I will be careful Papa! */
UPDATE db_script_string SET content_loc2='Ooohh... Ce caillou est joli et tout coloré. Voyons si je peux en trouver d\'autres...' WHERE entry=2000005323; /* content: Ooohh... This rock is nice and colorful. Let\'s see if I can find more like it... */
UPDATE db_script_string SET content_loc2='Hmm... Ce caillou est joli, mais trop petit.' WHERE entry=2000005324; /* content: Hmm... That rock is nice, but too small. */
UPDATE db_script_string SET content_loc2='Je parie que mon ami Gruk voudrait ce caillou pour la lance qu\'il fabrique ! Il est très coupant... J\'espère qu\'il lui plaira.' WHERE entry=2000005326; /* content: I bet my friend Gruk would like this rock for the spear he is making! It\'s very sharp... I hope he likes it. */
UPDATE db_script_string SET content_loc2='Voilà... Ça devrait être assez pour faire un collier. C\'est sûrement l\'heure de rentrer à la maison, de toute façon.' WHERE entry=2000005327; /* content: There... That should be enough rocks to make a necklace. I should probably get home now anyway. */
UPDATE db_script_string SET content_loc2='Anaya...? Mes yeux me jouent-ils des tours ? Est-ce vraiment vous ?' WHERE entry=2000005336; /* content: Anaya...? Do my eyes deceive me? Is it really you? */
UPDATE db_script_string SET content_loc2='Les temps ont été cruels envers vous et moi, mon amour, mais je peux vous l\'assurer, et nous sommes enfin réunis.' WHERE entry=2000005337; /* content: The ages have been cruel to you and I, my love, but be assured, it is, and at long last we are reunited. */
UPDATE db_script_string SET content_loc2='Faut-il que le destin soit si cruel qu\'il ne nous permette que cela après une séparation de mille ans...' WHERE entry=2000005338; /* content: That the fates should be so cruel as to permit us only this after a thousand years apart... */
UPDATE db_script_string SET content_loc2='Me haïssez-vous, mon amour ? Car j\'ai été forcé de détruire votre enveloppe vivante afin que votre esprit soit libéré d\'une triste servitude.' WHERE entry=2000005339; /* content: Do you hate me, my love? That I was forced to destroy your living form, that your spirit be released from unhappy bondage. */
UPDATE db_script_string SET content_loc2='Ne laissez pas votre cœur se troubler, bien-aimé. Vous m\'avez libérée de l\'esclavage et pour cette raison, je vous aime d\'autant plus.' WHERE entry=2000005340; /* content: Let it not trouble your heart, beloved. You have freed me from slavery, and for that I love you all the more. */
UPDATE db_script_string SET content_loc2='Malheureusement, même cela doit être abrégé... Les liens qui me rattachent à ce monde faiblissent et m\'éloignent...' WHERE entry=2000005341; /* content: Sadly, even this must be cut short... The ties that bind me to this world weaken, and pull me away... */
UPDATE db_script_string SET content_loc2='Non ! Anaya... Anaya ! Ne me quittez pas ! Je vous en prie...' WHERE entry=2000005342; /* content: No! Anaya... Anaya! Don\'t leave me! Please... */
UPDATE db_script_string SET content_loc2='Adieu, Cerellean, jusqu\'à ce que nous soyons à nouveau réunis...' WHERE entry=2000005343; /* content: Farewell, Cerellean, until we are joined once again... */
UPDATE db_script_string SET content_loc2='Comment, mon amour ? Comment trouverai-je la force de faire face à ce monde vieillissant sans vous avoir à mes côtés...' WHERE entry=2000005345; /* content: How, my love? How will I find the strength to face the ages of the world without you by my side... */
UPDATE db_script_string SET content_loc2='Les chutes de la Bondissante longent les montagnes à l\'est et au nord.' WHERE entry=2000005347; /* content: Cliffspring Falls lies along the mountains to the east and north. */
UPDATE db_script_string SET content_loc2='%s époussète la base de l\'artéfact.' WHERE entry=2000005348; /* content: %s dusts the base of the artifact. */
UPDATE db_script_string SET content_loc2='%s examine attentivement le grand vase.' WHERE entry=2000005349; /* content: %s meticulously examines the large vase. */
UPDATE db_script_string SET content_loc2='Pauvre Dorius. Si jamais je mets la main sur ces Sombrefer, je vous jure que...' WHERE entry=2000005350; /* content: Poor Dorius. If I ever get my hands on those Dark Irons, so help me... */
UPDATE db_script_string SET content_loc2='Quel est ce bruit ? On aurait dit un mugissement.' WHERE entry=2000005384; /* content: What was that noise? It sounded like a roar. */
UPDATE db_script_string SET content_loc2='Griffe-sinistre ?! Doucement mon ami... Où est ton maître Volcor ?' WHERE entry=2000005386; /* content: Grimclaw?! Easy there, my friend... where is your master Volcor? */
UPDATE db_script_string SET content_loc2='Doucement, doucement mon ami. Un moment...' WHERE entry=2000005388; /* content: Whoa, whoa there, my friend. One moment... */
UPDATE db_script_string SET content_loc2='Là... Ça devrait vous faire du bien. Maintenant, racontez-moi ce qui s\'est passé, Griffe-sinistre.' WHERE entry=2000005390; /* content: There... that should help. Now, tell me what\'s happened, Grimclaw. */
UPDATE db_script_string SET content_loc2='Je comprends, mon ami. Je trouverai quelqu\'un pour venir en aide à votre maître. Retournez auprès de lui maintenant, ou tout du moins, restez à proximité.' WHERE entry=2000005392; /* content: I understand, my friend. I shall find someone to help your master. Go back to him now, or at least stay close. */
UPDATE db_script_string SET content_loc2='Si vous avez le temps, vous pourriez donner un coup de main à Griffe-sinistre et son maître Volcor. Si cela vous intéresse, faites-le-moi savoir...' WHERE entry=2000005394; /* content: If you have the time, Grimclaw and his master Volcor could use your help. If you\'re interested, speak with me further... */
UPDATE db_script_string SET content_loc2='Si vous voulez la clé, il va falloir m\'attraper !' WHERE entry=2000005460; /* content: If you want the key, you\'ll have to catch me! */
UPDATE db_script_string SET content_loc2='Poursuivez-moi si vous l\'osez ! Je m\'enfuis sans hésiter !' WHERE entry=2000005461; /* content: Chase me if you dare! I run without a care! */
UPDATE db_script_string SET content_loc2='Pourquoi voudriez-vous me faire souffrir ? Venez. L\'amitié peut nous réunir !' WHERE entry=2000005462; /* content: Why would you ever want to harm me!? Come. Friends we can be! */
UPDATE db_script_string SET content_loc2='MEURS ? Vous faites pleurer Pusillin !' WHERE entry=2000005463; /* content: DIE?! You make Pusillin cry! */
UPDATE db_script_string SET content_loc2='Dites bonjour à mes petits amis !' WHERE entry=2000005464; /* content: Say hello to my little friends! */
UPDATE db_script_string SET content_loc2='$n, jetez un coup d\'œil à cet échantillon de vigne de Vylevrille que j\'ai pris dans les cavernes. Comme ça, vous saurez quoi chercher...' WHERE entry=2000005465; /* content: $N, please take a look at the sample of the Vylestem vine that I took from the caverns, so that you know what to look for... */
UPDATE db_script_string SET content_loc2='Voilà !' WHERE entry=2000005548; /* content: Ok, here I go! */
UPDATE db_script_string SET content_loc2='Allons-y !' WHERE entry=2000005553; /* content: Let\'s move forward! */
UPDATE db_script_string SET content_loc2='Personne n\'a le droit de voler le secret des Faiseurs !' WHERE entry=2000005555; /* content: None may steal the secrets of the Makers! */
UPDATE db_script_string SET content_loc2='Fouillez ces gravats.' WHERE entry=2000005556; /* content: Search this rubble. */
UPDATE db_script_string SET content_loc2='Trouvez-moi quelque chose d\'intéressant, chiens !' WHERE entry=2000005557; /* content: Find me something good, you dogs! */
UPDATE db_script_string SET content_loc2='Attendez... avez-vous entendu ça ? Quelque chose vient de l\'ouest !' WHERE entry=2000005627; /* content: Wait... did you hear that? Something approaches from the west! */
UPDATE db_script_string SET content_loc2='Je n\'ai plus de flèches ! Je crains que si d\'autres surgissent, vous devrez vous en occuper vous-même, camarade.' WHERE entry=2000005628; /* content: I\'ve run out of arrows! I\'m afraid if any more come you will need to take them on by yourself my friend. */
UPDATE db_script_string SET content_loc2='Louée soit Élune ! Je ne sais pas si j\'aurais pu survivre sans votre aide.' WHERE entry=2000005629; /* content: Praise Elune! I don\'t know if I could have survived the day without you, friend. */
UPDATE db_script_string SET content_loc2='Veuillez parler à la Sentinelle Onaeya au Poste de Maestra, dans Orneval, elle veillera à ce que vous receviez une récompense à la hauteur de la bravoure dont vous avez fait preuve.' WHERE entry=2000005631; /* content: Please speak with Sentinel Onaeya at Maestra\'s Post in Ashenvale, she will see to it that you are properly rewarded for your bravery this day. */
UPDATE db_script_string SET content_loc2='Tout le monde aux postes de combat ! Les nagas arrivent !' WHERE entry=2000005632; /* content: All hands to battle stations! Naga incoming! */
UPDATE db_script_string SET content_loc2='Si nous parvenons à les retenir maintenant, je suis sûr que nous serons hors de danger.' WHERE entry=2000005633; /* content: If we can just hold them now, I am sure we will be in the clear. */
UPDATE db_script_string SET content_loc2='Vous voulez en apprendre plus sur la pierre ? Suivez-moi.' WHERE entry=2000005646; /* content: You wish to learn of the stone? Follow me. */
UPDATE db_script_string SET content_loc2='J\'ai erré si longtemps sous ma forme maudite… Vous m\'avez libéré… Votre labeur sera récompensé.' WHERE entry=2000005647; /* content: For so long I have drifted in my cursed form. You have freed me... Your hard work shall be repaid. */
UPDATE db_script_string SET content_loc2='Suivez mes instructions, $n.' WHERE entry=2000005648; /* content: Please do as I instruct you, $N. */
UPDATE db_script_string SET content_loc2='Lisez ce tome que j\'ai placé devant vous et dites les mots à voix haute.' WHERE entry=2000005649; /* content: Read this tome I have placed before you, and speak the words aloud. */
UPDATE db_script_string SET content_loc2='%s commence à canaliser son énergie, en se concentrant sur la pierre.' WHERE entry=2000005650; /* content: %s begins to channel his energy, focusing on the stone. */
UPDATE db_script_string SET content_loc2='Ensemble, les deux morceaux ne feront qu\'un à nouveau.' WHERE entry=2000005651; /* content: Together, the two parts shall become one, once again. */
UPDATE db_script_string SET content_loc2='Shal myrinan ishnu daldorah...' WHERE entry=2000005652; /* content: Shal myrinan ishnu daldorah... */
UPDATE db_script_string SET content_loc2='Mon sceptre sera de nouveau unifié !' WHERE entry=2000005653; /* content: My scepter will once again become whole! */
UPDATE db_script_string SET content_loc2='%s choisit la cible orc...' WHERE entry=2000005730; /* content: %s picks the orc target... */
UPDATE db_script_string SET content_loc2='%s rate...' WHERE entry=2000005731; /* content: %s misses... */
UPDATE db_script_string SET content_loc2='%s vise plus précisément...' WHERE entry=2000005732; /* content: %s takes a more careful aim... */
UPDATE db_script_string SET content_loc2='%s rate encore…' WHERE entry=2000005733; /* content: %s misses again... */
UPDATE db_script_string SET content_loc2='%s vise tranquillement la cible orc...' WHERE entry=2000005734; /* content: %s casually points at the orc target... */
UPDATE db_script_string SET content_loc2='%s touche !' WHERE entry=2000005735; /* content: %s hits! */
UPDATE db_script_string SET content_loc2='%s vise les bouteilles...' WHERE entry=2000005736; /* content: %s takes aim at the bottles... */
UPDATE db_script_string SET content_loc2='%s rate...' WHERE entry=2000005737; /* content: %s misses... */
UPDATE db_script_string SET content_loc2='%s vise de nouveau les bouteilles...' WHERE entry=2000005738; /* content: %s takes aim at the bottles again... */
UPDATE db_script_string SET content_loc2='%s rate encore…' WHERE entry=2000005739; /* content: %s misses again... */
UPDATE db_script_string SET content_loc2='%s tire rapidement en plein centre de la cible du milieu...' WHERE entry=2000005740; /* content: %s quickly shoots the middle target dead center... */
UPDATE db_script_string SET content_loc2='%s vise ensuite les bouteilles...' WHERE entry=2000005741; /* content: %s then takes aim at the bottles... */
UPDATE db_script_string SET content_loc2='%s ne touche que le côté de la boîte !' WHERE entry=2000005742; /* content: %s only hits the side of the box! */
UPDATE db_script_string SET content_loc2='%s tire rapidement sur le mannequin orc...' WHERE entry=2000005743; /* content: %s quickly shoots the orc dummy... */
UPDATE db_script_string SET content_loc2='%s se tourne vers la cible de papier...' WHERE entry=2000005744; /* content: %s turns to the paper target... */
UPDATE db_script_string SET content_loc2='%s tire à hauteur de la hanche sur les bouteilles...' WHERE entry=2000005745; /* content: %s shoots from the hip at the bottles... */
UPDATE db_script_string SET content_loc2='Essayons...' WHERE entry=2000005752; /* content: Now let\'s try it... */
UPDATE db_script_string SET content_loc2='Maintenant, il faut trouver une harpie qui ne se doute de rien.' WHERE entry=2000005768; /* content: Now to find an unsuspecting Harpy! */
UPDATE db_script_string SET content_loc2='Ici ! Pour la Horde !' WHERE entry=2000005769; /* content: There\'s one! For the Horde! */
UPDATE db_script_string SET content_loc2='Prend ça, espèce de harpie pelée ! Hyaa !' WHERE entry=2000005770; /* content: Take that you mangy Harpy! Hyaa! */
UPDATE db_script_string SET content_loc2='Vous ne pouvez pas échapper à un guerrier de la Horde ! Prenez ça ! Et ça !' WHERE entry=2000005771; /* content: You can\'t get away from a warrior of the Horde! Take that! And that! */
UPDATE db_script_string SET content_loc2='Mogg, il faut rentrer. Tant que ton père n\'est pas là, c\'est dangereux, dehors.' WHERE entry=2000005774; /* content: Mogg, get inside. It is not safe out there while your father is gone. */
UPDATE db_script_string SET content_loc2='Nous devons nous hâter !' WHERE entry=2000005780; /* content: We must hurry. */
UPDATE db_script_string SET content_loc2='Esprits de la Forêt, montrez-vous et obéissez à l\'appel de la Nature !' WHERE entry=2000005781; /* content: Spirits of the forest come forth and heed nature\'s call! */
UPDATE db_script_string SET content_loc2='Esprits de la forêt, nous avons besoin de vous ! Venez vite au Val calciné !' WHERE entry=2000005782; /* content: Spirits of the forest, you are needed! Make haste to the Charred Vale! */
UPDATE db_script_string SET content_loc2='%s entre en transe...' WHERE entry=2000005800; /* content: %s enters a trance... */
UPDATE db_script_string SET content_loc2='L\'Eau des Prophètes est prête à être bue, $n.' WHERE entry=2000005803; /* content: The Water of the Seers is ready for your consumption, $N. */

SET NAMES 'latin1';
