UPDATE locales_npc_text SET Text0_0_loc8='Руины Зул\'Фаррака находятся в северо-западной части пустыни Танарис.',Text0_1_loc8='Руины Зул\'Фаррака находятся в северо-западной части пустыни Танарис.' WHERE entry=7213; /* Text0_0: The ruins of Zul\'Farrak are located in the northwestern corner of the Tanaris Desert. */ /* Text0_1: The ruins of Zul\'Farrak are located in the northwestern corner of the Tanaris Desert. */
UPDATE locales_npc_text SET Text0_0_loc8='Мародон находится посреди западного берега Пустошей, в долине Копий.',Text0_1_loc8='Мародон находится посреди западного берега Пустошей, в долине Копий.' WHERE entry=7215; /* Text0_0: Maraudon is located in the middle of the western coast of Desolace, in the Valley of Spears. */ /* Text0_1: Maraudon is located in the middle of the western coast of Desolace, in the Valley of Spears. */
UPDATE locales_npc_text SET Text0_0_loc8='Затонувший храм находится посреди большого озера на востоке Болота Печали.',Text0_1_loc8='Затонувший храм находится посреди большого озера на востоке Болота Печали.' WHERE entry=7217; /* Text0_0: The Sunken Temple can be found in the center of a great lake in eastern Swamp of Sorrows. */ /* Text0_1: The Sunken Temple can be found in the center of a great lake in eastern Swamp of Sorrows. */
UPDATE locales_npc_text SET Text0_0_loc8='Черная гора находится к югу от Тлеющего Ущелья и к северу от Пылающих степей.',Text0_1_loc8='Черная гора находится к югу от Тлеющего Ущелья и к северу от Пылающих степей.' WHERE entry=7218; /* Text0_0: Blackrock Mountain lies between Searing Gorge in the north and Burning Steppes in the south. */ /* Text0_1: Blackrock Mountain lies between Searing Gorge in the north and Burning Steppes in the south. */
