UPDATE locales_npc_text SET Text0_0_loc8='Здравствуй, |3-6($c)! Я могу предоставить стойло для твоих питомцев и помочь в розыске пропавших любимцев.',Text0_1_loc8='Здравствуй, |3-6($c)! Я могу предоставить стойло для твоих питомцев и помочь в розыске пропавших любимцев.' WHERE entry=13557; /* Text0_0: Greetings, $c. I can help stable your pets or assist you in recovering lost companions. */ /* Text0_1: Greetings, $c. I can help stable your pets or assist you in recovering lost companions. */
UPDATE locales_npc_text SET Text0_0_loc8='Жизнь моя и душа переполнены воспоминаниями о мерзостной Орде. Времена печали, отчаяния и жертв прошли. Сердце мое никогда не покинет ярость битвы!',Text0_1_loc8='Жизнь моя и душа переполнены воспоминаниями о мерзостной Орде. Времена печали, отчаяния и жертв прошли. Сердце мое никогда не покинет ярость битвы!' WHERE entry=13574; /* Text0_0: My life and spirit has been filled with memories of the insufferable Horde. Times of sorrow, desperation, and sacrifice have come to pass. The rage of battle could never leave my heart! */ /* Text0_1: My life and spirit has been filled with memories of the insufferable Horde. Times of sorrow, desperation, and sacrifice have come to pass. The rage of battle could never leave my heart! */
