UPDATE script_texts SET content_loc8='%s видит чудовищный кошмар. Что-то шевелится в темной толще вод.' WHERE entry=-1043011; /* content: %s dreams up a horrendous vision. Something stirs beneath the murky waters. */
UPDATE script_texts SET content_loc8='НАКОНЕЦ-ТО Я ПРОСНУЛСЯ.' WHERE entry=-1043013; /* content: I AM AWAKE, AT LAST! */
