UPDATE script_texts SET content_loc8='Заряды заложены. Отойдите – сейчас будет взрыв!' WHERE entry=-1090009; /* content: The charges are set. Get back before they blow! */
UPDATE script_texts SET content_loc8='Взрыв через 10 секунд!' WHERE entry=-1090010; /* content: Incoming blast in 10 seconds! */
