UPDATE script_texts SET content_loc8='Как вы смеете входить в мой храм!' WHERE entry=-1209000; /* content: How dare you enter my sanctum! */
UPDATE script_texts SET content_loc8='Чтоб тебя пески пожрали!' WHERE entry=-1209001; /* content: Sands consume you! */
UPDATE script_texts SET content_loc8='Умри!' WHERE entry=-1209002; /* content: Fall! */
UPDATE script_texts SET content_loc8='Придите ко мне, дети мои!' WHERE entry=-1209003; /* content: Come to me, my children! */
UPDATE script_texts SET content_loc8='%s начинает восстанавливать прежнюю силу!' WHERE entry=-1229000; /* content: %s begins to regain its strength! */
UPDATE script_texts SET content_loc8='Еще немного, и %s наберет силу!' WHERE entry=-1229001; /* content: %s is nearly at full strength! */
UPDATE script_texts SET content_loc8='%s восстанавливает силы и рвет путы!' WHERE entry=-1229002; /* content: %s regains its power and breaks free of its bonds! */
UPDATE script_texts SET content_loc8='Ха! Ха! Ха! Спасибо, что освободили меня, глупцы. А теперь я отблагодарю вас, содрав мясо с ваших костей.' WHERE entry=-1229003; /* content: Ha! Ha! Ha! Thank you for freeing me, fools. Now let me repay you by charring the flesh from your bones. */
UPDATE script_texts SET content_loc8='Превосходно... эти ничтожества пришли как раз вовремя – мой легион пора кормить. Приветствую, смертные!' WHERE entry=-1229004; /* content: Excellent... it would appear as if the meddlesome insects have arrived just in time to feed my legion. Welcome, mortals! */
UPDATE script_texts SET content_loc8='Не пролейте ни капли их крови на ринг арены, дети мои. Полакомьтесь их душами!' WHERE entry=-1229005; /* content: Let not even a drop of their blood remain upon the arena floor, my children. Feast on their souls! */
UPDATE script_texts SET content_loc8='Сэр, пустите меня в бой! Я сверну им шеи голыми руками!' WHERE entry=-1229007; /* content: Sire, let me join the fray! I shall tear their spines out with my bare hands! */
UPDATE script_texts SET content_loc8='Главное – убить целителя!' WHERE entry=-1229008; /* content: Concentrate your attacks upon the healer! */
UPDATE script_texts SET content_loc8='Невероятно!' WHERE entry=-1229009; /* content: Inconceivable! */
UPDATE script_texts SET content_loc8='Не искушайте меня, детки! Я пущу ваши шкуры на подкладку для своих сапог!' WHERE entry=-1229010; /* content: Do not force my hand, children! I shall use your hides to line my boots. */
UPDATE script_texts SET content_loc8='Осквернители!' WHERE entry=-1229011; /* content: Defilers! */
UPDATE script_texts SET content_loc8='Невозможный!' WHERE entry=-1229012; /* content: Impossible! */
UPDATE script_texts SET content_loc8='Ваши усилия бесплодны! Никто не встанет на нашем пути!' WHERE entry=-1229013; /* content: Your efforts will prove fruitless. None shall stand in our way! */
UPDATE script_texts SET content_loc8='ЭТОГО НЕ МОЖЕТ БЫТЬ! Ренд, расправься с этими ничтожествами!' WHERE entry=-1229014; /* content: THIS CANNOT BE!!! Rend, deal with these insects. */
UPDATE script_texts SET content_loc8='С удовольствием...' WHERE entry=-1229015; /* content: With pleasure... */
UPDATE script_texts SET content_loc8='Вождь не будет с вами церемониться, смертные. Готовьтесь!' WHERE entry=-1229016; /* content: The Warchief shall make quick work of you, mortals. Prepare yourselves! */
UPDATE script_texts SET content_loc8='Отведай моего могущества!' WHERE entry=-1229017; /* content: Taste in my power! */
UPDATE script_texts SET content_loc8='%s сброшен со своего дракона!!!' WHERE entry=-1229019; /* content: %s is knocked off his drake! */
UPDATE script_texts SET content_loc8='Ох, хорошо пошло!' WHERE entry=-1230000; /* content: Ah, hits the spot! */
UPDATE script_texts SET content_loc8='Слава королю, крошка!' WHERE entry=-1230002; /* content: Hail to the king, baby! */
UPDATE script_texts SET content_loc8='Ты бросил вызов Семерым – и потому умрешь!' WHERE entry=-1230003; /* content: You have challenged the Seven, and now you will die! */
UPDATE script_texts SET content_loc8='Сыны Тауриссана увидят, как ты сгинешь в Зале Правосудия!' WHERE entry=-1230004; /* content: The Sons of Thaurissan shall watch you perish in the Ring of the Law! */
UPDATE script_texts SET content_loc8='Вы приговорены к смертной казни за преступления против клана Черного Железа!' WHERE entry=-1230005; /* content: You have been sentenced to death for crimes against the Dark Iron Nation! */
UPDATE script_texts SET content_loc8='Дайте волю своей ярости! Закончим дело!' WHERE entry=-1230006; /* content: Unleash the fury and let it be done! */
UPDATE script_texts SET content_loc8='Но настоящее наказание еще впереди.' WHERE entry=-1230007; /* content: But your real punishment lies ahead. */
UPDATE script_texts SET content_loc8='Ха-ха! А вы уж было решили, что все, да?' WHERE entry=-1230008; /* content: Haha! I bet you thought you were done! */
UPDATE script_texts SET content_loc8='Скатертью дорога!' WHERE entry=-1230009; /* content: Good Riddance! */
UPDATE script_texts SET content_loc8='Спасибо, $n! Я свободен!!!' WHERE entry=-1230010; /* content: Thank you, $N! I\'m free!!! */
UPDATE script_texts SET content_loc8='Не того маршала ты $gзапер:заперла;, $n! Готовься к смерти!' WHERE entry=-1230011; /* content: You locked up the wrong Marshal, $N. Prepare to be destroyed! */
UPDATE script_texts SET content_loc8='Не подпускайте меня к |3-2($n), а то $gон:она; узнает на своей шкуре, что такое тюремные порядки.' WHERE entry=-1230013; /* content: You better hold me back or $N is going to feel some prison house beatings. */
UPDATE script_texts SET content_loc8='Пойдем-ка. Мое снаряжение лежит где-то тут на складе...' WHERE entry=-1230014; /* content: Let\'s get a move on. My gear should be in the storage area up this way... */
UPDATE script_texts SET content_loc8='Проверю ту камеру, $n. Если там есть живые, надо вытащить их оттуда.' WHERE entry=-1230015; /* content: Check that cell, $N. If someone is alive in there, we need to get them out. */
UPDATE script_texts SET content_loc8='Ну вот, $n. Мои вещи должны быть в той комнате. Прикрой меня, я иду!' WHERE entry=-1230017; /* content: This is it, $N. My stuff should be in that room. Cover me, I\'m going in! */
UPDATE script_texts SET content_loc8='А, вот оно где!' WHERE entry=-1230018; /* content: Ah, there it is! */
UPDATE script_texts SET content_loc8='Чувствуешь эту силу, $n??? Время ВЕСЕЛИТЬСЯ!' WHERE entry=-1230019; /* content: Can you feel the power, $N??? It\'s time to ROCK! */
UPDATE script_texts SET content_loc8='Осталось освободить Тобиаса – и мы можем уходить. За мной!' WHERE entry=-1230020; /* content: Now we just have to free Tobias and we can get out of here. This way! */
UPDATE script_texts SET content_loc8='Открой.' WHERE entry=-1230021; /* content: Open it. */
UPDATE script_texts SET content_loc8='Эти двое мне никогда не нравились. Идем дальше.' WHERE entry=-1230022; /* content: I never did like those two. Let\'s get moving. */
UPDATE script_texts SET content_loc8='Открывай. На этот раз будь осторожнее!' WHERE entry=-1230023; /* content: Open it and be careful this time! */
UPDATE script_texts SET content_loc8='Этот проклятый бурдюк с грязью наконец-то получил по заслугам. Счастливое избавление!' WHERE entry=-1230024; /* content: That intolerant dirtbag finally got what was coming to him. Good riddance! */
UPDATE script_texts SET content_loc8='Ладно, идем.' WHERE entry=-1230025; /* content: Alright, let\'s go. */
UPDATE script_texts SET content_loc8='Открывай. Нам надо поторопиться. Я чую Черное Железо за версту, и сейчас могу сказать наверняка: они ПРИБЛИЖАЮТСЯ!' WHERE entry=-1230026; /* content: Open it. We need to hurry up. I can smell those Dark Irons coming a mile away and I can tell you one thing, they\'re COMING! */
UPDATE script_texts SET content_loc8='Он должен быть в последней камере. Если только... они не убили его.' WHERE entry=-1230027; /* content: He has to be in the last cell. Unless... they killed him. */
UPDATE script_texts SET content_loc8='Вытащите его оттуда!' WHERE entry=-1230028; /* content: Get him out of there! */
UPDATE script_texts SET content_loc8='Превосходно, $n. Теперь нужно найти выход. Кажется, я знаю дорогу. За мной!' WHERE entry=-1230029; /* content: Excellent work, $N. Let\'s find the exit. I think I know the way. Follow me! */
UPDATE script_texts SET content_loc8='Мы это сделали!' WHERE entry=-1230030; /* content: We made it! */
UPDATE script_texts SET content_loc8='Встретимся у стоянки Максвелла. Пройдемся по следующим пунктам плана и найдем способ расшифровать мои таблички без шифровального кольца.' WHERE entry=-1230031; /* content: Meet me at Maxwell\'s encampment. We\'ll go over the next stages of the plan there and figure out a way to decode my tablets without the decryption ring. */
UPDATE script_texts SET content_loc8='Наконец-то я уйду отсюда!!!' WHERE entry=-1230033; /* content: Finally!! I can leave this dump. */
UPDATE script_texts SET content_loc8='Вы за это заплатите!' WHERE entry=-1230037; /* content: You\'ll pay for that! */
UPDATE script_texts SET content_loc8='Нет!' WHERE entry=-1230038; /* content: No! */
UPDATE script_texts SET content_loc8='А ну прочь от бочек!' WHERE entry=-1230041; /* content: Get away from those kegs! */
UPDATE script_texts SET content_loc8='Вы за это заплатите!' WHERE entry=-1230042; /* content: You\'ll pay for that! */
UPDATE script_texts SET content_loc8='Тут пусто!' WHERE entry=-1230044; /* content: That one\'s empty!! */
UPDATE script_texts SET content_loc8='Ах, опять пусто!' WHERE entry=-1230045; /* content: Ah, empty again! */
UPDATE script_texts SET content_loc8='ЭЛЯ!' WHERE entry=-1230046; /* content: ALE! */
UPDATE script_texts SET content_loc8='Мы... проводим исследования...' WHERE entry=-1230049; /* content: We...are...investigating. */
UPDATE script_texts SET content_loc8='Что вы там делаете?' WHERE entry=-1230054; /* content: What are you doing over there? */
UPDATE script_texts SET content_loc8='Эй! Вещи не трогать!' WHERE entry=-1230056; /* content: No stealing the goods! */
UPDATE script_texts SET content_loc8='Эй, меня обокрали!' WHERE entry=-1230059; /* content: Hey, my pockets were picked! */
UPDATE script_texts SET content_loc8='Ха! Вы даже не способны осознать всю тщетность своих усилий.' WHERE entry=-1230060; /* content: Ha! You can\'t even begin to imagine the futility of your efforts. */
UPDATE script_texts SET content_loc8='В любом случае, они только мешались.' WHERE entry=-1230062; /* content: They were just getting in the way anyways. */
UPDATE script_texts SET content_loc8='Я раздроблю вас на кусочки!' WHERE entry=-1230064; /* content: I will crush you into little tiny pieces! */
UPDATE script_texts SET content_loc8='Эй, Камнеузл!' WHERE entry=-1230066; /* content: Hey, Rocknot! */
UPDATE script_texts SET content_loc8='Пойдем, милый.' WHERE entry=-1230067; /* content: Let\'s go, honey. */
UPDATE script_texts SET content_loc8='%s целует своего любимого.' WHERE entry=-1230068; /* content: %s kisses her lover. */
UPDATE script_texts SET content_loc8='%s целует госпожу Нагмару.' WHERE entry=-1230069; /* content: %s kisses Mistress Nagmara */
UPDATE script_texts SET content_loc8='Эта бессмысленная возня вгоняет меня в тоску. Я сожгу вас всех!' WHERE entry=-1249002; /* content: This meaningless exertion bores me. I\'ll incinerate you all from above! */
UPDATE script_texts SET content_loc8='Похоже, вам требуется преподать еще один урок, смертные!' WHERE entry=-1249003; /* content: It seems you\'ll need another lesson, mortals! */
UPDATE script_texts SET content_loc8='%s делает глубокий вдох...' WHERE entry=-1249004; /* content: %s takes in a deep breath... */
UPDATE script_texts SET content_loc8='Начнем урок!' WHERE entry=-1289000; /* content: School is in session! */
UPDATE script_texts SET content_loc8='Да развернутся кольца ненависти!' WHERE entry=-1309000; /* content: Let the coils of hate unfurl! */
UPDATE script_texts SET content_loc8='Повелеваю вам обрушить на врагов огненный дождь!' WHERE entry=-1309003; /* content: I command you to rain fire down upon these invaders! */
UPDATE script_texts SET content_loc8='Шадра, сделай меня своей аватарой!' WHERE entry=-1309006; /* content: Shadra, make of me your avatar! */
UPDATE script_texts SET content_loc8='На помощь, родичи!' WHERE entry=-1309007; /* content: Aid me my brood! */
UPDATE script_texts SET content_loc8='Ширвалла, наполни меня своим ГНЕВОМ!' WHERE entry=-1309009; /* content: Shirvallah, fill me with your RAGE! */
UPDATE script_texts SET content_loc8='Бетекк, твоя жрица просит твоей силы!' WHERE entry=-1309011; /* content: Bethekk, your priestess calls upon your might! */
UPDATE script_texts SET content_loc8='$n вам понравится, красавчики мои!' WHERE entry=-1309012; /* content: Feast on $n, my pretties! */
UPDATE script_texts SET content_loc8='Наконец-то я свободна от Свежевателя Душ!' WHERE entry=-1309013; /* content: At last, I am free of the Soulflayer! */
UPDATE script_texts SET content_loc8='Опа!' WHERE entry=-1309016; /* content: DING! */
UPDATE script_texts SET content_loc8='$n! Я за тобой слежу!' WHERE entry=-1309018; /* content: $N! I\'m watching you! */
UPDATE script_texts SET content_loc8='Бегство не спасет вас, смертные!' WHERE entry=-1309021; /* content: Fleeing will do you no good, mortals! */
UPDATE script_texts SET content_loc8='Слуги Хаккара, внемлите своему богу. Храм под угрозой. Неверные вторглись в священные земли. Защищайте Алтарь Крови! Убейте их всех!' WHERE entry=-1309023; /* content: Minions of Hakkar, hear your God. The sanctity of this temple has been compromised. Invaders encroach upon holy ground! The Altar of Blood must be protected. Kill them all! */
UPDATE script_texts SET content_loc8='%s впадает в ярость, увидев, как ее ящер погиб!' WHERE entry=-1309024; /* content: %s goes into a rage after seeing his raptor fall in battle! */
UPDATE script_texts SET content_loc8='Мое племя победит!' WHERE entry=-1309025; /* content: The brood shall not fall! */
UPDATE script_texts SET content_loc8='%s издает оглушающий вопль!' WHERE entry=-1309026; /* content: %s emits a deafening shriek! */
UPDATE script_texts SET content_loc8='%s начинает читать заклинание "Большее исцеление"!' WHERE entry=-1309027; /* content: %s begins to cast a Great Heal! */
UPDATE script_texts SET content_loc8='Кристалл Аш\'ари пал! Храните верность Королю-личу, братья мои! Попытайтесь вызвать этот кристалл вновь!' WHERE entry=-1329004; /* content: An Ash\'ari Crystal has fallen! Stay true to the Lich King, my brethren, and attempt to resummon it. */
UPDATE script_texts SET content_loc8='Кристаллы Аш\'ари разрушены! Бойня в опасности!' WHERE entry=-1329007; /* content: The Ash\'ari Crystals have been destroyed! The Slaughterhouse is vulnerable! */
UPDATE script_texts SET content_loc8='Враг у боковых врат! Нужно предупредить Ривендера!' WHERE entry=-1329008; /* content: Intruders at the Service Gate! Lord Rivendare must be warned! */
UPDATE script_texts SET content_loc8='Так ты считаешь возможным играться с созданиями Короля-лича? Рамштайн, обязательно встреть чужаков как следует.' WHERE entry=-1329013; /* content: So you see fit to toy with the Lich King\'s creations? Ramstein, be sure to give the intruders a proper greeting. */
UPDATE script_texts SET content_loc8='Пора взять дело в свои руки. Идем. Вступи в мои владения и брось вызов могуществу Плети!' WHERE entry=-1329014; /* content: Time to take matters into my own hands. Come. Enter my domain and challenge the might of the Scourge! */
UPDATE script_texts SET content_loc8='Сегодня вы загубили то, на что я потратил многие годы! За это я собственными руками убью каждого из вас!' WHERE entry=-1329016; /* content: Today you have unmade what took me years to create! For this you shall all die by my hand! */
UPDATE script_texts SET content_loc8='Глупцы, вы думаете, что сможете победить меня так легко? Узрите же истинную мощь натрезимов!' WHERE entry=-1329017; /* content: You fools think you can defeat me so easily? Face the true might of the Nathrezim! */
UPDATE script_texts SET content_loc8='Моя смерть ничего не значит... свет... победит!' WHERE entry=-1329020; /* content: My death means nothing.... light... will... prevail! */
UPDATE script_texts SET content_loc8='Используй меня повторно' WHERE entry=-1409001; /* content: REUSE ME */
UPDATE script_texts SET content_loc8='Прах к праху...' WHERE entry=-1409005; /* content: Ashes to Ashes! */
UPDATE script_texts SET content_loc8='Придите, мои слуги! Защитите своего хозяина!' WHERE entry=-1409013; /* content: COME FORTH, MY SERVANTS! DEFEND YOUR MASTER! */
UPDATE script_texts SET content_loc8='Огонь очистит все!' WHERE entry=-1409015; /* content: BY FIRE BE PURGED! */
UPDATE script_texts SET content_loc8='Отведайте пламя Сульфурона!' WHERE entry=-1409016; /* content: TASTE THE FLAMES OF SULFURON! */
UPDATE script_texts SET content_loc8='Думаете, что уже победили? Похоже, придется преподать вам еще один урок!' WHERE entry=-1409019; /* content: You think you\'ve won already? Perhaps you\'ll need another lesson in pain! */
UPDATE script_texts SET content_loc8='Очень хорошо, $n.' WHERE entry=-1409023; /* content: Very well, $N. */
UPDATE script_texts SET content_loc8='Кто посмел разрушить неприкосновенность Eldre\'Thalas?. Покажись, трус.' WHERE entry=-1429001; /* content: Who dares disrupt the sanctity of Eldre\'Thalas? Face me, cowards! */
UPDATE script_texts SET content_loc8='Наконец-то я свободен от его проклятых уз.' WHERE entry=-1429002; /* content: At last... Freed from his cursed grasp! */
UPDATE script_texts SET content_loc8='Беги! Они идут!' WHERE entry=-1469002; /* content: Run! They are coming! */
UPDATE script_texts SET content_loc8='%s изменяется, мерцая.' WHERE entry=-1469003; /* content: %s flinches as its skin shimmers. */
UPDATE script_texts SET content_loc8='В этом мире, где время – ваш враг, оно – мой величайший союзник. Это великая игра под названием "жизнь", в которую, как вы думаете, вы играете, на самом деле играет вами. К нему я обращаюсь...' WHERE entry=-1469004; /* content: In this world where time is your enemy, it is my greatest ally. This grand game of life that you think you play in fact plays you. To that I say... */
UPDATE script_texts SET content_loc8='Пусть состязания начнутся!' WHERE entry=-1469005; /* content: Let the games begin! */
UPDATE script_texts SET content_loc8='Горите, мерзавцы, ГОРИТЕ!' WHERE entry=-1469009; /* content: BURN! You wretches! BURN! */
UPDATE script_texts SET content_loc8='Этого не может быть! Я здесь хозяин! Вы, смертные, ничто по сравнению со мной! Слышите? Ничто!' WHERE entry=-1469012; /* content: This cannot be!  I am the master here!  You mortals are nothing to my kind!  Do you hear me? Nothing! */
UPDATE script_texts SET content_loc8='И маги тоже? Осторожнее надо быть, когда играешь с магией...' WHERE entry=-1469013; /* content: Mages too? You should be more careful when you play with magic... */
UPDATE script_texts SET content_loc8='Я знаю, воины, вы можете бить сильнее! Ну-ка, покажите!' WHERE entry=-1469014; /* content: Warriors, I know you can hit harder than that! Let\'s see it! */
UPDATE script_texts SET content_loc8='Жрецы! Если вы собираетесь продолжать так лечить, то давайте хоть немного разнообразим процесс!' WHERE entry=-1469016; /* content: Priests! If you\'re going to keep healing like that, we might as well make it a little more interesting! */
UPDATE script_texts SET content_loc8='Шаманы, покажите, на что способны ваши тотемы!' WHERE entry=-1469018; /* content: Shamans, show me what your totems can do! */
UPDATE script_texts SET content_loc8='Чернокнижники, ну не беритесь вы за волшебство, которого сами не понимаете! Видите, что получилось?' WHERE entry=-1469019; /* content: Warlocks, you shouldn\'t be playing with magic you don\'t understand. See what happens? */
UPDATE script_texts SET content_loc8='Охотники со своими жалкими пугачами!' WHERE entry=-1469020; /* content: Hunters and your annoying pea-shooters! */
UPDATE script_texts SET content_loc8='Разбойники? Хватит прятаться, покажитесь!' WHERE entry=-1469021; /* content: Rogues? Stop hiding and face me! */
UPDATE script_texts SET content_loc8='Недоумки! Эти яйца ценнее, чем вы думаете!' WHERE entry=-1469023; /* content: Fools! These eggs are more precious than you know! */
UPDATE script_texts SET content_loc8='Да сколько же можно?! Вы заплатите за это головой!' WHERE entry=-1469024; /* content: No - not another one! I\'ll have your heads for this atrocity! */
UPDATE script_texts SET content_loc8='%s убегает, как только сила сферы пошла на спад.' WHERE entry=-1469033; /* content: %s flee as the controlling power of the orb is drained. */
UPDATE script_texts SET content_loc8='%s чувствует ваш страх.' WHERE entry=-1509000; /* content: %s senses your fear. */
UPDATE script_texts SET content_loc8='%s рассвирепел!' WHERE entry=-1509001; /* content: %s bristles with energy! */
UPDATE script_texts SET content_loc8='%s смотрит на |3-3($n).' WHERE entry=-1509002; /* content: %s sets eyes on $N! */
UPDATE script_texts SET content_loc8='Они пришли. Постарайся не дать себя убить, $gюноша:девушка;.' WHERE entry=-1509003; /* content: They come now. Try not to get yourself killed, young blood. */
UPDATE script_texts SET content_loc8='Помнишь, Раджакс, я говорил, что убью тебя последним?' WHERE entry=-1509004; /* content: Remember, Rajaxx, when I said I\'d kill you last? */
UPDATE script_texts SET content_loc8='Час возмездия близок! Да охватит мрак сердца наших врагов!' WHERE entry=-1509005; /* content: The time of our retribution is at hand! Let darkness reign in the hearts of our enemies! */
UPDATE script_texts SET content_loc8='Пусть наши враги трепещут! Смерть им!' WHERE entry=-1509007; /* content: Fear is for the enemy! Fear and death! */
UPDATE script_texts SET content_loc8='Настырная тварь! Я сам тебя убью!' WHERE entry=-1509010; /* content: Impudent fool! I will kill you myself! */
UPDATE script_texts SET content_loc8='Воины и капитаны! Продолжайте битву!' WHERE entry=-1509014; /* content: Warriors! Captains! Continue the fight! */
UPDATE script_texts SET content_loc8='Стены проломлены!' WHERE entry=-1509022; /* content: The walls have been breached! */
UPDATE script_texts SET content_loc8='С вами покончено.' WHERE entry=-1509026; /* content: You are terminated. */
UPDATE script_texts SET content_loc8='%s похищает вашу ману и превращается в камень.' WHERE entry=-1509028; /* content: %s drains your mana and turns to stone. */
UPDATE script_texts SET content_loc8='Подходи поближе!' WHERE entry=-1509029; /* content: Come get some! */
UPDATE script_texts SET content_loc8='Сначала убей, а потом спрашивай... Вперед!' WHERE entry=-1509030; /* content: Kill first, ask questions later... Incoming! */
UPDATE script_texts SET content_loc8='Я лгал...' WHERE entry=-1509031; /* content: I lied... */
UPDATE script_texts SET content_loc8='Пусть твоя смерть послужит уроком другим.' WHERE entry=-1531003; /* content: Let your death serve as an example! */
UPDATE script_texts SET content_loc8='Я приговариваю тебя к смерти!' WHERE entry=-1531009; /* content: I sentence you to death! */
UPDATE script_texts SET content_loc8='Я служу до последнего!' WHERE entry=-1531010; /* content: I serve to the last! */
UPDATE script_texts SET content_loc8='%s ослаблен!' WHERE entry=-1531011; /* content: %s is weakened! */
UPDATE script_texts SET content_loc8='Огромное висящее в воздухе око в центре зала поворачивает зрачок к вам. Вы стоите перед божеством.' WHERE entry=-1531012; /* content: The massive floating eyeball in the center of the chamber turns its gaze upon you. You stand before a god. */
UPDATE script_texts SET content_loc8='Всего лишь плоть и кости. Смертные – такая легкая добыча...' WHERE entry=-1531013; /* content: Only flesh and bone. Mortals are such easy prey... */
UPDATE script_texts SET content_loc8='Где твои манеры, братишка? Давай как следует поприветствуем наших гостей.' WHERE entry=-1531014; /* content: Where are your manners, brother. Let us properly welcome our guests. */
UPDATE script_texts SET content_loc8='Будет больно...' WHERE entry=-1531015; /* content: There will be pain... */
UPDATE script_texts SET content_loc8='Очень больно...' WHERE entry=-1531016; /* content: Oh so much pain... */
UPDATE script_texts SET content_loc8='От смерти не уйти!' WHERE entry=-1531023; /* content: You will not escape death! */
UPDATE script_texts SET content_loc8='Ваша судьба решена!' WHERE entry=-1531030; /* content: Your fate is sealed! */
UPDATE script_texts SET content_loc8='Век\'лор, я ощущаю твою боль!' WHERE entry=-1531031; /* content: Vek\'lor, I feel your pain! */
UPDATE script_texts SET content_loc8='О, добро пожаловать в мою гостиную.' WHERE entry=-1533000; /* content: Ahh... welcome to my parlor. */
UPDATE script_texts SET content_loc8='Посмотрим, какие вы на вкус!' WHERE entry=-1533001; /* content: Just a little taste... */
UPDATE script_texts SET content_loc8='Вам отсюда не выбраться.' WHERE entry=-1533002; /* content: There is no way out. */
UPDATE script_texts SET content_loc8='Бегите, бегите! Я люблю горячую кровь!' WHERE entry=-1533003; /* content: Yes, Run! It makes the blood pump faster! */
UPDATE script_texts SET content_loc8='Куда идти? Что делать? Выбирайте, но все пути ведут к боли... и смерти.' WHERE entry=-1533005; /* content: Where to go? What to do? So many choices that all end in pain, end in death. */
UPDATE script_texts SET content_loc8='Убейте их во имя господина!' WHERE entry=-1533010; /* content: Slay them in the master\'s name! */
UPDATE script_texts SET content_loc8='Вам не скрыться от меня!' WHERE entry=-1533011; /* content: You cannot hide from me! */
UPDATE script_texts SET content_loc8='Склонитесь передо мной, черви!' WHERE entry=-1533012; /* content: Kneel before me, worm! */
UPDATE script_texts SET content_loc8='Беги, пока еще можешь!' WHERE entry=-1533013; /* content: Run while you still can! */
UPDATE script_texts SET content_loc8='Тебе не повезло!' WHERE entry=-1533014; /* content: You have failed! */
UPDATE script_texts SET content_loc8='Жалкое ничтожество!' WHERE entry=-1533015; /* content: Pathetic wretch! */
UPDATE script_texts SET content_loc8='Лоскутик хочет поиграть!' WHERE entry=-1533017; /* content: Patchwerk want to play! */
UPDATE script_texts SET content_loc8='Кел\'Тузад объявил Лоскутика воплощением войны!' WHERE entry=-1533018; /* content: Kel\'Thuzad make Patchwerk his Avatar of War! */
UPDATE script_texts SET content_loc8='Закончили играть?' WHERE entry=-1533019; /* content: No more play? */
UPDATE script_texts SET content_loc8='%s разбрызгивает по комнате слизь!' WHERE entry=-1533021; /* content: %s sprays slime across the room! */
UPDATE script_texts SET content_loc8='Сталагг сокрушить вас!' WHERE entry=-1533023; /* content: Stalagg crush you! */
UPDATE script_texts SET content_loc8='Господин, спаси меня!' WHERE entry=-1533024; /* content: Stalagg kill! */
UPDATE script_texts SET content_loc8='Я скормлю вас господину!' WHERE entry=-1533026; /* content: Feed you to master! */
UPDATE script_texts SET content_loc8='Убивать!' WHERE entry=-1533030; /* content: KILL! */
UPDATE script_texts SET content_loc8='Спаси… бо…' WHERE entry=-1533035; /* content: Thank... you... */
UPDATE script_texts SET content_loc8='Умоляю, не-е-ет!' WHERE entry=-1533038; /* content: Help me! Save me! */
UPDATE script_texts SET content_loc8='Глупцы! Вы сами нашли свою погибель!' WHERE entry=-1533040; /* content: Foolishly you have sought your own demise. */
UPDATE script_texts SET content_loc8='Надеюсь, они проживут достаточно долго, чтобы я... успела представиться.' WHERE entry=-1533046; /* content: I do hope they stay alive long enough for me to... introduce myself. */
UPDATE script_texts SET content_loc8='Первая жертва будет на моем счету! Кто-нибудь хочет пари?' WHERE entry=-1533047; /* content: The first kill goes to me! Anyone care to wager? */
UPDATE script_texts SET content_loc8='Твоя жизнь принадлежит мне!' WHERE entry=-1533048; /* content: Your life is mine! */
UPDATE script_texts SET content_loc8='Кто следующий?' WHERE entry=-1533049; /* content: Who\'s next? */
UPDATE script_texts SET content_loc8='К оружию, олухи! У нас гости!' WHERE entry=-1533052; /* content: To arms, ye roustabouts! We\'ve got company! */
UPDATE script_texts SET content_loc8='Люблю мясо с хрустящей корочкой!' WHERE entry=-1533055; /* content: I like my meat extra crispy! */
UPDATE script_texts SET content_loc8='В следующий раз приводи больше друзей!' WHERE entry=-1533056; /* content: Next time, bring more friends! */
UPDATE script_texts SET content_loc8='Все это... было зря...' WHERE entry=-1533057; /* content: What a bloody waste this is! */
UPDATE script_texts SET content_loc8='Бегите, пока не поздно!' WHERE entry=-1533058; /* content: Flee, before it\'s too late! */
UPDATE script_texts SET content_loc8='Чужаки, откажитесь от этой безрассудной авантюры! Бегите, пока не поздно!' WHERE entry=-1533059; /* content: Invaders, cease this foolish venture at once! Turn away while you still can! */
UPDATE script_texts SET content_loc8='Остановитесь! Уходите прочь, пока еще есть время!' WHERE entry=-1533061; /* content: Do not continue! Turn back while there\'s still time! */
UPDATE script_texts SET content_loc8='Простите меня!' WHERE entry=-1533063; /* content: Forgive me! */
UPDATE script_texts SET content_loc8='Ты ищешь смерти?' WHERE entry=-1533065; /* content: You seek death? */
UPDATE script_texts SET content_loc8='Никто не пройдет!' WHERE entry=-1533066; /* content: None shall pass! */
UPDATE script_texts SET content_loc8='Тихо!' WHERE entry=-1533067; /* content: Be still! */
UPDATE script_texts SET content_loc8='И даже смерть не даст тебе покоя.' WHERE entry=-1533068; /* content: You will find no peace in death. */
UPDATE script_texts SET content_loc8='Воля господина исполнена.' WHERE entry=-1533069; /* content: The master\'s will is done. */
UPDATE script_texts SET content_loc8='Жизнь бессмысленна. Лишь смерть покажет, чего мы стоим.' WHERE entry=-1533073; /* content: Life is meaningless. It is in death that we are truly tested. */
UPDATE script_texts SET content_loc8='Слава господину!' WHERE entry=-1533075; /* content: Glory to the master! */
UPDATE script_texts SET content_loc8='Ваша жизнь уже не принадлежит вам!' WHERE entry=-1533076; /* content: Your life is forfeit! */
UPDATE script_texts SET content_loc8='Смерть чужакам!' WHERE entry=-1533077; /* content: Die, trespasser! */
UPDATE script_texts SET content_loc8='Встаньте, мои воины! Встаньте и сражайтесь вновь!' WHERE entry=-1533078; /* content: Rise, my soldiers! Rise and fight once more! */
UPDATE script_texts SET content_loc8='Моя задача выполнена!' WHERE entry=-1533079; /* content: My task is done! */
UPDATE script_texts SET content_loc8='Больше не дыши!' WHERE entry=-1533080; /* content: Breathe no more! */
UPDATE script_texts SET content_loc8='%s делает глубокий вдох...' WHERE entry=-1533082; /* content: %s takes in a deep breath... */
UPDATE script_texts SET content_loc8='Нет!!! Будьте вы прокляты, незваные гости! Армии Короля-лича поймают вас. Вы не сбежите от своей погибели...' WHERE entry=-1533089; /* content: No!!! A curse upon you, interlopers! The armies of the Lich King will hunt you down. You will not escape your fate... */
UPDATE script_texts SET content_loc8='Я устал от этих игр. Еще немного – и я отправлю ваши души во мрак забвения!' WHERE entry=-1533092; /* content: I grow tired of these games. Proceed, and I will banish your souls to oblivion! */
UPDATE script_texts SET content_loc8='Вы не имеете представления, что за ужас ждет вас впереди. Вы ничего не знаете! Замерзшее сердце Наксрамаса ждет вас!' WHERE entry=-1533093; /* content: You have no idea what horrors lie ahead. You have seen nothing! The frozen heart of Naxxramas awaits you! */
UPDATE script_texts SET content_loc8='Молите о пощаде!' WHERE entry=-1533094; /* content: Pray for mercy! */
UPDATE script_texts SET content_loc8='Кричите! Кричите изо всех сил!' WHERE entry=-1533095; /* content: Scream your dying breath! */
UPDATE script_texts SET content_loc8='Вы уже мертвы!' WHERE entry=-1533096; /* content: The end is upon you! */
UPDATE script_texts SET content_loc8='Тебя ждет тьма и пустота!' WHERE entry=-1533097; /* content: The dark void awaits you! */
UPDATE script_texts SET content_loc8='Вам не уйти от возмездия!' WHERE entry=-1533101; /* content: There will be no escape! */
UPDATE script_texts SET content_loc8='Я заморожу кровь в ваших жилах!' WHERE entry=-1533102; /* content: I will freeze the blood in your veins! */
UPDATE script_texts SET content_loc8='Вот ты где!' WHERE entry=-1533110; /* content: I see you! */
UPDATE script_texts SET content_loc8='Все народы этого мира исчезнут без следа. Это лишь вопрос времени.' WHERE entry=-1533113; /* content: The races of the world will perish. It is only a matter of time. */
UPDATE script_texts SET content_loc8='Вам конец.' WHERE entry=-1533116; /* content: The end is upon you. */
UPDATE script_texts SET content_loc8='Сражайтесь!' WHERE entry=-1533121; /* content: Stand and fight! */
UPDATE script_texts SET content_loc8='Покажите мне, на что способны!' WHERE entry=-1533122; /* content: Show me what you\'ve got! */
UPDATE script_texts SET content_loc8='Вспомните, чему я вас учил!' WHERE entry=-1533125; /* content: Do as I taught you! */
UPDATE script_texts SET content_loc8='Не щадите никого!' WHERE entry=-1533126; /* content: Show them no mercy! */
UPDATE script_texts SET content_loc8='Вы разочаровываете меня, ученики.' WHERE entry=-1533127; /* content: You disappoint me, students! */
UPDATE script_texts SET content_loc8='%s призывает скелетов-воинов!' WHERE entry=-1533130; /* content: %s summons forth Skeletal Warriors! */
UPDATE script_texts SET content_loc8='%s поднимает новых скелетов!' WHERE entry=-1533131; /* content: %s raises more skeletons! */
UPDATE script_texts SET content_loc8='%s перемещается на находящийся выше балкон!' WHERE entry=-1533132; /* content: %s teleports to the balcony above! */
UPDATE script_texts SET content_loc8='%s телепортируется обратно в гущу сражения!' WHERE entry=-1533133; /* content: %s teleports back into the battle! */
UPDATE script_texts SET content_loc8='Страж Ледяной Короны вступает в бой!' WHERE entry=-1533134; /* content: A Guardian of Icecrown enters the fight! */
UPDATE script_texts SET content_loc8='%s телепортируется и начинает творить заклинание!' WHERE entry=-1533136; /* content: %s teleports and begins to channel a spell! */
UPDATE script_texts SET content_loc8='%s снова бросается в бой!' WHERE entry=-1533137; /* content: %s rushes to attack once more! */
UPDATE script_texts SET content_loc8='%s телепортируется в гущу боя!' WHERE entry=-1533138; /* content: %s teleports into the fray! */
UPDATE script_texts SET content_loc8='Центральные ворота открываются!' WHERE entry=-1533139; /* content: The central gate opens! */
UPDATE script_texts SET content_loc8='Вам никогда не понять, сколь велика сила, потревоженная вами.' WHERE entry=-1533140; /* content: Brazenly you have disregarded powers beyond your understanding. */
UPDATE script_texts SET content_loc8='Вы сражались отчаянно, чтобы проникнуть в царство жнеца.' WHERE entry=-1533141; /* content: You have fought hard to invade the realm of the harvester. */
UPDATE script_texts SET content_loc8='Аура некротической энергии препятствует любому исцелению!' WHERE entry=-1533143; /* content: An aura of necrotic energy blocks all healing! */
UPDATE script_texts SET content_loc8='Аура тает, снова появляется возможность лечить!' WHERE entry=-1533145; /* content: The aura fades away, allowing healing once more! */
UPDATE script_texts SET content_loc8='%s свивает паутину в кокон!' WHERE entry=-1533146; /* content: %s spins her web into a cocoon! */
UPDATE script_texts SET content_loc8='В паутине появляются паучата!' WHERE entry=-1533147; /* content: Spiderlings appear on the web! */
UPDATE script_texts SET content_loc8='%s разбрасывает нити паутины.' WHERE entry=-1533148; /* content: %s sprays strands of web everywhere! */
UPDATE script_texts SET content_loc8='%s теряет связь!' WHERE entry=-1533149; /* content: %s loses its link! */
UPDATE script_texts SET content_loc8='%s перезагружается!' WHERE entry=-1533150; /* content: %s overloads! */
UPDATE script_texts SET content_loc8='Полярность изменилась!' WHERE entry=-1533151; /* content: The polarity has shifted! */
UPDATE script_texts SET content_loc8='%s уничтожает всякую плоть, что находится поблизости!' WHERE entry=-1533152; /* content: %s decimates all nearby flesh! */
UPDATE script_texts SET content_loc8='Трупные скарабеи выползают из тела некрорахнида-стражника!' WHERE entry=-1533155; /* content: Corpse Scarabs appear from a Crypt Guard\'s corpse! */
UPDATE script_texts SET content_loc8='%s применяет на всех заклинание "Неутолимая боль"!' WHERE entry=-1533156; /* content: %s casts Unyielding Pain on everyone! */
UPDATE script_texts SET content_loc8='%s впрыскивает вам мутаген!' WHERE entry=-1533158; /* content: %s injects you with a mutagen! */

