UPDATE db_script_string SET content_loc8='Поганище уже в лагере Ночного Дозора! Скорее, мы должны остановить его, пока он не дошел до города!' WHERE entry=2000000187; /* content: The abomination has overrun the Night Watch camp! Quickly, we must intercept it before it reaches town! */
