UPDATE db_script_string SET content_loc8='Он боялся, что его силы иссякнут и что скоро даже мы, последние самоцветы его духа, потускнеем и умрем.' WHERE entry=2000000205; /* content: He worried that his power weakened, that soon even we last of his precious soulgems would fade and die. */
UPDATE db_script_string SET content_loc8='%s нюхает эликсир, а затем набрасывается на него!' WHERE entry=2000000207; /* content: %s sniffs the elixir then eagerly digs in! */
