UPDATE db_script_string SET content_loc8='Когда я с тобой разберусь, твоя голова пойдет на шляпку для Ограбиси.' WHERE entry=2000005100; /* content: Perhaps Ograbisi will use your head as a tiny little hat when I\'m through with you. */
UPDATE db_script_string SET content_loc8='Там, откуда я родом, нас били за то, что мы открывали дверь в соседнюю камеру!' WHERE entry=2000005101; /* content: Where I come from, you get shanked for opening another inmate\'s cell door! */
UPDATE db_script_string SET content_loc8='Ах, какая сладкая парочка! А я тут воду разношу, одна на всю округу.' WHERE entry=2000005103; /* content: Jack and Jill my wrinkled patoot! I do all the water luggin\' \'round here. */
UPDATE db_script_string SET content_loc8='Где вода, Эмма? Найди воду, Эмма... Если бы не я, они бы вообще не знали, как вода выглядит.' WHERE entry=2000005104; /* content: Where\'s the water, Emma? Get the water, Emma? If\'n it weren\'t fer me, that lot wouldn\'t know what water looks like. */
UPDATE db_script_string SET content_loc8='Как будто в моем возрасте заняться больше нечем – знай ведра носи!' WHERE entry=2000005106; /* content: As if I don\'t have better things to do in my old age than carry buckets of water. */
UPDATE db_script_string SET content_loc8='Они говорят, что он может иногда превращаться в ворона.' WHERE entry=2000005109; /* content: They say he can turn into a raven sometimes. */
UPDATE db_script_string SET content_loc8='Они говорят, что он может иногда превращаться в ворона.' WHERE entry=2000005110; /* content: They say he can turn into a raven sometimes. */
UPDATE db_script_string SET content_loc8='И затем кролик откусил его голову... Я клянусь.' WHERE entry=2000005111; /* content: And then the rabbit just bit his head off... I swear. */
UPDATE db_script_string SET content_loc8='Я клянусь, люди фактически видели их. Панды действительно существуют!!' WHERE entry=2000005112; /* content: I swear, people have actualy seen them. Pandaren really do exist!! */
UPDATE db_script_string SET content_loc8='Вы знаете почему у Орков красные глаза? Это - потому что они пьют кровь!' WHERE entry=2000005113; /* content: You know why Orcs eyes glow red? It\'s because they drink blood! */
UPDATE db_script_string SET content_loc8='Я не думаю, что в этих каналах есть хоть какая-нибудь рыба.' WHERE entry=2000005114; /* content: I dont think there\'s any fish in these canals. */
