UPDATE locales_npc_text SET Text0_0_loc6='¿Quieres mejorar tu habilidad con las armas? El maestro de armas está en nivel más elevado de la Grada de los Mercaderes, en el Bancal del Guerrero.',Text0_1_loc6='¿Quieres mejorar tu habilidad con las armas? El maestro de armas está en nivel más elevado de la Grada de los Mercaderes, en el Bancal del Guerrero.' WHERE entry=9565; /* Text0_0: You wish to improve your weapon skills?  Our weapon master is located on the highest level of the Trader\'s Tier, on the Warriors\' Terrace. */ /* Text0_1: You wish to improve your weapon skills?  Our weapon master is located on the highest level of the Trader\'s Tier, on the Warriors\' Terrace. */
