UPDATE locales_npc_text SET Text0_0_loc6='Hola. ¿Puedes ayudarme a salir de aquí?',Text0_1_loc6='Hola. ¿Puedes ayudarme a salir de aquí?' WHERE entry=9568; /* Text0_0: Hi!  Do you think you might help me to get out of here? */ /* Text0_1: Hi!  Do you think you might help me to get out of here? */
