UPDATE db_script_string SET content_loc7='¿¿Quemaescoria??? ¡Ah! ¿Crematoria? Debo de estar quedándome sordo... No, nunca he oído hablar de ello... ¡Deja de molestarme!' WHERE entry=2000005215; /* content: The what-bringer? Oh, ASHbringer? Never heard of it... What? Stop bothering me! */
UPDATE db_script_string SET content_loc7='¡Eh, que alguien me ayude a coger esta cosa!' WHERE entry=2000005218; /* content: Hey, someone help me catch this thing! */
UPDATE db_script_string SET content_loc7='Debería haber tomado la pastilla translúcida.' WHERE entry=2000005228; /* content: I should have taken the translucent pill. */
UPDATE db_script_string SET content_loc7='¡No se me paga lo suficiente por este excremento de vaca!' WHERE entry=2000005229; /* content: I don\'t get paid enough for this bull excrement! */
UPDATE db_script_string SET content_loc7='¡No puedo pagar tantos impuestos! ¿Seguridad Social? ¿SS? ¡No le queda mal el nombre!' WHERE entry=2000005234; /* content: I can\'t take these taxes! F.I.C.A. me? F.I.C.A. YOU! */
UPDATE db_script_string SET content_loc7='¡Sean testigos del innegable poder de su Jefe de Guerra! ¡Sientan cómo los eleva el grito de llamamiento de sus cazadragones!' WHERE entry=2000005237; /* content: Bear witness to the undeniable power of your Warchief! Be lifted by the rallying cry of your dragon slayers! */
UPDATE db_script_string SET content_loc7='Ciudadanos y aliados de Ventormenta, en el día de hoy, hemos hecho historia. $n ha derrotado a quienes pretendían usurpar el gobierno del reino. Venid y reuníos para honrar a nuestros héroes.' WHERE entry=2000005238; /* content: Citizens and allies of Stormwind, on this day, history has been made. $N has laid waste to that which had attempted to usurp the rule of the kingdom. Gather round and join me in honoring our heroes. */
UPDATE db_script_string SET content_loc7='¡Contemplen el poder de la Alianza! La aterradora Onyxia, está colgada de los arcos. ¡Que el grito de llamamiento de los cazadores de dragones eleve sus espíritus!' WHERE entry=2000005239; /* content: Behold the might of the Alliance! The dread lady, Onyxia, hangs from the arches! Let the rallying cry of the dragon slayer lift your spirits! */
UPDATE db_script_string SET content_loc7='El número de mis compañeros merma, diosa, y mi propio poder pronto será insuficiente para contener a los demonios de Frondavil.' WHERE entry=2000005240; /* content: The numbers of my companions dwindles, goddess, and my own power shall soon be insufficient to hold back the demons of Felwood. */
UPDATE db_script_string SET content_loc7='¡Diosa, otórgame el poder para vencer a mis enemigos! Escúchame, por favor. ¡Lo necesito desesperadamente y mi causa es justa!' WHERE entry=2000005241; /* content: Goddess, grant me the power to overcome my enemies! Hear me, please, my need is desperate and my cause is just! */
UPDATE db_script_string SET content_loc7='He fallado... He fallado en mi deber... he fallado a mi gente... El huargen huyó veloz y la guadaña de mi diosa se ha perdido.' WHERE entry=2000005243; /* content: I have failed... I have failed in my duty... failed my people... The worgen run rampant, and the Scythe of my goddess is lost. */
UPDATE db_script_string SET content_loc7='Este mal que he desatado... en cuya mano yace el poder que mi diosa me concedió... ¿Pero de quién es la mano que lo dirige?' WHERE entry=2000005244; /* content: This evil I have unleashed... In whose hand does the power my goddess granted to me now lie... By whose hand is it directed? */
UPDATE db_script_string SET content_loc7='Canteros... err... Que avisen a Defias: El ancla oxidada se hundirá esta noche.' WHERE entry=2000005302; /* content: Stonemasons. . .errr. . Defias be warned:  The rusty anchor sinks tonight. */
UPDATE db_script_string SET content_loc7='¡Por las estrellas! ¡Se ha invocado a un espíritu!' WHERE entry=2000005304; /* content: By the stars! A spirit has been summoned! */
UPDATE db_script_string SET content_loc7='¿Quién ha invocado a Aman?' WHERE entry=2000005305; /* content: Who hath summoned forth Aman? */
UPDATE db_script_string SET content_loc7='Ah, veo que trabajas arduamente con las reliquias del pasado.' WHERE entry=2000005306; /* content: Ah, I see you toil with relics of the past. */
UPDATE db_script_string SET content_loc7='Te advierto que incluso tus creadores pueden equivocarse.' WHERE entry=2000005307; /* content: Be warned that even your creators are fallible. */
UPDATE db_script_string SET content_loc7='Si escarbas demasiado en tu pasado, tu futuro puede tener un fin repentino.' WHERE entry=2000005308; /* content: Digging too deep into your past might bring an abrupt end to your future. */
UPDATE db_script_string SET content_loc7='¡Está claro que es un misterio del pasado! ¡Pero también la llave de nuestro futuro!' WHERE entry=2000005310; /* content: It\'s a mystery of the past indeed! But a key to our future! */
UPDATE db_script_string SET content_loc7='¡Caramba! ¡Esa birra Zarpanegra sí que pega fuerte!' WHERE entry=2000005311; /* content: My, that Black Claw stout packs quite a punch! */
UPDATE db_script_string SET content_loc7='¿Pero qué quiere decir "alérgico"? ¿Y qué tiene que ver eso con mis gatitos?' WHERE entry=2000005314; /* content: What does allergic mean anyway? And what does it have to do with either of my kitties? */
UPDATE db_script_string SET content_loc7='No puedo creer que papá no me deje conservar a tu hermana.' WHERE entry=2000005315; /* content: I can\'t believe dad won\'t let me keep your sister. */
UPDATE db_script_string SET content_loc7='No te preocupes, te encontraré un buen hogar.' WHERE entry=2000005316; /* content: Don\'t worry, I\'ll find a good home for ya. */
UPDATE db_script_string SET content_loc7='¿Alguien puede darle un hogar a mi adorable gatito?' WHERE entry=2000005317; /* content: Can anyone give my adorable, extra little kitty a home? */
UPDATE db_script_string SET content_loc7='¿Alguien podría proporcionar un buen hogar a mi gatito?' WHERE entry=2000005318; /* content: Will someone please give my little kitten a good home? */
UPDATE db_script_string SET content_loc7='Gatitos en venta, estamos buscándoles un buen hogar.' WHERE entry=2000005319; /* content: Kitten for sale, looking for a good home. */
UPDATE db_script_string SET content_loc7='¿Puedo ir a buscar más piedras para mi collar, papá?' WHERE entry=2000005320; /* content: Can I go collect more rocks for my necklace, Papa? */
UPDATE db_script_string SET content_loc7='¡Dabu! ¡Tendré cuidado, papá!' WHERE entry=2000005322; /* content: Dabu! I will be careful Papa! */
UPDATE db_script_string SET content_loc7='Ooohh... Esta piedra es bonita y me gusta mucho el color. A ver si puedo encontrar más iguales...' WHERE entry=2000005323; /* content: Ooohh... This rock is nice and colorful. Let\'s see if I can find more like it... */
UPDATE db_script_string SET content_loc7='Mmm... Esa piedra es bonita, pero demasiado pequeña.' WHERE entry=2000005324; /* content: Hmm... That rock is nice, but too small. */
UPDATE db_script_string SET content_loc7='Seguro que a mi amigo Gruk le gustaría esta piedra para la lanza que está haciendo. Es muy afilada... Espero que le guste.' WHERE entry=2000005326; /* content: I bet my friend Gruk would like this rock for the spear he is making! It\'s very sharp... I hope he likes it. */
UPDATE db_script_string SET content_loc7='Ya está... Con esto debería tener suficientes piedras para hacer un collar. De todas formas, ya debería volver a casa...' WHERE entry=2000005327; /* content: There... That should be enough rocks to make a necklace. I should probably get home now anyway. */
UPDATE db_script_string SET content_loc7='¿Anaia?... ¿Me engañan mis ojos? ¿Eres tú de verdad?' WHERE entry=2000005336; /* content: Anaya...? Do my eyes deceive me? Is it really you? */
UPDATE db_script_string SET content_loc7='Él ha sido cruel con nosotros, amor mío, pero ten por seguro que ahora sí permaneceremos unidos.' WHERE entry=2000005337; /* content: The ages have been cruel to you and I, my love, but be assured, it is, and at long last we are reunited. */
UPDATE db_script_string SET content_loc7='El destino ha sido muy cruel al permitirnos volver a estar juntos solo tras miles de años de separación...' WHERE entry=2000005338; /* content: That the fates should be so cruel as to permit us only this after a thousand years apart... */
UPDATE db_script_string SET content_loc7='¿Me odias, amor mío? Por haber tenido que destruir tu forma viviente y por liberar tu espíritu de tan triste cautiverio...' WHERE entry=2000005339; /* content: Do you hate me, my love? That I was forced to destroy your living form, that your spirit be released from unhappy bondage. */
UPDATE db_script_string SET content_loc7='No permitas que esto atormente tu corazón, amor mío. Me has liberado de la esclavitud y por ello te amo más aún.' WHERE entry=2000005340; /* content: Let it not trouble your heart, beloved. You have freed me from slavery, and for that I love you all the more. */
UPDATE db_script_string SET content_loc7='Desafortunadamente, esto también debe interrumpirse... Los lazos que me atan a este mundo se debilitan y me arrastran...' WHERE entry=2000005341; /* content: Sadly, even this must be cut short... The ties that bind me to this world weaken, and pull me away... */
UPDATE db_script_string SET content_loc7='¡No! Anaia... ¡Anaia! ¡No me abandones! Por favor...' WHERE entry=2000005342; /* content: No! Anaya... Anaya! Don\'t leave me! Please... */
UPDATE db_script_string SET content_loc7='Adiós, Cerellean, hasta que volvamos a reunirnos...' WHERE entry=2000005343; /* content: Farewell, Cerellean, until we are joined once again... */
UPDATE db_script_string SET content_loc7='¿Cómo, amor mío? ¿Cómo encontraré fuerzas para enfrentarme a las edades del mundo sin ti a mi lado...?' WHERE entry=2000005345; /* content: How, my love? How will I find the strength to face the ages of the world without you by my side... */
UPDATE db_script_string SET content_loc7='Salto de Fonroca se encuentra en las montañas al Este y Norte.' WHERE entry=2000005347; /* content: Cliffspring Falls lies along the mountains to the east and north. */
UPDATE db_script_string SET content_loc7='%s quita el polvo a la base del artefacto.' WHERE entry=2000005348; /* content: %s dusts the base of the artifact. */
UPDATE db_script_string SET content_loc7='%s examina meticulosamente el gran jarrón.' WHERE entry=2000005349; /* content: %s meticulously examines the large vase. */
UPDATE db_script_string SET content_loc7='Pobre Dorius. Si alguna vez pongo mis manos encima de esos Hierro Negro, que la Luz me asista.' WHERE entry=2000005350; /* content: Poor Dorius. If I ever get my hands on those Dark Irons, so help me... */
UPDATE db_script_string SET content_loc7='¿Qué ha sido ese ruido? Parecía un rugido.' WHERE entry=2000005384; /* content: What was that noise? It sounded like a roar. */
UPDATE db_script_string SET content_loc7='¡¿Zarpadura?! Ahí mismo $gamigo mío:amiga mía;... ¿dónde está tu maestro Volcor?' WHERE entry=2000005386; /* content: Grimclaw?! Easy there, my friend... where is your master Volcor? */
UPDATE db_script_string SET content_loc7='¡Guau! Espera. Un momento...' WHERE entry=2000005388; /* content: Whoa, whoa there, my friend. One moment... */
UPDATE db_script_string SET content_loc7='Ahí está... esto deberá servir. Ahora dime qué ha pasado, Zarpadura.' WHERE entry=2000005390; /* content: There... that should help. Now, tell me what\'s happened, Grimclaw. */
UPDATE db_script_string SET content_loc7='Ya entiendo, $gamigo mío:amiga mía;. Encontraré a alguien que ayude a tu maestro. Vuelve junto a él ahora, o al menos permanece cerca de él.' WHERE entry=2000005392; /* content: I understand, my friend. I shall find someone to help your master. Go back to him now, or at least stay close. */
UPDATE db_script_string SET content_loc7='Si dispones de tiempo, podrías ayudar a Zarpadura y a su maestro Volcor. Si te interesa, habla conmigo más adelante...' WHERE entry=2000005394; /* content: If you have the time, Grimclaw and his master Volcor could use your help. If you\'re interested, speak with me further... */
UPDATE db_script_string SET content_loc7='¡Si quieres la llave tendrás que cogerme!' WHERE entry=2000005460; /* content: If you want the key, you\'ll have to catch me! */
UPDATE db_script_string SET content_loc7='¡Cógeme si puedes! ¡Corro tan rápido como el viento!' WHERE entry=2000005461; /* content: Chase me if you dare! I run without a care! */
UPDATE db_script_string SET content_loc7='¿Por qué ibas a querer hacerme daño? ¡Vamos! ¡Podemos ser amigos!' WHERE entry=2000005462; /* content: Why would you ever want to harm me!? Come. Friends we can be! */
UPDATE db_script_string SET content_loc7='¿MORIR? ¡Estás haciendo llorar a Pusillín!' WHERE entry=2000005463; /* content: DIE?! You make Pusillin cry! */
UPDATE db_script_string SET content_loc7='¡Saluda a mis amiguitos!' WHERE entry=2000005464; /* content: Say hello to my little friends! */
UPDATE db_script_string SET content_loc7='$n, por favor, echa un vistazo a la muestra de hiedravil que cogí en las cuevas, así sabrás lo que estás buscando...' WHERE entry=2000005465; /* content: $N, please take a look at the sample of the Vylestem vine that I took from the caverns, so that you know what to look for... */
UPDATE db_script_string SET content_loc7='¡Vale, allá voy!' WHERE entry=2000005548; /* content: Ok, here I go! */
UPDATE db_script_string SET content_loc7='¡Venga, en marcha!' WHERE entry=2000005553; /* content: Let\'s move forward! */
UPDATE db_script_string SET content_loc7='¡Nadie puede robar los secretos de los Creadores!' WHERE entry=2000005555; /* content: None may steal the secrets of the Makers! */
UPDATE db_script_string SET content_loc7='Buscad en estos escombros.' WHERE entry=2000005556; /* content: Search this rubble. */
UPDATE db_script_string SET content_loc7='¡Encontrad algo, perros!' WHERE entry=2000005557; /* content: Find me something good, you dogs! */
UPDATE db_script_string SET content_loc7='Espera... ¿has oído eso? ¡Algo se aproxima desde el Oeste!' WHERE entry=2000005627; /* content: Wait... did you hear that? Something approaches from the west! */
UPDATE db_script_string SET content_loc7='¡Me he quedado sin flechas! Me temo que si viene alguno más tendrás que ocuparte tú de ellos.' WHERE entry=2000005628; /* content: I\'ve run out of arrows! I\'m afraid if any more come you will need to take them on by yourself my friend. */
UPDATE db_script_string SET content_loc7='¡Alabada sea Elune! No sé si habría acabado el día con vida si no fuera por ti, $gamigo:amiga;.' WHERE entry=2000005629; /* content: Praise Elune! I don\'t know if I could have survived the day without you, friend. */
UPDATE db_script_string SET content_loc7='Por favor, ve a hablar con la centinela Onaeya en la Atalaya de Maestra, en Vallefresno, procurará dar una recompensa adecuada a la valentía que has mostrado este día.' WHERE entry=2000005631; /* content: Please speak with Sentinel Onaeya at Maestra\'s Post in Ashenvale, she will see to it that you are properly rewarded for your bravery this day. */
UPDATE db_script_string SET content_loc7='¡Zafarrancho de combate! ¡Se acercan nagas!' WHERE entry=2000005632; /* content: All hands to battle stations! Naga incoming! */
UPDATE db_script_string SET content_loc7='Si conseguimos pararlos ahora, seguro que estaremos fuera de peligro.' WHERE entry=2000005633; /* content: If we can just hold them now, I am sure we will be in the clear. */
UPDATE db_script_string SET content_loc7='¿Quieres saber más sobre la piedra? Sígueme.' WHERE entry=2000005646; /* content: You wish to learn of the stone? Follow me. */
UPDATE db_script_string SET content_loc7='He vivido durante demasiado tiempo en mi forma maldita. Me has liberado... Se te recompensará por tu duro trabajo.' WHERE entry=2000005647; /* content: For so long I have drifted in my cursed form. You have freed me... Your hard work shall be repaid. */
UPDATE db_script_string SET content_loc7='Por favor, haz lo que te digo, $n.' WHERE entry=2000005648; /* content: Please do as I instruct you, $N. */
UPDATE db_script_string SET content_loc7='Lee en voz alta el libro que he colocado ante ti.' WHERE entry=2000005649; /* content: Read this tome I have placed before you, and speak the words aloud. */
UPDATE db_script_string SET content_loc7='%s comienza a canalizar su energía, concentrándose en la piedra.' WHERE entry=2000005650; /* content: %s begins to channel his energy, focusing on the stone. */
UPDATE db_script_string SET content_loc7='Juntas, las dos partes se convierten en una, de nuevo.' WHERE entry=2000005651; /* content: Together, the two parts shall become one, once again. */
UPDATE db_script_string SET content_loc7='Shal myrinan ishnu daldorah...' WHERE entry=2000005652; /* content: Shal myrinan ishnu daldorah... */
UPDATE db_script_string SET content_loc7='¡Mi cetro volverá a estar completo!' WHERE entry=2000005653; /* content: My scepter will once again become whole! */
UPDATE db_script_string SET content_loc7='%s localiza al orco...' WHERE entry=2000005730; /* content: %s picks the orc target... */
UPDATE db_script_string SET content_loc7='%s falla...' WHERE entry=2000005731; /* content: %s misses... */
UPDATE db_script_string SET content_loc7='%s afina la puntería...' WHERE entry=2000005732; /* content: %s takes a more careful aim... */
UPDATE db_script_string SET content_loc7='%s falla de nuevo...' WHERE entry=2000005733; /* content: %s misses again... */
UPDATE db_script_string SET content_loc7='%s apunta al orco por casualidad...' WHERE entry=2000005734; /* content: %s casually points at the orc target... */
UPDATE db_script_string SET content_loc7='%s acierta.' WHERE entry=2000005735; /* content: %s hits! */
UPDATE db_script_string SET content_loc7='%s apunta a las botellas...' WHERE entry=2000005736; /* content: %s takes aim at the bottles... */
UPDATE db_script_string SET content_loc7='%s falla...' WHERE entry=2000005737; /* content: %s misses... */
UPDATE db_script_string SET content_loc7='%s afina la puntería con las botellas...' WHERE entry=2000005738; /* content: %s takes aim at the bottles again... */
UPDATE db_script_string SET content_loc7='%s falla de nuevo...' WHERE entry=2000005739; /* content: %s misses again... */
UPDATE db_script_string SET content_loc7='%s dispara rápidamente al punto muerto del blanco central...' WHERE entry=2000005740; /* content: %s quickly shoots the middle target dead center... */
UPDATE db_script_string SET content_loc7='%s entonces apunta a las botellas...' WHERE entry=2000005741; /* content: %s then takes aim at the bottles... */
UPDATE db_script_string SET content_loc7='%s solo le da a un lado de la caja.' WHERE entry=2000005742; /* content: %s only hits the side of the box! */
UPDATE db_script_string SET content_loc7='%s dispara rápidamente al orco de trapo...' WHERE entry=2000005743; /* content: %s quickly shoots the orc dummy... */
UPDATE db_script_string SET content_loc7='%s se vuelve hacia el blanco de papel...' WHERE entry=2000005744; /* content: %s turns to the paper target... */
UPDATE db_script_string SET content_loc7='%s apunta sin desenfundar y dispara rápidamente a las botellas...' WHERE entry=2000005745; /* content: %s shoots from the hip at the bottles... */
UPDATE db_script_string SET content_loc7='Ahora probémoslo...' WHERE entry=2000005752; /* content: Now let\'s try it... */
UPDATE db_script_string SET content_loc7='¡Ahora, voy a buscar a una arpía desprevenida!' WHERE entry=2000005768; /* content: Now to find an unsuspecting Harpy! */
UPDATE db_script_string SET content_loc7='¡Ahí hay una! ¡Por la Horda!' WHERE entry=2000005769; /* content: There\'s one! For the Horde! */
UPDATE db_script_string SET content_loc7='¡Toma eso, asquerosa arpía! ¡Síiiii!' WHERE entry=2000005770; /* content: Take that you mangy Harpy! Hyaa! */
UPDATE db_script_string SET content_loc7='¡No puedes escaparte de un guerrero de la Horda! ¡Toma esto! ¡Y esto!' WHERE entry=2000005771; /* content: You can\'t get away from a warrior of the Horde! Take that! And that! */
UPDATE db_script_string SET content_loc7='Mogg, entra. No es seguro que estés ahí fuera mientras tu padre no está.' WHERE entry=2000005774; /* content: Mogg, get inside. It is not safe out there while your father is gone. */
UPDATE db_script_string SET content_loc7='Debemos darnos prisa.' WHERE entry=2000005780; /* content: We must hurry. */
UPDATE db_script_string SET content_loc7='¡Espíritus del bosque, salid y atended a la llamada de la naturaleza!' WHERE entry=2000005781; /* content: Spirits of the forest come forth and heed nature\'s call! */
UPDATE db_script_string SET content_loc7='¡Espíritus del bosque, se os necesita! ¡Id deprisa hacia la Vega Carbonizada!' WHERE entry=2000005782; /* content: Spirits of the forest, you are needed! Make haste to the Charred Vale! */
UPDATE db_script_string SET content_loc7='%s entra en trance...' WHERE entry=2000005800; /* content: %s enters a trance... */
UPDATE db_script_string SET content_loc7='El agua de los videntes está lista para su consumo, $n.' WHERE entry=2000005803; /* content: The Water of the Seers is ready for your consumption, $N. */

