UPDATE script_texts SET content_loc7='Vale, ¡tú ganas! ¡Me rindo! ¡Pero no me hagas daño!' WHERE entry=-1000355; /* content: All right, you win! I surrender! Just don\'t hurt me! */
UPDATE script_texts SET content_loc7='¡Vámonos antes de que descubran que estoy libre!' WHERE entry=-1000357; /* content: Let\'s go before they find out I\'m free! */
UPDATE script_texts SET content_loc7='¡Cuidado! ¡Nos atacan!' WHERE entry=-1000358; /* content: Look out! We\'re under attack! */
UPDATE script_texts SET content_loc7='Gracias por ayudarme. Puedo seguir sola a partir de aquí.' WHERE entry=-1000359; /* content: Thank you for helping me. I know my way back from here. */
UPDATE script_texts SET content_loc7='¡Los hilos de la VIDA se han roto! ¡Tenemos que vengar a los Soñadores!' WHERE entry=-1000360; /* content: The strands of LIFE have been severed! The Dreamers must be avenged! */
UPDATE script_texts SET content_loc7='Venid, Soñadores ¡y reclamad venganza!' WHERE entry=-1000361; /* content: Come forth, ye Dreamers - and claim your vengeance! */
UPDATE script_texts SET content_loc7='Mil gracias... Puedo encontrar el camino de vuelta al Campamento del Tramo Blanco. Asegúrate de hablar con Motega Pirocabello; quizás puedas evitar que me envíe a casa.' WHERE entry=-1000364; /* content: Thanks a bunch... I can find my way back to Whitereach Post from here. Be sure to talk with Motega Firemane; perhaps you can keep him from sending me home. */
UPDATE script_texts SET content_loc7='En guardia... Arnak tiene un poder extraño sobre los Tótem Siniestro... no se alegrarán de verme huir.' WHERE entry=-1000365; /* content: Be on guard... Arnak has some strange power over the Grimtotem... they will not be happy to see me escape. */
UPDATE script_texts SET content_loc7='¡Cuidado, los Tótem Siniestro se nos echan encima!' WHERE entry=-1000366; /* content: Look out, the Grimtotem are upon us! */
UPDATE script_texts SET content_loc7='Aquí vienen.' WHERE entry=-1000367; /* content: Here they come. */
UPDATE script_texts SET content_loc7='¡Se acercan más Tótem Siniestro por este camino!' WHERE entry=-1000368; /* content: More Grimtotems are coming this way! */
UPDATE script_texts SET content_loc7='Al fin libre... debo irme, gracias por ayudarme a escapar. Puedo volver al Poblado Viento Libre por mí misma.' WHERE entry=-1000369; /* content: Finally, free at last... I must be going now, thanks for helping me escape. I can get back to Freewind Post by myself. */
UPDATE script_texts SET content_loc7='No te alejes, $n. Voy a necesitar toda la ayuda posible para conseguir salir de aquí. ¡En marcha!' WHERE entry=-1000370; /* content: Stay close, $n. I\'ll need all the help I can get to break out of here. Let\'s go! */
UPDATE script_texts SET content_loc7='Ahora estoy mejor. Volvamos a Trinquete. Venga, $n.' WHERE entry=-1000372; /* content: Now i feel better. Let\'s get back to Ratchet. Come on, $n. */
UPDATE script_texts SET content_loc7='Al parecer, los filibusteros de los Mares del Sur se han atrincherado fuertemente en la costa. La cosa podría ponerse difícil.' WHERE entry=-1000373; /* content: Looks like the Southsea Freebooters are heavily entrenched on the coast. This could get rough. */
UPDATE script_texts SET content_loc7='¡Queda poco para estar de vuelta en Trinquete! Mantengamos el paso...' WHERE entry=-1000378; /* content: Almost back to Ratchet! Let\'s keep up the pace... */
UPDATE script_texts SET content_loc7='Ah, me encanta el aire salobre de Trinquete.' WHERE entry=-1000379; /* content: Ah, the sweet salt air of Ratchet. */
UPDATE script_texts SET content_loc7='¡Capitán Solbrillante, $n me ha liberado! $n, estoy seguro de que el Capitán recompensará tu valentía.' WHERE entry=-1000380; /* content: Captain Brightsun, $N here has freed me! $N, i am certain the Captain will reward your bravery. */
UPDATE script_texts SET content_loc7='¡Cuidado!' WHERE entry=-1000382; /* content: Watch out! */
UPDATE script_texts SET content_loc7='¡Ofensiva naga! ¡A cubierto!' WHERE entry=-1000383; /* content: Naga attackers! Defend yourself! */
UPDATE script_texts SET content_loc7='Los nagas de Cicatriz Espiral son muy crueles con sus prisioneros. Es un milagro que sobrevivieras en esa prisión de agua tanto como yo. Demos gracias a la Madre Tierra.' WHERE entry=-1000385; /* content: The naga of Coilskar are exceptionally cruel to their prisoners. It is a miracle that I survived inside that watery prison for as long as I did. Earthmother be praised. */
UPDATE script_texts SET content_loc7='Ahora debemos encontrar la salida.' WHERE entry=-1000386; /* content: Now we must find the exit. */
UPDATE script_texts SET content_loc7='Lady Vashj deberá responder por esas atrocidades. ¡Hay que llevarla ante la justicia!' WHERE entry=-1000387; /* content: Lady Vashj must answer for these atrocities. She must be brought to justice! */
UPDATE script_texts SET content_loc7='La naturaleza turbulenta de las grandes vías de agua de Azeroth y Draenor son una consecuencia de la irritación de los espíritus del agua.' WHERE entry=-1000388; /* content: The tumultuous nature of the great waterways of Azeroth and Draenor are a direct result of tormented water spirits. */
UPDATE script_texts SET content_loc7='No debería estar muy lejos, $n. La salida está por aquí.' WHERE entry=-1000389; /* content: It shouldn\'t be much further, $n. The exit is just up ahead. */
UPDATE script_texts SET content_loc7='Gracias, $n. Vuelve con los míos al Altar de Condenación, cerca de la Mano de Gul\'dan y diles que Wilda está a salvo. Que la Madre Tierra esté contigo...' WHERE entry=-1000390; /* content: Thank you, $n. Please return to my brethren at the Altar of Damnation, near the Hand of Gul\'dan, and tell them that Wilda is safe. May the Earthmother watch over you... */
UPDATE script_texts SET content_loc7='Tengo sed.' WHERE entry=-1000391; /* content: I\'m Thirsty. */
UPDATE script_texts SET content_loc7='Thorta tiene que estar muy preocupada.' WHERE entry=-1000392; /* content: Torta must be so worried. */
UPDATE script_texts SET content_loc7='Thorta, ¡amor mío! Por fin he vuelto.' WHERE entry=-1000393; /* content: Torta, my love! I have returned at long last. */
UPDATE script_texts SET content_loc7='¿Te haces una idea del tiempo que llevo esperando aquí? ¿Y dónde está la cena? ¿Tanto tiempo fuera y no me tienes nada preparado?' WHERE entry=-1000394; /* content: You have any idea how long I\'ve been waiting here? And where\'s dinner? All that time gone and nothing to show for it? */
UPDATE script_texts SET content_loc7='¡Bueno, ya basta! ¡Estoy hambrienta! Mete ese polvoriento caparazón en el mar y trae a mamá algo rico.' WHERE entry=-1000396; /* content: Enough with the rambling. I am starving! Now, get your dusty shell into that ocean and bring momma some grub. */
UPDATE script_texts SET content_loc7='¡La paz no es más que un sueño fugaz! ¡Que reine la PESADILLA!' WHERE entry=-1000399; /* content: Peace is but a fleeting dream! Let the NIGHTMARE reign! */
UPDATE script_texts SET content_loc7='Hijos de la Locura, ¡yo os libero en este mundo!' WHERE entry=-1000400; /* content: Children of Madness - I release you upon this world! */
UPDATE script_texts SET content_loc7='¡La esperanza es una ENFERMEDAD del alma! ¡Esta tierra se marchitará y morirá!' WHERE entry=-1000401; /* content: Hope is a DISEASE of the soul! This land shall wither and die! */
UPDATE script_texts SET content_loc7='¡Prueben la corrupción de su mundo!' WHERE entry=-1000402; /* content: Taste your world\'s corruption! */
UPDATE script_texts SET content_loc7='¡Rin\'ji es libre!' WHERE entry=-1000403; /* content: Rin\'ji is free! */
UPDATE script_texts SET content_loc7='¡Rin\'ji necesita ayuda!' WHERE entry=-1000405; /* content: Rin\'ji needs help! */
UPDATE script_texts SET content_loc7='¡Están atacando a Rin\'ji!' WHERE entry=-1000406; /* content: Rin\'ji is being attacked! */
UPDATE script_texts SET content_loc7='¡Si lo encuentras, es tuyo! ¡No le digas a nadie que Rin\'ji habló contigo!' WHERE entry=-1000409; /* content: You find it, you keep it! Don\'t tell no one that Rin\'ji talked to you! */
UPDATE script_texts SET content_loc7='Por favor... por favor... Señorita Valiente. No tenía la intención de...' WHERE entry=-1000412; /* content: Please... please... Miss Proudmore. I didn\'t mean to... */
UPDATE script_texts SET content_loc7='Pido disculpas por tardar tanto en llegar hasta aquí. Quería que Lady Valiente también estuviera presente.' WHERE entry=-1000413; /* content: I apologize for taking so long to get here. I wanted Lady Proudmoore to be present also. */
UPDATE script_texts SET content_loc7='Solo podemos quedarnos poco tiempo antes de regresar a la torre. Si queréis hablar más con nosotros, podréis encontrarnos aquí.' WHERE entry=-1000414; /* content: We can only stay a few moments before returning to the tower. If you wish to speak to us more you may find us there. */
UPDATE script_texts SET content_loc7='%s abandona la persecución, ha recibido demasiado daño.' WHERE entry=-1000415; /* content: %s, too injured, gives up the chase. */
UPDATE script_texts SET content_loc7='No sé hasta dónde llegaré en este estado... me siento muy débil...' WHERE entry=-1000416; /* content: Well, I\'m not sure how far I\'ll make it in this state... I\'m feeling kind of faint... */
UPDATE script_texts SET content_loc7='El calor... no puedo soportarlo...' WHERE entry=-1000418; /* content: The heat... I can\'t take it... */
UPDATE script_texts SET content_loc7='Quizás... puedas llevarme.' WHERE entry=-1000419; /* content: Maybe... you could carry me? */
UPDATE script_texts SET content_loc7='Uuuuuuggggghhhhh...' WHERE entry=-1000420; /* content: Uuuuuuggggghhhhh.... */
UPDATE script_texts SET content_loc7='No me siento demasiado bien...' WHERE entry=-1000421; /* content: I\'m not feeling so well... */
UPDATE script_texts SET content_loc7='¿Dónde... dónde estoy?' WHERE entry=-1000422; /* content: Where... Where am I? */
UPDATE script_texts SET content_loc7='Ya me siento un poco mejor, gracias.' WHERE entry=-1000423; /* content: I am feeling a little better now, thank you. */
UPDATE script_texts SET content_loc7='Sí, debo continuar.' WHERE entry=-1000424; /* content: Yes, I must go on. */
UPDATE script_texts SET content_loc7='¿Cómo me siento? Empapado, gracias.' WHERE entry=-1000425; /* content: How am I feeling? Quite soaked, thank you. */
UPDATE script_texts SET content_loc7='¡Spraggle! ¡Nunca creí que volvería!' WHERE entry=-1000426; /* content: Spraggle! I didn\'t think I\'d make it back! */
UPDATE script_texts SET content_loc7='¡Ringo! ¿Estás bien?' WHERE entry=-1000427; /* content: Ringo! You\'re okay! */
UPDATE script_texts SET content_loc7='Oh... me siento débil...' WHERE entry=-1000428; /* content: Oh... I\'m feeling faint... */
UPDATE script_texts SET content_loc7='¡Uf!' WHERE entry=-1000431; /* content: Ugh. */
UPDATE script_texts SET content_loc7='¿Ringo? ¡Despierta! No te preocupes, me ocuparé de ti.' WHERE entry=-1000432; /* content: Ringo? Wake up! Don\'t worry, I\'ll take care of you. */
UPDATE script_texts SET content_loc7='Liladris me está esperando en la Atalaya de Maestra por lo que debemos darnos prisa, $n.' WHERE entry=-1000434; /* content: Liladris has been waiting for me at Maestra\'s Post, so we should make haste, $N. */
UPDATE script_texts SET content_loc7='%s parece tener mucho sueño...' WHERE entry=-1000435; /* content: %s looks very sleepy... */
UPDATE script_texts SET content_loc7='%s comienza a dormirse...' WHERE entry=-1000437; /* content: %s begins to drift off... */
UPDATE script_texts SET content_loc7='Parece el lugar perfecto para echar una siesta...' WHERE entry=-1000438; /* content: This looks like the perfect place for a nap... */
UPDATE script_texts SET content_loc7='Uaaaaaaaaa...' WHERE entry=-1000439; /* content: Yaaaaawwwwwnnnn... */
UPDATE script_texts SET content_loc7='Oh, estoy tan cansado...' WHERE entry=-1000440; /* content: Oh, I am so tired... */
UPDATE script_texts SET content_loc7='¿No te importa que me detenga aquí un segundo, verdad?' WHERE entry=-1000441; /* content: You don\'t mind if I stop here for a moment, do you? */
UPDATE script_texts SET content_loc7='Está tranquilo... demasiado tranquilo...' WHERE entry=-1000443; /* content: It\'s quiet... Too quiet... */
UPDATE script_texts SET content_loc7='Oh, puedo ver a Liladris desde aquí... Dile que estoy aquí, ¿no lo harás?' WHERE entry=-1000444; /* content: Oh, I can see Liladris from here... Tell her I\'m here, won\'t you? */
UPDATE script_texts SET content_loc7='¡%s se despierta!' WHERE entry=-1000445; /* content: %s wakes up! */
UPDATE script_texts SET content_loc7='Bueno... bueno, Memo. Memo bueno. Casa. Encontrar casa.' WHERE entry=-1000447; /* content: Good... good, A-Me. A-Me good. Home. Find home. */
UPDATE script_texts SET content_loc7='He visto fuego en la isla que está allí. También a un humano. Vamos a comprobarlo, $n.' WHERE entry=-1000452; /* content: I noticed some fire on that island over there. A human, too. Let\'s go check it out, $n. */
UPDATE script_texts SET content_loc7='Es Reethe, de acuerdo. Veamos qué es lo que tiene que decir, ¿vale?' WHERE entry=-1000453; /* content: That\'s Reethe alright. Let\'s go see what he has to say, yeah? */
UPDATE script_texts SET content_loc7='¿Q... qué es lo que quieres? ¡Déjame en paz!' WHERE entry=-1000454; /* content: W-what do you want? Just leave me alone... */
UPDATE script_texts SET content_loc7='Solo dinos lo que sabes de la Posada Reposo Umbrío y no te cortaré la cabeza.' WHERE entry=-1000456; /* content: Just tell us what you know about the Shady Rest Inn, and I won\'t bash your skull in. */
UPDATE script_texts SET content_loc7='Yo... bueno, quizás haya cogido una cosita o dos de la posada... ¿pero qué le puede importar a un ogro?' WHERE entry=-1000457; /* content: I... Well, I may of taken a little thing or two from the inn... but what would an ogre care about that? */
UPDATE script_texts SET content_loc7='Mira, si no me cuentas lo del fuego...' WHERE entry=-1000458; /* content: Look here, if you don\'t tell me about the fire-- */
UPDATE script_texts SET content_loc7='¡No des un paso más, ogro!' WHERE entry=-1000459; /* content: Not one step closer, ogre! */
UPDATE script_texts SET content_loc7='Si yo no sé nada sobre ese fuego del que hablas...' WHERE entry=-1000460; /* content: And I don\'t know anything about this fire of yours... */
UPDATE script_texts SET content_loc7='¿Qué ha sido eso? ¿Has oído algo?' WHERE entry=-1000461; /* content: What was that? Did you hear something? */
UPDATE script_texts SET content_loc7='¡Paval Reethe! Por fin te he encontrado. ¿Y ahora tratas con los ogros? No temas, incluso los desertores y traidores merecen algo de clemencia.' WHERE entry=-1000462; /* content: Paval Reethe! Found you at last. And consorting with ogres now? No fear, even deserters and traitors are afforded some mercy. */
UPDATE script_texts SET content_loc7='Soldado, muestre un poco de misericordia hacia el teniente Reethe.' WHERE entry=-1000463; /* content: Private, show Lieutenant Reethe some mercy. */
UPDATE script_texts SET content_loc7='Será un placer, señor.' WHERE entry=-1000464; /* content: Gladly, sir. */
UPDATE script_texts SET content_loc7='%s se tambalea hacia atrás mientras la flecha se hunde profundamente de su pecho.' WHERE entry=-1000465; /* content: %s staggers backwards as the arrow lodges itself deeply in his chest. */
UPDATE script_texts SET content_loc7='Agh... Hallan, no pensé que lo llevabas dentro...' WHERE entry=-1000466; /* content: Ugh... Hallan, didn\'t think you had it in you... */
UPDATE script_texts SET content_loc7='¡Ahora limpiemos el resto de la basura!' WHERE entry=-1000467; /* content: Now, let\'s clean up the rest of the trash, men! */
UPDATE script_texts SET content_loc7='¡Maldita sea! ¡Será mejor que no mueras aquí, humano!' WHERE entry=-1000468; /* content: Damn it! You\'d better not die on me, human! */
UPDATE script_texts SET content_loc7='¿Sigues aquí, Reethe?' WHERE entry=-1000469; /* content: Still with us, Reethe? */
UPDATE script_texts SET content_loc7='Debe ser tu día de suerte. Muy bien, hablaré. Pero déjame solo. Mira, no vas a creerme, pero era... oh, Luz, es como si la niña pudiera disparar...' WHERE entry=-1000470; /* content: Must be your lucky day. Alright, I\'ll talk. Just leave me alone. Look, you\'re not going to believe me, but it wa... oh, Light, looks like the girl could shoot... */
UPDATE script_texts SET content_loc7='Por cierto, gracias por vigilar mi retaguardia.' WHERE entry=-1000471; /* content: By the way, thanks for watching my back. */
UPDATE script_texts SET content_loc7='1...' WHERE entry=-1000472; /* content: 1... */
UPDATE script_texts SET content_loc7='2...' WHERE entry=-1000473; /* content: 2... */
UPDATE script_texts SET content_loc7='3...' WHERE entry=-1000474; /* content: 3... */
UPDATE script_texts SET content_loc7='4...' WHERE entry=-1000475; /* content: 4... */
UPDATE script_texts SET content_loc7='5...' WHERE entry=-1000476; /* content: 5... */
UPDATE script_texts SET content_loc7='¡No irás a ninguna parte, $gestúpido:estúpida;!' WHERE entry=-1000478; /* content: You\'ll go nowhere, fool! */
UPDATE script_texts SET content_loc7='¡No escaparás de nosotros tan fácilmente!' WHERE entry=-1000480; /* content: You will not escape us so easily! */
UPDATE script_texts SET content_loc7='¡Cuidado!' WHERE entry=-1000482; /* content: Look out! */
UPDATE script_texts SET content_loc7='¡No les dejéis escapar! ¡Matad primero al fuerte!' WHERE entry=-1000483; /* content: Don\'t let them escape! Kill the strong one first! */
UPDATE script_texts SET content_loc7='¡Vienen más de ellos! ¡Cuidados!' WHERE entry=-1000484; /* content: More of them coming! Watch out! */
UPDATE script_texts SET content_loc7='¿Dónde creéis que vais? ¡Matadlos a todos!' WHERE entry=-1000485; /* content: Where do you think you\'re going? Kill them all! */
UPDATE script_texts SET content_loc7='¡Toma descarga, escoria!' WHERE entry=-1000486; /* content: Ride the lightning, filth! */
UPDATE script_texts SET content_loc7='Ten cuidado con lo que dices, se lo está enseñando todo la mascota de Khadgar.' WHERE entry=-1000491; /* content: Be mindful of what you say, this one\'s being shown around by Khadgar\'s pet. */
UPDATE script_texts SET content_loc7='%s levanta la cabeza, como si oyera algo.' WHERE entry=-1000496; /* content: %s lifts its head into the air, as if listening for something. */
UPDATE script_texts SET content_loc7='%s deja escapar un aullido que atraviesa las montañas al norte y te hace seguirlo.' WHERE entry=-1000497; /* content: %s lets out a howl that rings across the mountains to the north and motions for you to follow. */
UPDATE script_texts SET content_loc7='¡Estoy asustado! ¡Echaré a correr!' WHERE entry=-1000500; /* content: Me scared! Me run now! */
UPDATE script_texts SET content_loc7='¿Seguro que estás $gpreparado:preparada;? Si no tenemos un grupo de tus aliados que nos ayude, perderemos.' WHERE entry=-1000501; /* content: Are you sure that you are ready? If we do not have a group of your allies to aid us, we will surely fail. */
UPDATE script_texts SET content_loc7='Este es el brasero, $n. Apágalo. Vorsha es una bestia, ¡no se merece que nadie le alabe!' WHERE entry=-1000503; /* content: This is the brazier, $n. Put it out. Vorsha is a beast, worthy of praise from no one! */
UPDATE script_texts SET content_loc7='Ahora debemos esperar. Los nagas no tardarán en ver lo que hemos hecho.' WHERE entry=-1000504; /* content: Now we must wait. It won\'t be long before the naga realize what we have done. */
UPDATE script_texts SET content_loc7='¡En guardia, $n!' WHERE entry=-1000505; /* content: Be on your guard, $n! */
UPDATE script_texts SET content_loc7='Quizás podamos descansar un momento. Pero no me rendiré hasta que nos hayamos enfrentado a Vorsha.' WHERE entry=-1000506; /* content: Perhaps we will get a moment to rest. But i will not give up until we have faced off against Vorsha! */
UPDATE script_texts SET content_loc7='¡Lo hemos conseguido!' WHERE entry=-1000507; /* content: We have done it! */
UPDATE script_texts SET content_loc7='Te lo agradezco de todo corazón. Gracias.' WHERE entry=-1000508; /* content: You have my deepest gratitude. I thank you. */
UPDATE script_texts SET content_loc7='Voy a seguir patrullando la zona un rato más para asegurarme de que todo está bien.' WHERE entry=-1000509; /* content: I am going to patrol the area for a while longer and ensure that things are truly safe. */
UPDATE script_texts SET content_loc7='Por favor, vete a Zoram\'gar e informa sobre nuestro éxito al corredor Grito de Guerra.' WHERE entry=-1000510; /* content: Please return to Zoram\'gar and report our success to the Warsong runner. */
UPDATE script_texts SET content_loc7='¡De acuerdo, escuchad! ¡Haced un círculo a mi alrededor y marchaos!' WHERE entry=-1000511; /* content: Aright, listen up! Form a circle around me and move out! */
UPDATE script_texts SET content_loc7='De acuerdo, $r, cuídate del peligro mientras trabajamos. Te pagaremos cuando volvamos.' WHERE entry=-1000512; /* content: Aright, $r, just keep us safe from harm while we work. We\'ll pay you when we return. */
UPDATE script_texts SET content_loc7='La excavación está al norte de aquí.' WHERE entry=-1000513; /* content: The dig site is just north of here. */
UPDATE script_texts SET content_loc7='¡Estamos aquí! ¡Comienza a cavar!' WHERE entry=-1000514; /* content: We\'re here! Start diggin\'! */
UPDATE script_texts SET content_loc7='¡Creo que hay algo enterrado aquí, bajo la arena!' WHERE entry=-1000515; /* content: I think there\'s somethin\' buried here, beneath the sand! */
UPDATE script_texts SET content_loc7='¡Casi lo tengo!' WHERE entry=-1000516; /* content: Almost got it! */
UPDATE script_texts SET content_loc7='¡Por los platanitos de Brann! ¡¿Qué es eso!? Parece... parece un tambor.' WHERE entry=-1000517; /* content: By brann\'s brittle bananas! What is it!? It... It looks like a drum. */
UPDATE script_texts SET content_loc7='Guau... un tambor.' WHERE entry=-1000518; /* content: Wow... a drum. */
UPDATE script_texts SET content_loc7='Este descubrimiento sin duda sacudirá los cimientos de la arqueología moderna.' WHERE entry=-1000519; /* content: This discovery will surely rock the foundation of modern archaeology. */
UPDATE script_texts SET content_loc7='Sí, genial. ¿Podemos irnos ya? Este desierto me da urticaria.' WHERE entry=-1000520; /* content: Yea, great. Can we leave now? This desert is giving me hives. */
UPDATE script_texts SET content_loc7='¿Has perdido el juicio? ¿Esperas que deje atrás un tambor sin tocarlo primero? ¡No este hijo de Forjaz! ¡No, señor!' WHERE entry=-1000521; /* content: Have ye gone mad? You expect me to leave behind a drum without first beatin\' on it? Not this son of Ironforge! No sir! */
UPDATE script_texts SET content_loc7='Eso me recuerda la vez que nos hiciste registrar a Silithus para encontrar pruebas de gnomos de arena.' WHERE entry=-1000522; /* content: This reminds me of that one time where you made us search Silithus for evidence of sand gnomes. */
UPDATE script_texts SET content_loc7='O aquella vez que nos dijiste que habías descubierto la cura para la peste del siglo veinte. ¿Qué es eso? ¿El siglo veinte?' WHERE entry=-1000523; /* content: Or that time when you told us that you\'d discovered the cure for the plague of the 20th century. What is that even? 20th century? */
UPDATE script_texts SET content_loc7='No sé. No es posible que supere la vez que intentó convencernos de que en realidad todos somos producto de la imaginación de algún ser y que solo nos usa para diversión personal. Eso duró toda la comida con la familia.' WHERE entry=-1000525; /* content: I dunno. It can\'t possibly beat the time he tried to convince us that we\'re all actually a figment of some being\'s imagination and that they only use us for their own personal amusement. That went over well during dinner with the family. */
UPDATE script_texts SET content_loc7='¡Cerrad el pico! ¡Voy a golpear este tambor y se acabó!' WHERE entry=-1000526; /* content: Shut yer yaps! I\'m gonna bang on this drum and that\'s that! */
UPDATE script_texts SET content_loc7='Decid, ¿oís eso, muchachos?' WHERE entry=-1000527; /* content: Say, do you guys hear that? */
UPDATE script_texts SET content_loc7='¡EN TU CARA! ¡Te dije que aquí había algo!' WHERE entry=-1000528; /* content: IN YOUR FACE! I told you there was somethin\' here! */
UPDATE script_texts SET content_loc7='%s coge el tambor.' WHERE entry=-1000530; /* content: %s picks up the drum. */
UPDATE script_texts SET content_loc7='¡Has sido de gran ayuda, $r! ¡Vayámonos de aquí antes de que aparezcan más cosas de estas! Haré saber a Enaniski que hiciste la tarea que se te encomendó de forma admirable.' WHERE entry=-1000531; /* content: You\'ve been a tremendous help, $r! Let\'s get out of here before more of those things show up! I\'ll let Dwarfowitz know you did the job asked of ya\' admirably. */
UPDATE script_texts SET content_loc7='Como desees, Lord Illidan.' WHERE entry=-1000533; /* content: As you desire, Lord Illidan. */
UPDATE script_texts SET content_loc7='¿Qué clase de persona estúpida osa alzarse ante Illidan Tempestira? ¡Soldados, destruid a estos insectos!' WHERE entry=-1000536; /* content: What manner of fool dares stand before Illidan Stormrage? Soldiers, destroy these insects! */
UPDATE script_texts SET content_loc7='Corre mientras puedas. Los altos señores vienen pronto...' WHERE entry=-1000538; /* content: Run while you still can. The highlords come soon... */
UPDATE script_texts SET content_loc7='Así que has derrotado al Sigilo Carmesí. ¿Y ahora quieres desafiar mi dominio? Ni siquiera Arthas pudo derrotarme y, aun así, ¿osas siquiera albergar tales pensamientos? Entonces, ¡te digo que vengas! ¡Ven, $n! El Templo Oscuro aguarda...' WHERE entry=-1000540; /* content: So you have defeated the Crimson Sigil. You now seek to challenge my rule? Not even Arthas could defeat me, yet you dare to even harbor such thoughts? Then I say to you, come! Come $N! The Black Temple awaits... */
UPDATE script_texts SET content_loc7='¿Por qué has venido aquí? ¡Solo encontrarás dolor! Nuestro destino será el tuyo...' WHERE entry=-1000543; /* content: Why have you come here, outsider? You will only find pain! Our fate will be yours... */
UPDATE script_texts SET content_loc7='La oscuridad... la corrupción... vinieron demasiado rápido sin que nadie se diera cuenta...' WHERE entry=-1000545; /* content: The darkness... the corruption... they came too quickly for anyone to know... */
UPDATE script_texts SET content_loc7='La oscuridad consumirá a todos... todos los vivos...' WHERE entry=-1000546; /* content: The darkness will consume all... all the living... */
UPDATE script_texts SET content_loc7='Es demasiado tarde para nosotros, viviente. Coge a tu acompañante y marcharos de aquí antes de que seáis... reclamados...' WHERE entry=-1000547; /* content: It is too late for us, living one. Take yourself and your friend away from here before you both are... claimed... */
UPDATE script_texts SET content_loc7='Es demasiado tarde para Jarl... su dominio es demasiado fuerte...' WHERE entry=-1000548; /* content: It is too late for Jarl... its hold is too strong... */
UPDATE script_texts SET content_loc7='¡Vete... seas quién seas! La Colina de las Brujas es mía... ¡mía!' WHERE entry=-1000549; /* content: Go away, whoever you are! Witch Hill is mine... mine! */
UPDATE script_texts SET content_loc7='La casa... alguien más... pronto se consumirá' WHERE entry=-1000550; /* content: The manor... someone else... will soon be consumed... */
UPDATE script_texts SET content_loc7='Prepárate, $n. Oigo al consejo que regresa. ¡Listos para tender la emboscada!' WHERE entry=-1000553; /* content: Be ready, $N. I hear the council returning. Prepare to ambush! */
UPDATE script_texts SET content_loc7='Bien hecho. ¡Es sin duda un golpe para Arugal!' WHERE entry=-1000554; /* content: Well done. A blow to Arugal no doubt! */
UPDATE script_texts SET content_loc7='¡Gracias por rescatarme, valiente! Doy por seguro que el alguacil Marris sabrá recompensar tu noble acto.' WHERE entry=-1000565; /* content: Brave adventurer, thank you for rescuing me! I am sure Marshal Marris will reward your kind deed. */
UPDATE script_texts SET content_loc7='¡Atrás! ¡Atrás! Tenemos que dejarle mucho espacio al infernal.' WHERE entry=-1000566; /* content: Stand back! Stand clear! The infernal will need to be given a wide berth! */
UPDATE script_texts SET content_loc7='¡INCLINAOS ANTE EL TODOPODEROSO! INCLINAOS ANTE MI INFERNAL DESTRUC... ¿Gallina?' WHERE entry=-1000567; /* content: BOW DOWN TO THE ALMIGHTY! BOW DOWN BEFORE MY INFERNAL DESTRO... chicken? */
UPDATE script_texts SET content_loc7='%s rueda por el suelo riéndose sin parar.' WHERE entry=-1000568; /* content: %s rolls on the floor laughing. */
UPDATE script_texts SET content_loc7='¡Silencio, siervo! ¡La venganza será mía! ¡Muerte a Ventormenta! ¡Muerte por sobredosis de gallina!' WHERE entry=-1000570; /* content: Silence, servant! Vengeance will be mine! Death to Stormwind! Death by chicken! */
UPDATE script_texts SET content_loc7='¡Socorro! Solo tengo una mano para defenderme.' WHERE entry=-1000571; /* content: Help! I\'ve only one hand to defend myself with. */
UPDATE script_texts SET content_loc7='¡Siente el poder de los enanos Hierro Negro!' WHERE entry=-1000572; /* content: Feel the power of the Dark Iron Dwarves! */
UPDATE script_texts SET content_loc7='¡Traédmelos! ¡No me asustan esas bestias esqueléticas!' WHERE entry=-1000573; /* content: Send them on! I\'m not afraid of some scrawny beasts! */
UPDATE script_texts SET content_loc7='¡Ah, al fin hemos llegado! Qué alivio poder librarnos de todos esos cañones.' WHERE entry=-1000574; /* content: Ah, here at last! It\'s going to feel so good to get rid of these barrels. */
UPDATE script_texts SET content_loc7='REUSE ME' WHERE entry=-1000579; /* content: REUSE ME */
UPDATE script_texts SET content_loc7='Vámonos de aquí.' WHERE entry=-1000583; /* content: Let us leave this place. */
UPDATE script_texts SET content_loc7='¡Socorro! ¡Me están atacando!' WHERE entry=-1000585; /* content: Help! I\'m under attack! */
UPDATE script_texts SET content_loc7='%s susurra a $n el secreto para abrir su caja fuerte.' WHERE entry=-1000587; /* content: %s whispers to $N the secret to opening his strongbox. */
UPDATE script_texts SET content_loc7='%s desaparece en el pantano.' WHERE entry=-1000588; /* content: %s disappears into the swamp. */
UPDATE script_texts SET content_loc7='¿Kroshius vivo? ¡Kroshius aplastar!' WHERE entry=-1000589; /* content: Kroshius live? Kroshius crush! */
UPDATE script_texts SET content_loc7='¡Guay!' WHERE entry=-1000590; /* content: Woot! */
UPDATE script_texts SET content_loc7='¡Qué asco!' WHERE entry=-1000593; /* content: Gross! */
UPDATE script_texts SET content_loc7='Nosotros entregar cositas brillantes a santuario... nosotros no egoístas... ¿por qué pasar esto?' WHERE entry=-1000616; /* content: We gave shinies to shrine... we not greedy... why this happen? */
UPDATE script_texts SET content_loc7='¿Yo hacer algo mal? Sentirlo...' WHERE entry=-1000617; /* content: I do something bad? I sorry.... */
UPDATE script_texts SET content_loc7='Nosotros no hacerles nada... a ellos... Yo no entender.' WHERE entry=-1000618; /* content: We not do anything... to them... I no understand. */
UPDATE script_texts SET content_loc7='Por favor coge... mis cositas brillantes. Todo acabado...' WHERE entry=-1000620; /* content: Please take... my shinies. All done... */
UPDATE script_texts SET content_loc7='Todos los sistemas en línea. Prepárate, nos marchamos en breve.' WHERE entry=-1000621; /* content: All systems on-line.  Prepare yourself, we leave shortly. */
UPDATE script_texts SET content_loc7='Ten cuidado y vuelve de una pieza.' WHERE entry=-1000622; /* content: Be careful in there and come back in one piece! */
UPDATE script_texts SET content_loc7='Procede.' WHERE entry=-1000623; /* content: Proceed. */
UPDATE script_texts SET content_loc7='¡Has vuelto! ¿Has conseguido todas las máquinas?' WHERE entry=-1000624; /* content: You\'re back!  Were you able to get all of the machines? */
UPDATE script_texts SET content_loc7='%s recoge la sangre del persecutor de distorsión.' WHERE entry=-1000625; /* content: %s gathers the warp chaser\'s blood. */
UPDATE script_texts SET content_loc7='%s se alimenta del persecutor de distorsión recién matado.' WHERE entry=-1000628; /* content: %s feeds on the freshly-killed warp chaser. */
UPDATE script_texts SET content_loc7='Ejem, ejem... ¡Hay una doncella en peligro aquí!' WHERE entry=-1000629; /* content: <cough> <cough> Damsel in distress over here! */
UPDATE script_texts SET content_loc7='Vale, ¡larguémonos de aquí!' WHERE entry=-1000632; /* content: Ok, let\'s get out of here! */
UPDATE script_texts SET content_loc7='¡Vamos allá!' WHERE entry=-1000634; /* content: Alright, let\'s do this! */
UPDATE script_texts SET content_loc7='¡Ay! ¡Ya basta! ¡Dejo el negocio de los objetivos!' WHERE entry=-1000644; /* content: Ouch! That\'s it, I quit the target business! */
UPDATE script_texts SET content_loc7='¡Mi oreja! ¡Me has arañado la oreja!' WHERE entry=-1000645; /* content: My ear! You grazed my ear! */
UPDATE script_texts SET content_loc7='¡El bigote no! ¡Ahora soy asimétrico!' WHERE entry=-1000646; /* content: Not the \'stache! Now I\'m asymmetrical! */
UPDATE script_texts SET content_loc7='¡Buen disparo!' WHERE entry=-1000647; /* content: Good shot! */
UPDATE script_texts SET content_loc7='Deja de quejarte. Aún conservas tu suerte.' WHERE entry=-1000648; /* content: Stop whining. You\'ve still got your luck. */
UPDATE script_texts SET content_loc7='Bah, es una mejora.' WHERE entry=-1000649; /* content: Bah, it\'s an improvement. */
UPDATE script_texts SET content_loc7='¡Yo mucha hambre! ¡YUM!' WHERE entry=-1000652; /* content: Me so hungry! YUM! */
UPDATE script_texts SET content_loc7='Hey...yo no sentirme demasiado bien...' WHERE entry=-1000653; /* content: Hey... me not feel so good. */
UPDATE script_texts SET content_loc7='¡Tú eres orco malo... maaalo... o... argh!' WHERE entry=-1000654; /* content: You is bad orc... baaad... or... argh! */
UPDATE script_texts SET content_loc7='¡¿Hora de comer?!' WHERE entry=-1000655; /* content: Time for eating!? */
UPDATE script_texts SET content_loc7='¡Yo meter oveja en estómago!' WHERE entry=-1000656; /* content: It put the mutton in the stomach! */
UPDATE script_texts SET content_loc7='Puedo sentir la SOMBRA en sus corazones. ¡No puede haber descanso para los malos!' WHERE entry=-1000666; /* content: I can sense the SHADOW on your hearts. There can be no rest for the wicked! */
UPDATE script_texts SET content_loc7='¡Sus malvadas almas alimentan mi poder!' WHERE entry=-1000667; /* content: Your wicked souls shall feed my power! */
UPDATE script_texts SET content_loc7='%s libera lo que queda de sus fuerzas en la piedra rúnica cercana, y consigue reactivarla.' WHERE entry=-1000668; /* content: %s releases the last of its energies into the nearby runestone, successfully reactivating it. */
UPDATE script_texts SET content_loc7='¡Demonio! ¡Afronta el poder de Cenarius!' WHERE entry=-1000676; /* content: Fiend! Face the might of Cenarius! */
UPDATE script_texts SET content_loc7='%s suelta una carcajada siniestra.' WHERE entry=-1000677; /* content: %s lets loose a sinister laugh. */
UPDATE script_texts SET content_loc7='¿Mi redención? Eres muy $gosado:osada;, $gpequeñajo:pequeñaja;. Mi redención llegará cuando mi dios así lo desee.' WHERE entry=-1000680; /* content: My redemption? You are bold, little one. My redemption comes by the will of my god. */
UPDATE script_texts SET content_loc7='%s ruge con furia.' WHERE entry=-1000681; /* content: %s roars furiously. */
UPDATE script_texts SET content_loc7='¡Date prisa, $n! ¡Debemos ponernos a cubierto!' WHERE entry=-1000682; /* content: Hurry, $N! We must find protective cover! */
UPDATE script_texts SET content_loc7='Por favor, valiente, protege a nuestra gente.' WHERE entry=-1000683; /* content: Please, champion, protect our people. */
UPDATE script_texts SET content_loc7='¡Erguíos, sirvientes de la Pesadilla! ¡Erguíos y destruid este mundo!' WHERE entry=-1000684; /* content: Rise, servants of the Nightmare! Rise and destroy this world! Let there be no survivors... */
UPDATE script_texts SET content_loc7='¿Derrotasteis a mis esbirros? ¡Pues ahora enfrentaos a mí, mortales!' WHERE entry=-1000687; /* content: Defeated my minions? Then face me, mortals! */
UPDATE script_texts SET content_loc7='Remulos, ¿ves cómo caen ante mí? ¡No puedes hacer nada, idiota! Entrégame al druida y todo habrá acabado...' WHERE entry=-1000688; /* content: Remulos, look how easy they fall before me? You can stop this, fool. Turn the druid over to me and it will all be over... */
UPDATE script_texts SET content_loc7='Elune, escucha mis plegarias. ¡Concédenos sosiego! Vela por nuestros caídos...' WHERE entry=-1000689; /* content: Elune, hear my prayers. Grant us serenity! Watch over our fallen... */
UPDATE script_texts SET content_loc7='¡Ocupaos de los daños afligidos a los heridos, hermanas!' WHERE entry=-1000690; /* content: Tend to the injuries of the wounded, sisters! */
UPDATE script_texts SET content_loc7='Serás perdonado, Eranikus. Elune siempre te amará. ¡Rompe las cadenas que te dominan!' WHERE entry=-1000692; /* content: You will be forgiven, Eranikus. Elune will always love you. Break free of the bonds that command you! */
UPDATE script_texts SET content_loc7='Yo... siento... siento la influencia de Elune sobre mí una vez más... Ella me sonríe... Sí... Yo...' WHERE entry=-1000696; /* content: I... I feel... I feel the touch of Elune upon my being once more... She smiles upon me... Yes... I... */
UPDATE script_texts SET content_loc7='%s hinca una rodilla.' WHERE entry=-1000698; /* content: %s falls to one knee. */
UPDATE script_texts SET content_loc7='Alabada sea Elune... Eranikus ha sido redimido.' WHERE entry=-1000699; /* content: Praise be to Elune... Eranikus is redeemed. */
UPDATE script_texts SET content_loc7='Pero... Malfurion, Cenarius, Ysera... continúan luchando. Me necesitan y por eso he de volver al Sueño de inmediato.' WHERE entry=-1000701; /* content: But... Malfurion, Cenarius, Ysera... They still fight. They need me. I must return to the Dream at once. */
UPDATE script_texts SET content_loc7='Y valientes... Yo tengo lo que buscáis. Que el mal se desvanezca una vez más. Remulos, ocúpate de que nuestro valiente reciba el fragmento del Vuelo Verde.' WHERE entry=-1000703; /* content: And heroes... I hold that which you seek. May it once more see the evil dissolved. Remulos, see to it that our champion receives the shard of the Green Flight. */
UPDATE script_texts SET content_loc7='Todo irá bien, Eranikus. No te preocupes, anciano.' WHERE entry=-1000704; /* content: It will be done, Eranikus. Be well, ancient one. */
UPDATE script_texts SET content_loc7='Tu mundo llegará a su fin. ¡La Pesadilla viene a por ti!' WHERE entry=-1000706; /* content: Your world shall suffer an unmerciful end. The Nightmare comes for you! */
UPDATE script_texts SET content_loc7='Esta luz azul... es extraña. ¿Qué crees que significa?' WHERE entry=-1000707; /* content: This blue light... It\'s strange. What do you think it means? */
UPDATE script_texts SET content_loc7='¡Lo hemos encontrado!' WHERE entry=-1000708; /* content: We\'ve found it! */
UPDATE script_texts SET content_loc7='Aquí es. Vamos a iluminarlo.' WHERE entry=-1000710; /* content: This is the place. Let\'s light it. */
UPDATE script_texts SET content_loc7='Vamos a buscar el siguiente.' WHERE entry=-1000711; /* content: Let\'s find the next one. */
UPDATE script_texts SET content_loc7='Debemos continuar.' WHERE entry=-1000712; /* content: We must continue on now. */
UPDATE script_texts SET content_loc7='Ha llegado la hora del sueño final; debemos activar el altar.' WHERE entry=-1000713; /* content: It is time for the final step; we must activate the altar. */
UPDATE script_texts SET content_loc7='Leeré las palabras talladas en la piedra, encuentra una forma de iluminarla.' WHERE entry=-1000714; /* content: I will read the words carved into the stone, and you must find a way to light it. */
UPDATE script_texts SET content_loc7='¡El altar está resplandeciendo! ¡Lo hemos conseguido!' WHERE entry=-1000715; /* content: The altar is glowing! We have done it! */
UPDATE script_texts SET content_loc7='¿Qué ocurre? ¡Mira!' WHERE entry=-1000716; /* content: What is happening? Look! */
UPDATE script_texts SET content_loc7='Han sido muchos años...' WHERE entry=-1000717; /* content: It has been many years... */
UPDATE script_texts SET content_loc7='¿Quién ha perturbado el altar de la diosa?' WHERE entry=-1000718; /* content: Who has disturbed the altar of the goddess? */
UPDATE script_texts SET content_loc7='Por favor, sacerdotisa, perdona nuestra intromisión. No queremos causar ningún problema.' WHERE entry=-1000719; /* content: Please, priestesses, forgive us for our intrusion. We do not wish any harm here. */
UPDATE script_texts SET content_loc7='Solo queremos saber por qué los lechúcicos salvajes guardan esta zona...' WHERE entry=-1000720; /* content: We only wish to know why the wildkin guard this area... */
UPDATE script_texts SET content_loc7='Antaño esta gema permitía comunicarse directamente con la propia Elune.' WHERE entry=-1000724; /* content: This gem once allowed direct communication with Elune, herself. */
UPDATE script_texts SET content_loc7='A través de la gema, Elune canalizaba su sabiduría infinita...' WHERE entry=-1000725; /* content: Through the gem, Elune channeled her infinite wisdom... */
UPDATE script_texts SET content_loc7='Al comprender que la gema debía ser protegida, recurrimos a la propia diosa.' WHERE entry=-1000726; /* content: Realizing that the gem needed to be protected, we turned to the goddess herself. */
UPDATE script_texts SET content_loc7='Poco después comenzamos a tener visiones de una criatura... Una criatura con plumas de búho, pero con la voluntad y la fuerza del oso...' WHERE entry=-1000727; /* content: Soon after, we began to have visions of a creature... A creature with the feathers of an owl, but the will and might of a bear... */
UPDATE script_texts SET content_loc7='Los lechúcicos salvajes son mágicos intrínsecamente; un poder que les fue otorgado por la diosa.' WHERE entry=-1000732; /* content: Wildkin are inherently magical; this power was bestowed upon them by the goddess. */
UPDATE script_texts SET content_loc7='Tienes que saber que donde quiera que combatas con ellos, estarán protegiendo algo importante, como se les encargó hace tantísimo tiempo.' WHERE entry=-1000733; /* content: Know that wherever you might find them in the world, they are protecting something of importance, as they were entrusted to do so long ago. */
UPDATE script_texts SET content_loc7='Por favor, recuerda lo que te he enseñado...' WHERE entry=-1000734; /* content: Please, remember what we have shown you... */
UPDATE script_texts SET content_loc7='Adiós.' WHERE entry=-1000735; /* content: Farewell. */
UPDATE script_texts SET content_loc7='Quiero quedarme aquí y meditar sobre lo que he visto. Por favor, ve a ver a Eralas y dile lo que hemos aprendido.' WHERE entry=-1000737; /* content: I want to stay here and reflect on what we have seen. Please see Erelas and tell him what we have learned. */
UPDATE script_texts SET content_loc7='%s comienza a lanzar un extraño hechizo...' WHERE entry=-1000738; /* content: %s begins chanting a strange spell... */
UPDATE script_texts SET content_loc7='Hay un modo...' WHERE entry=-1000742; /* content: There is a way... */
UPDATE script_texts SET content_loc7='%s hace un gesto de complicidad.' WHERE entry=-1000743; /* content: %s nods knowingly. */
UPDATE script_texts SET content_loc7='%s mira a sus compatriotas.' WHERE entry=-1000745; /* content: %s glances at her compatriots. */
UPDATE script_texts SET content_loc7='Manténganse cerca.' WHERE entry=-1000754; /* content: Stay close. */
UPDATE script_texts SET content_loc7='Las tierras del tiempo se detendrán, ¡pero solo por un instante! Haré aparecer la barrera.' WHERE entry=-1000755; /* content: The sands of time will halt, but only for a moment! I will now conjure the barrier. */
UPDATE script_texts SET content_loc7='¡CONCLUYE EL HECHIZO, CORZOCELADA! ¡NO PUEDO CONTENER A LOS GLIFOS QUE PROTEGEN EL LUGAR POR MUCHO MÁS TIEMPO! ¡INVOCA A LAS RAÍCES!' WHERE entry=-1000756; /* content: FINISH THE SPELL, STAGHELM! I CANNOT HOLD THE GLYPHS OF WARDING IN PLACE MUCH LONGER! CALL FORTH THE ROOTS! */
UPDATE script_texts SET content_loc7='Ancestros, guíen mi mano... ¡Despierten de su sueño! ¡DESPIERTEN Y SELLEN ESTE LUGAR MALDITO!' WHERE entry=-1000757; /* content: Ancient ones guide my hand... Wake from your slumber! WAKE AND SEAL THIS CURSED PLACE! */
UPDATE script_texts SET content_loc7='%s hinca exhausto una rodilla.' WHERE entry=-1000758; /* content: %s falls to one knee - exhausted. */
UPDATE script_texts SET content_loc7='Se... Se ha acabado, Lord Corzocelada. ¡Hemos vencido! Si bien el precio que hemos pagado por esta victoria ha sido alto.' WHERE entry=-1000759; /* content: It... It is over, Lord Staghelm. We are victorious. Albeit the cost for this victory was great. */
UPDATE script_texts SET content_loc7='Tras la violencia que mi pueblo ha presenciado y de la cual ha sido víctima, ¿esperas que acepte otra carga, dragón? Sin duda, estás loco.' WHERE entry=-1000763; /* content: After the savagery that my people have witnessed and felt, you expect me to accept another burden, dragon? Surely you are mad. */
UPDATE script_texts SET content_loc7='%s arroja el cetro del Mar de Dunas contra la barrera, haciéndolo añicos.' WHERE entry=-1000765; /* content: %s hurls the Scepter of the Shifting Sands into the barrier, shattering it. */
UPDATE script_texts SET content_loc7='%s sacude la cabeza en un gesto de decepción.' WHERE entry=-1000768; /* content: %s shakes his head in disappointment. */
UPDATE script_texts SET content_loc7='Al parecer, tenemos problemas. ¡Anímate, aquí vienen!' WHERE entry=-1000772; /* content: It looks like we\'re in trouble. Look lively, here they come! */
UPDATE script_texts SET content_loc7='Asesinos de esa secta que habéis encontrado... Vámonos antes de que otro nos encuentre aquí.' WHERE entry=-1000773; /* content: Assassins from that cult you found... Let\'s get moving before someone else finds us out here. */
UPDATE script_texts SET content_loc7='¡Espera! Presiento una presencia maligna... ¡No-muertos!' WHERE entry=-1000774; /* content: Hold! I sense an evil presence... Undead! */
UPDATE script_texts SET content_loc7='Están reapareciendo tras un largo periodo de inactividad. Vamos a seguir adelante o daremos con más seres que quieren verme muerto.' WHERE entry=-1000776; /* content: They\'re coming out of the woodwork today. Let\'s keep moving or we may find more things that want me dead. */
UPDATE script_texts SET content_loc7='¿Otra vez estos tres?' WHERE entry=-1000777; /* content: These three again? */
UPDATE script_texts SET content_loc7='¿No estás tan seguro de ti mismo sin el Purificador, eh?' WHERE entry=-1000778; /* content: Not quite so sure of yourself without the Purifier, hm? */
UPDATE script_texts SET content_loc7='¡Esta vez acabaré contigo de una vez por todas!' WHERE entry=-1000779; /* content: I\'ll finish you off for good this time! */
UPDATE script_texts SET content_loc7='¡Bien hecho! A partir de ahora podré arreglármelas solo. No olvidéis hablar con Delgren cuando regreséis a la Atalaya de Maestra en Vallefresno.' WHERE entry=-1000780; /* content: Well done! I should be fine on my own from here. Remember to talk to Delgren when you return to Maestra\'s Post in Ashenvale. */
UPDATE script_texts SET content_loc7='Lo logramos, $gamigo mío:amiga mía;. No olvides buscar a Terenthis y hacerle saber que estamos a salvo. Gracias de nuevo.' WHERE entry=-1000790; /* content: We made it, My friend. Remember to find Terenthis and let him know we\'re safe. Thank you again. */
UPDATE script_texts SET content_loc7='Aquí vienen.' WHERE entry=-1000791; /* content: Here they come. */
UPDATE script_texts SET content_loc7='¡Al menos nos ganaremos el derecho a morir!' WHERE entry=-1000793; /* content: We shall earn our deaths at the very least! */
UPDATE script_texts SET content_loc7='Sueño... tener tanto sueño...' WHERE entry=-1000796; /* content: Sleepy... so sleepy... */
UPDATE script_texts SET content_loc7='¡%s grazna y se dirige hacia Velo Shalas. ¡Apresúrate y síguelo!' WHERE entry=-1000797; /* content: %s squawks and heads toward Veil Shalas. Hurry and follow! */
UPDATE script_texts SET content_loc7='%s se calla brevemente ante el árbol y se dirige a su interior.' WHERE entry=-1000798; /* content: %s pauses briefly before the tree and then heads inside. */
UPDATE script_texts SET content_loc7='%s parece estar buscando algo. Quiere que lo sigas.' WHERE entry=-1000799; /* content: %s seems to be looking for something. He wants you to follow. */
UPDATE script_texts SET content_loc7='¡%s vuela hasta la plataforma que hay debajo! Será mejor que saltes si quieres seguir el ritmo. ¡Date prisa!' WHERE entry=-1000800; /* content: %s flies to the platform below! You\'d better jump if you want to keep up. Hurry! */
UPDATE script_texts SET content_loc7='¡%s lanza un fuerte graznido!' WHERE entry=-1000801; /* content: %s bellows a loud squawk! */
UPDATE script_texts SET content_loc7='¡Al fin libre de esa horrible maldición! ¡Gracias! Por favor, envía un mensaje a Rilak el Redimido diciéndole que estoy bien. Mi misión está en Skettis. ¡Hay que vencer a Terokk!' WHERE entry=-1000802; /* content: Free at last from that horrible curse! Thank you! Please send word to Rilak the Redeemed that I am okay. My mission lies in Skettis. Terokk must be defeated! */
UPDATE script_texts SET content_loc7='Lucharemos cuando estés $gpreparado:preparada;.' WHERE entry=-1000804; /* content: We will fight when you are ready. */
UPDATE script_texts SET content_loc7='¡Liberaremos a los elementos de tu alcance por la fuerza!' WHERE entry=-1000805; /* content: We will set the elements free of your grasp by force! */
UPDATE script_texts SET content_loc7='Oigo lo mismo que tú, hermano. Mira tras de ti...' WHERE entry=-1000807; /* content: I hear what you hear, brother. Look behind you... */
UPDATE script_texts SET content_loc7='¡Se han redimido! Entonces, ¿hemos ganado?' WHERE entry=-1000808; /* content: They are redeemed! Then we have won? */
UPDATE script_texts SET content_loc7='Ahora es como debería ser, chamán. Has hecho bien.' WHERE entry=-1000809; /* content: It is now as it should be, shaman. You have done well. */
UPDATE script_texts SET content_loc7='Sí... Está bien por los elementos que están aquí, pero la clave es conocida para otro... Los espíritus del fuego están confusos... Si no se detiene esta fuerza, el mundo del que vinieron estos mortales cesará de existir.' WHERE entry=-1000810; /* content: Yes... Well enough for the elements that are here, but the cipher is known to another... The spirits of fire are in turmoil... If this force is not stopped, the world where these mortals came from will cease. */
UPDATE script_texts SET content_loc7='Adiós, mortales... El ensalmador de la tierra sabe lo que siente el fuego...' WHERE entry=-1000811; /* content: Farewell, mortals... The earthmender knows what fire feels... */
UPDATE script_texts SET content_loc7='Los hombres del corazón roto entregan sus armas al ensalmador de la tierra Torlok.' WHERE entry=-1000813; /* content: The Torn-heart men give their weapons to Earthmender Torlok. */
UPDATE script_texts SET content_loc7='Da esto a los héroes que lo hicieron posible.' WHERE entry=-1000814; /* content: Give these to the heroes that made this possible. */
UPDATE script_texts SET content_loc7='¡Cúrate!' WHERE entry=-1000815; /* content: Be healed! */
UPDATE script_texts SET content_loc7='¡Estamos salvados! ¡Los campesinos se han salvado de la Plaga!' WHERE entry=-1000816; /* content: We are saved! The peasants have escaped the Scourge! */
UPDATE script_texts SET content_loc7='He vuelto a fallar...' WHERE entry=-1000817; /* content: I have failed once more... */
UPDATE script_texts SET content_loc7='Ahora regreso al lugar de donde vengo, solo para encontrarme de nuevo aquí, para revivir la misma tragedia épica.' WHERE entry=-1000818; /* content: I now return to whence I came, only to find myself here once more to relive the same epic tragedy. */
UPDATE script_texts SET content_loc7='¡La Plaga nos ataca! ¡Corran! ¡Corran si quieren salvar su vida!' WHERE entry=-1000819; /* content: The Scourge are upon us! Run! Run for your lives! */
UPDATE script_texts SET content_loc7='¡Por favor, ayúdanos! ¡El príncipe se ha vuelto loco!' WHERE entry=-1000820; /* content: Please help us! The Prince has gone mad! */
UPDATE script_texts SET content_loc7='Ya he enviado la señal. Seguro que llegará pronto.' WHERE entry=-1000822; /* content: The signal has been sent. He should be arriving shortly. */
UPDATE script_texts SET content_loc7='¡Aaaaau!' WHERE entry=-1000823; /* content: Yawww! */
UPDATE script_texts SET content_loc7='Sabía que vendrías, $n. Me alegra verte de nuevo, camarada.' WHERE entry=-1000824; /* content: I knew you would come, $N. It is good to see you again, friend. */
UPDATE script_texts SET content_loc7='En guardia, camarada. El dragón no se rendirá sin luchar.' WHERE entry=-1000825; /* content: On guard, friend. The lady dragon will not give in without a fight. */
UPDATE script_texts SET content_loc7='Como se predijo hace una eternidad en Karazhan, monstruo, he venido y, conmigo, traigo la justicia.' WHERE entry=-1000826; /* content: As was fated a lifetime ago in Karazhan, monster - I come - and with me I bring justice. */
UPDATE script_texts SET content_loc7='¡Atrapadle! ¡Apresad al indigno criminal y a sus aliados!' WHERE entry=-1000827; /* content: Seize him! Seize the worthless criminal and his allies! */
UPDATE script_texts SET content_loc7='Reginald, sabes que no puedo dejarte pasar.' WHERE entry=-1000828; /* content: Reginald, you know that I cannot let you pass. */
UPDATE script_texts SET content_loc7='Debes hacer lo que creas correcto, Marcus. Servimos juntos a las órdenes de Turalyon. Él nos convirtió en los hombres que somos hoy. ¿Crees que se equivocó conmigo? ¿Realmente piensas que pretendo hacer daño a nuestra alianza? ¿Crees que avergonzaría a nuestros héroes?' WHERE entry=-1000829; /* content: You must do what you think is right, Marcus. We served together under Turalyon. He made us both the men that we are today. Did he err with me? Do you truly believe my intent is to cause harm to our alliance? Would I shame our heroes? */
UPDATE script_texts SET content_loc7='Mantenerme aquí no es la decisión correcta, Marcus.' WHERE entry=-1000830; /* content: Holding me here is not the right decision, Marcus. */
UPDATE script_texts SET content_loc7='%s parece meditar.' WHERE entry=-1000831; /* content: %s appears lost in contemplation. */
UPDATE script_texts SET content_loc7='Me siento avergonzado. Ya no sé qué hago. No eres tú quien podría avergonzar a los héroes de la leyenda... sino yo. Yo y el resto de esos políticos corruptos. Llenan nuestras vidas de promesas vacías, mentiras interminables.' WHERE entry=-1000832; /* content: I am ashamed, old friend. I know not what I do anymore. It is not you that would dare bring shame to the heroes of legend - it is I. It is I and the rest of these corrupt politicians. They fill our lives with empty promises, unending lies. */
UPDATE script_texts SET content_loc7='Avergonzamos a nuestros ancestros. Avergonzamos a aquellos que murieron por nosotros... perdóname, Reginald.' WHERE entry=-1000833; /* content: We shame our ancestors. We shame those lost to us... forgive me, Reginald. */
UPDATE script_texts SET content_loc7='Querido amigo, tú les honras con tu vigilante guardia. Eres firme en tu lealtad. No dudo ni por un instante, que no harías un sacrificio por tu gente tan grande como cualquiera de los héroes que están por encima de ti.' WHERE entry=-1000834; /* content: Dear friend, you honor them with your vigilant watch. You are steadfast in your allegiance. I do not doubt for a moment that you would not give as great a sacrifice for your people as any of the heroes you stand under. */
UPDATE script_texts SET content_loc7='Es hora de poner fin a su reinado, Marcus. No te incorpores, camarada.' WHERE entry=-1000835; /* content: Now, it is time to bring her reign to an end, Marcus. Stand down, friend. */
UPDATE script_texts SET content_loc7='¡Agachaos! ¿No veis que los héroes están pasando entre nosotros?' WHERE entry=-1000836; /* content: Stand down! Can you not see that heroes walk among us? */
UPDATE script_texts SET content_loc7='¡Moveos! ¡Dejadles pasar!' WHERE entry=-1000837; /* content: Move aside! Let them pass! */
UPDATE script_texts SET content_loc7='¡Reginald Windsor no debe sufrir ni un rasguño! ¡Pasará por aquí y nadie le tocará!' WHERE entry=-1000838; /* content: Reginald Windsor is not to be harmed! He shall pass through untouched! */
UPDATE script_texts SET content_loc7='Adelante, Reginald. Que la Luz guíe tu mano.' WHERE entry=-1000839; /* content: Go, Reginald. May the light guide your hand. */
UPDATE script_texts SET content_loc7='Gracias, $gviejo amigo:vieja amiga;. Has hecho lo correcto.' WHERE entry=-1000840; /* content: Thank you, old friend. You have done the right thing. */
UPDATE script_texts SET content_loc7='Amigos, seguidme. ¡Al Castillo de Ventormenta!' WHERE entry=-1000841; /* content: Follow me, friends. To Stormwind Keep! */
UPDATE script_texts SET content_loc7='Que la Luz sea contigo, señor.' WHERE entry=-1000842; /* content: Light be with you, sir. */
UPDATE script_texts SET content_loc7='Solo somos suciedad bajo sus pies, señor.' WHERE entry=-1000843; /* content: We are but dirt beneath your feet, sir. */
UPDATE script_texts SET content_loc7='... coraje de torio.' WHERE entry=-1000844; /* content: ...nerves of thorium. */
UPDATE script_texts SET content_loc7='¡Haced sitio!' WHERE entry=-1000845; /* content: Make way! */
UPDATE script_texts SET content_loc7='Un momento que recordaré eternamente.' WHERE entry=-1000847; /* content: A moment I shall remember for always. */
UPDATE script_texts SET content_loc7='Eres una inspiración para todos nosotros, señor.' WHERE entry=-1000848; /* content: You are an inspiration to us all, sir. */
UPDATE script_texts SET content_loc7='Sed valientes, amigos. El reptil se sacudirá violentamente. Es un acto de desesperación. Cuando estéis listos, avisad.' WHERE entry=-1000849; /* content: Be brave, friends. The reptile will thrash wildly. It is an act of desperation. When you are ready, give me the word. */
UPDATE script_texts SET content_loc7='¡EN GUARDIA!' WHERE entry=-1000850; /* content: Onward! */
UPDATE script_texts SET content_loc7='Majestad, corred mientras podéis. No es quien creéis que es...' WHERE entry=-1000851; /* content: Majesty, run while you still can. She is not what you think her to be... */
UPDATE script_texts SET content_loc7='A la sala asegurada, su majestad.' WHERE entry=-1000852; /* content: To the safe hall, your majesty. */
UPDATE script_texts SET content_loc7='La farsa se ha terminado, Lady Prestor. O... debería llamarte por tu verdadero nombre... Onyxia...' WHERE entry=-1000853; /* content: The masquerade is over, Lady Prestor. Or should I call you by your true name... Onyxia... */
UPDATE script_texts SET content_loc7='%s ríe.' WHERE entry=-1000854; /* content: %s laughs. */
UPDATE script_texts SET content_loc7='Windsor, serás encarcelado y juzgado por traición. Estaré observando con júbilo mientras pronuncian el veredicto de culpabilidad y dictan tu sentencia de muerte... en la horca.' WHERE entry=-1000855; /* content: You will be incarcerated and tried for treason, Windsor. I shall watch with glee as they hand down a guilty verdict and sentence you to death by hanging... */
UPDATE script_texts SET content_loc7='No escaparás a tu destino, Onyxia. La profecía así lo cuenta... una visión resonando en las grandes salas de Karazhan. Acaba ahora...' WHERE entry=-1000857; /* content: You will not escape your fate, Onyxia. It has been prophesied - a vision resonating from the great halls of Karazhan. It ends now... */
UPDATE script_texts SET content_loc7='Los Hierro Negro creían que esas tablillas estaban codificadas. Esto no se trata de ningún código, es la lengua antigua de los dragones.' WHERE entry=-1000859; /* content: The Dark Irons thought these tablets to be encoded. This is not any form of coding, it is the tongue of ancient dragon. */
UPDATE script_texts SET content_loc7='Escucha, dragón. Que la verdad resuene en estas salas.' WHERE entry=-1000860; /* content: Listen, dragon. Let the truth resonate throughout these halls. */
UPDATE script_texts SET content_loc7='%s contiene un grito.' WHERE entry=-1000862; /* content: %s gasps. */
UPDATE script_texts SET content_loc7='Muy curioso... Windsor, en esa visión, ¿sobrevivías? Solo lo pregunto porque lo único que puedo asegurar y aseguraré, es tu muerte. Aquí y ahora.' WHERE entry=-1000863; /* content: Curious... Windsor, in this vision, did you survive? I only ask because one thing that I can and will assure is your death. Here and now. */
UPDATE script_texts SET content_loc7='¡Dragón asqueroso! ¡Guardias! ¡Guardias! ¡Coged al monstruo!' WHERE entry=-1000864; /* content: Dragon filth! Guards! Guards! Seize this monster! */
UPDATE script_texts SET content_loc7='Sssí... ¡Guardias, vengan en ayuda de su señor!' WHERE entry=-1000865; /* content: Yesss... Guards, come to your lord\'s aid! */
UPDATE script_texts SET content_loc7='¡NO LA DEJÉIS ESCAPAR!' WHERE entry=-1000866; /* content: DO NOT LET HER ESCAPE! */
UPDATE script_texts SET content_loc7='Le habéis fallado, mortalesss... ¡Adiós!' WHERE entry=-1000868; /* content: You have failed him, mortalsss... Farewell! */
UPDATE script_texts SET content_loc7='Reginald... Yo... Lo siento.' WHERE entry=-1000869; /* content: Reginald... I... I am sorry. */
UPDATE script_texts SET content_loc7='Bol... Bolvar... El medallón... Utilízalo...' WHERE entry=-1000870; /* content: Bol... Bolvar... the medallion... use... */
UPDATE script_texts SET content_loc7='%s muere.' WHERE entry=-1000871; /* content: %s dies. */
UPDATE script_texts SET content_loc7='Conozco el camino, gusano. No tienes que empujarme como si fuera una res.' WHERE entry=-1000873; /* content: I know the way, insect. There is no need to prod me as if I were cattle. */
UPDATE script_texts SET content_loc7='No pienses que te vas a salir con la tuya con esta incursión. Vendrán a buscarme y pagarás por tu insolencia.' WHERE entry=-1000874; /* content: Surely you do not think that you will get away with this incursion. They will come for me and you shall pay for your insolence. */
UPDATE script_texts SET content_loc7='¡ATRAVESADLOS CON LAS ESPADAS, HERMANOS!' WHERE entry=-1000875; /* content: RUN THEM THROUGH BROTHERS! */
UPDATE script_texts SET content_loc7='Dudo que tengáis tanta suerte la próxima vez que te encuentres a mis hermanos.' WHERE entry=-1000876; /* content: I doubt you will be so lucky the next time you encounter my brethren. */
UPDATE script_texts SET content_loc7='¡Vienen a por ti, idiota!' WHERE entry=-1000877; /* content: They come for you, fool! */
UPDATE script_texts SET content_loc7='¿Qué crees que vas a lograr con esto, idiota? Incluso ahora, los ejércitos Roca Negra están preparándose para destruir tu mundo.' WHERE entry=-1000878; /* content: What do you think you accomplish from this, fool? Even now, the Blackrock armies make preparations to destroy your world. */
UPDATE script_texts SET content_loc7='Su vuelo es el más oscuro. ¡Prepárate para tu final!' WHERE entry=-1000879; /* content: On darkest wing they fly. Prepare to meet your end! */
UPDATE script_texts SET content_loc7='¡Aún falta lo peor!' WHERE entry=-1000880; /* content: The worst is yet to come! */
UPDATE script_texts SET content_loc7='%s ríe.' WHERE entry=-1000881; /* content: %s laughs. */
UPDATE script_texts SET content_loc7='Es hora de hacer tu última aparición, miserable.' WHERE entry=-1000882; /* content: Time to make your final stand, Insect. */
UPDATE script_texts SET content_loc7='%s eleva su hacha gigantesca sobre Grark.' WHERE entry=-1000885; /* content: %s raises his massive axe over Grark. */
UPDATE script_texts SET content_loc7='%s levanta la mano y luego la baja.' WHERE entry=-1000886; /* content: %s raises his hand and then lowers it. */
UPDATE script_texts SET content_loc7='¡Acaba con él!' WHERE entry=-1000887; /* content: End him... */
UPDATE script_texts SET content_loc7='¡Tú, Soldado, preséntate ante Kargath de inmediato!' WHERE entry=-1000888; /* content: You, soldier, report back to Kargath at once! */
UPDATE script_texts SET content_loc7='%s se somete.' WHERE entry=-1000889; /* content: %s submits. */
UPDATE script_texts SET content_loc7='¿Has venido a jugar? ¡Juguemos, pues!' WHERE entry=-1000890; /* content: You have come to play? Then let us play! */
UPDATE script_texts SET content_loc7='¡Ha caído un defensor!' WHERE entry=-1000892; /* content: A defender has fallen! */
UPDATE script_texts SET content_loc7='¡Por la Horda!' WHERE entry=-1000895; /* content: For the Horde! */
UPDATE script_texts SET content_loc7='¡Defended los búnkeres!' WHERE entry=-1000896; /* content: Defend the bunkers! */
UPDATE script_texts SET content_loc7='¡Nuestros enemigos caerán!' WHERE entry=-1000897; /* content: Our foes will fall! */
UPDATE script_texts SET content_loc7='REUSE ME' WHERE entry=-1000898; /* content: REUSE ME */
UPDATE script_texts SET content_loc7='REUSE ME' WHERE entry=-1000899; /* content: REUSE ME */
UPDATE script_texts SET content_loc7='REUSE ME' WHERE entry=-1000900; /* content: REUSE ME */
