UPDATE script_texts SET content_loc7='REUSE ME' WHERE entry=-1000904; /* content: REUSE ME */
UPDATE script_texts SET content_loc7='Atácame si quieres, pero no vas a impedir que regrese con Quae.' WHERE entry=-1000950; /* content: Attack me if you will, but you won\'t stop me from getting back to Quae. */
UPDATE script_texts SET content_loc7='¡Vigilad mi retaguardia! Veré qué puedo encontrar en toda esta basura...' WHERE entry=-1000952; /* content: Watch my rear! I\'ll see what I can find in all this junk... */
UPDATE script_texts SET content_loc7='%s empieza a hurgar en las pertenencias del boticario.' WHERE entry=-1000953; /* content: %s begins rummaging through the apothecary\'s belongings. */
UPDATE script_texts SET content_loc7='¡Bueno, salgamos de aquí deprisa, deprisa! Esforzaos y animaos. Voy a hacer una pausa para esto.' WHERE entry=-1000955; /* content: Okay, let\'s get out of here quick quick! Try and keep up. I\'m going to make a break for it. */
UPDATE script_texts SET content_loc7='¡Lo hemos conseguido! Quae, ¡lo hemos conseguido!' WHERE entry=-1000956; /* content: We made it! Quae, we made it! */
UPDATE script_texts SET content_loc7='%s entrega su mochila a Quae.' WHERE entry=-1000957; /* content: %s hands her pack to Quae. */
UPDATE script_texts SET content_loc7='¡Vale, vale! No es necesaria tanta violencia. Hablaré. ¡Hablaré!' WHERE entry=-1000978; /* content: Okay, okay! No need to get all violent. I\'ll talk. I\'ll talk! */
UPDATE script_texts SET content_loc7='¡Guau! Supera con creces lo que habíamos acordado, ¡ahora te las tendrás que apañar solo, Flaco!' WHERE entry=-1000979; /* content: Whoa! This is way more than what I bargained for, you\'re on your own, Slim! */
UPDATE script_texts SET content_loc7='Tengo unas cuantas notas del trabajo en casa. Las cogeré y te volveré a encontrar en la posada.' WHERE entry=-1000980; /* content: I have a few notes from the job back at my place. I\'ll get them and then meet you back in the inn. */
UPDATE script_texts SET content_loc7='¡Está encendido! $n, mira mis puños. Primero, di hola a mis puños, $n.' WHERE entry=-1000993; /* content: It\'s on! $N, meet my fists. Fists, say hello to $N. */
UPDATE script_texts SET content_loc7='Voy a abrir una lata encima de $n.' WHERE entry=-1000994; /* content: I\'m about to open a can on this $N. */
UPDATE script_texts SET content_loc7='¡Allí! ¡Por allí!' WHERE entry=-1001150; /* content: There! Over there! */
UPDATE script_texts SET content_loc7='Lo único que necesito es un lazo de oro.' WHERE entry=-1001152; /* content: All I need now is a golden lasso. */
UPDATE script_texts SET content_loc7='¡TRAIDOR!' WHERE entry=-1001155; /* content: BETRAYER! */
UPDATE script_texts SET content_loc7='¡Oh no! ¿Qué ha pasado? Es todo culpa mía...' WHERE entry=-1001158; /* content: No! My friend... what\'s happened? This is all my fault... */
UPDATE script_texts SET content_loc7='Sígueme y te abriré la puerta del patio.' WHERE entry=-1033000; /* content: Follow me and I\'ll open the courtyard door for you. */
UPDATE script_texts SET content_loc7='Conozco el conjuro para abrir esta puerta. Es una pena que las puertas de la celda no hubieran sido cerradas aleatoriamente.' WHERE entry=-1033001; /* content: I have just the spell to get this door open. Too bad the cell doors weren\'t locked so haphazardly. */
UPDATE script_texts SET content_loc7='¡Ya está! Abierta de par en par. Buena suerte en la conquista del más allá. ¡Tengo que informar al Kirin Tor de inmediato!' WHERE entry=-1033002; /* content: There it is! Wide open. Good luck to you conquering what lies beyond. I must report back to the Kirin Tor at once! */
UPDATE script_texts SET content_loc7='¡Por fin estoy fuera de esta maldita celda! Te acompañaré al patio...' WHERE entry=-1033003; /* content: Free from this wretched cell at last! Let me show you to the courtyard.... */
UPDATE script_texts SET content_loc7='Realmente eres valiente por querer hacer frente a los horrores que se ocultan tras esta puerta.' WHERE entry=-1033004; /* content: You are indeed courageous for wanting to brave the horrors that lie beyond this door. */
UPDATE script_texts SET content_loc7='¡Allá vamos!' WHERE entry=-1033005; /* content: There we go! */
UPDATE script_texts SET content_loc7='Buena suerte con Arugal. Tengo que volver deprisa a Hadrec.' WHERE entry=-1033006; /* content: Good luck with Arugal. I must hurry back to Hadrec now. */
UPDATE script_texts SET content_loc7='Ya era hora de que alguien acabara con el pobre infeliz.' WHERE entry=-1033007; /* content: About time someone killed the wretch. */
UPDATE script_texts SET content_loc7='Por una vez estoy de acuerdo contigo... canalla.' WHERE entry=-1033008; /* content: For once I agree with you... scum. */
UPDATE script_texts SET content_loc7='He cambiado de parecer, mis leales sirvientes, no tenéis que traer al prisionero hasta mi estudio. Me ocuparé de él aquí y ahora.' WHERE entry=-1033009; /* content: I have changed my mind loyal servants, you do not need to bring the prisoner all the way to my study, I will deal with him here and now. */
UPDATE script_texts SET content_loc7='¡Vincent! Ni tú ni los de tu pelaje tendréis más éxito aniquilándonos a mí y a mis hijos que aquellos miserables vestigios del Kirin Tor.' WHERE entry=-1033010; /* content: Vincent!  You and your pathetic ilk will find no more success in routing my sons and I than those beggardly remnants of the Kirin Tor. */
UPDATE script_texts SET content_loc7='Si no sirvieras a mi maestro con tu espada y con tu saber sobre sus enemigos...' WHERE entry=-1033011; /* content: If you will not serve my Master with your sword and knowledge of his enemies... */
UPDATE script_texts SET content_loc7='¿Quién osa interferir con los hijos de Arugal?' WHERE entry=-1033013; /* content: Who dares interfere with the Sons of Arugal? */
UPDATE script_texts SET content_loc7='%s desaparece.' WHERE entry=-1033014; /* content: %s vanishes. */
UPDATE script_texts SET content_loc7='%s manosea con torpeza el oxidado cerrojo de la puerta del patio.' WHERE entry=-1033015; /* content: %s fumbles with the rusty lock on the courtyard door. */
UPDATE script_texts SET content_loc7='¡Arrrgh!' WHERE entry=-1033016; /* content: Arrrgh! */
UPDATE script_texts SET content_loc7='¡Tú también servirás!' WHERE entry=-1033017; /* content: You, too, shall serve! */
UPDATE script_texts SET content_loc7='¡Otro que cae!' WHERE entry=-1033018; /* content: Another Falls! */
UPDATE script_texts SET content_loc7='¡Libera tu furia!' WHERE entry=-1033019; /* content: Release your rage! */
UPDATE script_texts SET content_loc7='¡Ah! ¡Me están haciendo enojar!' WHERE entry=-1036003; /* content: D\'ah! Now you\'re making me angry! */
UPDATE script_texts SET content_loc7='¡Al fin! ¡Podemos despertar a Naralex! ¡Venid a ayudarme, valientes aventureros!' WHERE entry=-1043000; /* content: At last! Naralex can be awakened! Come aid me, brave adventurers! */
UPDATE script_texts SET content_loc7='Debo realizar los preparativos necesarios antes de iniciar el ritual del despertar. ¡Debes protegerme!' WHERE entry=-1043001; /* content: I must make the necessary preparations before the awakening ritual can begin. You must protect me! */
UPDATE script_texts SET content_loc7='Estas cavernas fueron en su día un templo de promisión para el resurgimiento en Los Baldíos. Ahora se han convertido en los corredores de las pesadillas.' WHERE entry=-1043002; /* content: These caverns were once a temple of promise for regrowth in the Barrens. Now, they are the halls of nightmares. */
UPDATE script_texts SET content_loc7='Ven. Debemos continuar. Hay mucho que hacer antes de poder sacar a Naralex de su pesadilla.' WHERE entry=-1043003; /* content: Come. We must continue. There is much to be done before we can pull Naralex from his nightmare. */
UPDATE script_texts SET content_loc7='Con este círculo de fuego, lanzaré el hechizo para desterrar los espíritus de los Nobles del Colmillo fallecidos.' WHERE entry=-1043004; /* content: Within this circle of fire I must cast the spell to banish the spirits of the slain Fanglords. */
UPDATE script_texts SET content_loc7='Las Cuevas han sido purificadas. ¡Nos vamos a la Cámara de Naralex!' WHERE entry=-1043005; /* content: The caverns have been purified. To Naralex\'s chamber we go! */
UPDATE script_texts SET content_loc7='Tras este pasillo, Naralex yace en un sueño intermitente. Vamos a despertarle antes de que sea demasiado tarde.' WHERE entry=-1043006; /* content: Beyond this corridor, Naralex lies in fitful sleep. Let us go awaken him before it is too late. */
UPDATE script_texts SET content_loc7='¡Protegedme, almas valientes, mientras escarbo en el Sueño Esmeralda para rescatar a Naralex y acabar con esta corrupción!' WHERE entry=-1043007; /* content: Protect me brave souls as I delve into the Emerald Dream to rescue Naralex and put an end to this corruption! */
UPDATE script_texts SET content_loc7='%s comienza a realizar el ritual del despertar con Naralex.' WHERE entry=-1043008; /* content: %s begins to perform the awakening ritual on Naralex. */
UPDATE script_texts SET content_loc7='%s da vueltas intermitentemente en un sueño agitado.' WHERE entry=-1043009; /* content: %s tosses fitfully in troubled sleep. */
UPDATE script_texts SET content_loc7='%s se retuerce de dolor. El Discípulo parece penetrar en la defensa enemiga.' WHERE entry=-1043010; /* content: %s writhes in agony. The Disciple seems to be breaking through. */
UPDATE script_texts SET content_loc7='%s tiene en sueños una espantosa visión. Algo se mueve bajo las turbias aguas.' WHERE entry=-1043011; /* content: %s dreams up a horrendous vision. Something stirs beneath the murky waters. */
UPDATE script_texts SET content_loc7='¡ESTOY DESPIERTO AL FIN!' WHERE entry=-1043013; /* content: I AM AWAKE, AT LAST! */
UPDATE script_texts SET content_loc7='¡Al fin! Naralex despierta de la pesadilla.' WHERE entry=-1043014; /* content: At last! Naralex awakes from the nightmare. */
UPDATE script_texts SET content_loc7='¡Ah, al fin liberado de una pavorosa pesadilla! Te lo agradezco, mi leal Discípulo, a ti y a tus valientes compañeros.' WHERE entry=-1043015; /* content: Ah, to be pulled from the dreaded nightmare! I thank you, my loyal Disciple, along with your brave companions. */
UPDATE script_texts SET content_loc7='¡Me atacan! ¡Ayudadme a sacarme a $n de encima!' WHERE entry=-1043017; /* content: Attacked! Help get this $N off of me! */
UPDATE script_texts SET content_loc7='¡AYUDA!' WHERE entry=-1043018; /* content: Help! */
UPDATE script_texts SET content_loc7='¡Encárgate de esto, $n! ¡Necesito prepararme para despertar a Naralex!' WHERE entry=-1043019; /* content: Deal with this $N! I need to prepare to awake Naralex! */
UPDATE script_texts SET content_loc7='¡Ju, ju! Al fin voy a salir de aquí. De todas maneras, no va a ser fácil. Mantened los ojos abiertos por si tenemos problemas.' WHERE entry=-1047000; /* content: Woo hoo! Finally getting out of here. It\'s going to be rough though. Keep your eyes peeled for trouble. */
UPDATE script_texts SET content_loc7='Allí es donde reside Charlga Filonavaja. Maldita bruja vieja.' WHERE entry=-1047001; /* content: Up there is where Charlga Razorflank resides. Blasted old crone. */
UPDATE script_texts SET content_loc7='¡En algún lugar de esta trinchera hay una trufa hojazul! ¡Es como oro esperando a ser extraído, créeme!' WHERE entry=-1047002; /* content: There\'s blueleaf tuber in this trench! It\'s like gold waiting to be mined I tell you! */
UPDATE script_texts SET content_loc7='Aquí acecha el peligro en cada esquina.' WHERE entry=-1047003; /* content: There could be danger around every corner here. */
UPDATE script_texts SET content_loc7='No puedo entender que estos nauseabundos animales vivan en este lugar... ¡Puaj! ¡Apesta!' WHERE entry=-1047004; /* content: I don\'t see how these foul animals live in this place... sheesh it smells! */
UPDATE script_texts SET content_loc7='Creo que veo un modo de salir de este enorme y enredado zarzal.' WHERE entry=-1047005; /* content: I think I see a way for us to get out of this big twisted mess of a bramble. */
