UPDATE script_texts SET content_loc7='¡Ha caído uno de los Cristales de Ash\'ari! Manteneos fieles al Rey Exánime, hermanos míos, e intentad invocarlo de nuevo.' WHERE entry=-1329004; /* content: An Ash\'ari Crystal has fallen! Stay true to the Lich King, my brethren, and attempt to resummon it. */
