UPDATE script_texts SET content_loc5='我想我看到了一條可以走出這片荊棘林的路。' WHERE entry=-1047005; /* content: I think I see a way for us to get out of this big twisted mess of a bramble. */
UPDATE script_texts SET content_loc5='能走出這條臭溝真好，儘管出去了也好不了多少。' WHERE entry=-1047006; /* content: Glad to be out of that wretched trench. Not much nicer up here though! */
UPDATE script_texts SET content_loc5='終於!我終於能走出這個地方了。' WHERE entry=-1047007; /* content: Finally! I\'ll be glad to get out of this place. */
UPDATE script_texts SET content_loc5='我想在回到棘齒城之前休息一下。謝謝你的幫助!' WHERE entry=-1047008; /* content: I think I\'ll rest a moment and catch my breath before heading back to Ratchet. Thanks for all the help! */
UPDATE script_texts SET content_loc5='$n正在向這裡衝來!做好戰鬥準備!' WHERE entry=-1047009; /* content: $N heading this way fast! To arms! */
