UPDATE script_texts SET content_loc5='%s從他的座龍身上被打了下來!' WHERE entry=-1229019; /* content: %s is knocked off his drake! */
UPDATE script_texts SET content_loc5='啊，太棒了!' WHERE entry=-1230000; /* content: Ah, hits the spot! */
UPDATE script_texts SET content_loc5='向王者歡呼，寶貝!' WHERE entry=-1230002; /* content: Hail to the king, baby! */
UPDATE script_texts SET content_loc5='你對七賢發出了挑戰，死吧!' WHERE entry=-1230003; /* content: You have challenged the Seven, and now you will die! */
UPDATE script_texts SET content_loc5='索瑞森的臣民將在秩序競技場中看著你死去!' WHERE entry=-1230004; /* content: The Sons of Thaurissan shall watch you perish in the Ring of the Law! */
UPDATE script_texts SET content_loc5='你因為反對黑鐵王國的罪行而被宣判死罪!' WHERE entry=-1230005; /* content: You have been sentenced to death for crimes against the Dark Iron Nation! */
UPDATE script_texts SET content_loc5='釋放你的怒氣吧!' WHERE entry=-1230006; /* content: Unleash the fury and let it be done! */
UPDATE script_texts SET content_loc5='但是真正的懲罰還在後面呢。' WHERE entry=-1230007; /* content: But your real punishment lies ahead. */
UPDATE script_texts SET content_loc5='哈!我敢打賭你以為自己能獲勝!' WHERE entry=-1230008; /* content: Haha! I bet you thought you were done! */
UPDATE script_texts SET content_loc5='太好了!' WHERE entry=-1230009; /* content: Good Riddance! */
UPDATE script_texts SET content_loc5='謝謝你，$n!我自由了!!!' WHERE entry=-1230010; /* content: Thank you, $N! I\'m free!!! */
UPDATE script_texts SET content_loc5='你關錯元帥了，$n。準備受死吧!' WHERE entry=-1230011; /* content: You locked up the wrong Marshal, $N. Prepare to be destroyed! */
UPDATE script_texts SET content_loc5='你最好抓牢我，否則$n就有苦頭吃了!' WHERE entry=-1230013; /* content: You better hold me back or $N is going to feel some prison house beatings. */
UPDATE script_texts SET content_loc5='我們行動吧。我的裝備應該在這條路上的某個儲物間裡。' WHERE entry=-1230014; /* content: Let\'s get a move on. My gear should be in the storage area up this way... */
UPDATE script_texts SET content_loc5='檢查那間牢房，$n。如果那裡還有人活著，我們就得把他們救出來。' WHERE entry=-1230015; /* content: Check that cell, $N. If someone is alive in there, we need to get them out. */
UPDATE script_texts SET content_loc5='沒錯，$n，我的東西都在那個房間裡。保護我，我準備衝進去了!' WHERE entry=-1230017; /* content: This is it, $N. My stuff should be in that room. Cover me, I\'m going in! */
UPDATE script_texts SET content_loc5='啊，它在那兒!' WHERE entry=-1230018; /* content: Ah, there it is! */
UPDATE script_texts SET content_loc5='你能感受到這力量嗎，$n?讓我們舞動起來吧!' WHERE entry=-1230019; /* content: Can you feel the power, $N??? It\'s time to ROCK! */
UPDATE script_texts SET content_loc5='我們只需解放托比亞斯，然後就能離開這兒了。這邊走!' WHERE entry=-1230020; /* content: Now we just have to free Tobias and we can get out of here. This way! */
UPDATE script_texts SET content_loc5='打開它。' WHERE entry=-1230021; /* content: Open it. */
UPDATE script_texts SET content_loc5='我從未喜歡過那兩個傢伙。我們繼續前進吧。' WHERE entry=-1230022; /* content: I never did like those two. Let\'s get moving. */
UPDATE script_texts SET content_loc5='打開它，這次小心點!' WHERE entry=-1230023; /* content: Open it and be careful this time! */
UPDATE script_texts SET content_loc5='那個愚蠢的傢伙真是罪有應得!' WHERE entry=-1230024; /* content: That intolerant dirtbag finally got what was coming to him. Good riddance! */
UPDATE script_texts SET content_loc5='好吧，我們走。' WHERE entry=-1230025; /* content: Alright, let\'s go. */
UPDATE script_texts SET content_loc5='打開它。我們要抓緊時間。我已經可以聞到黑鐵軍團的氣味了，我可以告訴你，他們就要來了!' WHERE entry=-1230026; /* content: Open it. We need to hurry up. I can smell those Dark Irons coming a mile away and I can tell you one thing, they\'re COMING! */
UPDATE script_texts SET content_loc5='他應該在最後一間牢房裡。除非...除非他們已經把他給殺了。' WHERE entry=-1230027; /* content: He has to be in the last cell. Unless... they killed him. */
UPDATE script_texts SET content_loc5='把他從那裡弄出去!' WHERE entry=-1230028; /* content: Get him out of there! */
UPDATE script_texts SET content_loc5='幹得很好，$n。讓我們去找出口吧。我想我知道該怎麼走，跟著我!' WHERE entry=-1230029; /* content: Excellent work, $N. Let\'s find the exit. I think I know the way. Follow me! */
UPDATE script_texts SET content_loc5='我們成功了!' WHERE entry=-1230030; /* content: We made it! */
UPDATE script_texts SET content_loc5='到麥斯威爾的營地碰面吧。我們將在那兒開始下一階段的計畫，在沒有解碼戒指的情況下找出破解石板上的文字的方法。' WHERE entry=-1230031; /* content: Meet me at Maxwell\'s encampment. We\'ll go over the next stages of the plan there and figure out a way to decode my tablets without the decryption ring. */
UPDATE script_texts SET content_loc5='我終於可以離開這個垃圾場了!!' WHERE entry=-1230033; /* content: Finally!! I can leave this dump. */
UPDATE script_texts SET content_loc5='你要為此付出代價!' WHERE entry=-1230037; /* content: You\'ll pay for that! */
UPDATE script_texts SET content_loc5='不可能!' WHERE entry=-1230038; /* content: No! */
UPDATE script_texts SET content_loc5='離那些酒桶遠一點!' WHERE entry=-1230041; /* content: Get away from those kegs! */
UPDATE script_texts SET content_loc5='你要為此付出代價!' WHERE entry=-1230042; /* content: You\'ll pay for that! */
UPDATE script_texts SET content_loc5='喝空的!!' WHERE entry=-1230044; /* content: That one\'s empty!! */
UPDATE script_texts SET content_loc5='啊，又空了!' WHERE entry=-1230045; /* content: Ah, empty again! */
UPDATE script_texts SET content_loc5='麥芽酒!' WHERE entry=-1230046; /* content: ALE! */
UPDATE script_texts SET content_loc5='我...我們...在調查。' WHERE entry=-1230049; /* content: We...are...investigating. */
UPDATE script_texts SET content_loc5='你在那兒做什麼?' WHERE entry=-1230054; /* content: What are you doing over there? */
UPDATE script_texts SET content_loc5='不許偷盜貨物!' WHERE entry=-1230056; /* content: No stealing the goods! */
UPDATE script_texts SET content_loc5='嘿，我的東西被偷了!' WHERE entry=-1230059; /* content: Hey, my pockets were picked! */
UPDATE script_texts SET content_loc5='哈!你甚至不能想像你的努力全都白費了。' WHERE entry=-1230060; /* content: Ha! You can\'t even begin to imagine the futility of your efforts. */
UPDATE script_texts SET content_loc5='他們只是阻礙了一下。' WHERE entry=-1230062; /* content: They were just getting in the way anyways. */
UPDATE script_texts SET content_loc5='我要把你碎屍萬段!' WHERE entry=-1230064; /* content: I will crush you into little tiny pieces! */
UPDATE script_texts SET content_loc5='嗨，羅克諾特!' WHERE entry=-1230066; /* content: Hey, Rocknot! */
UPDATE script_texts SET content_loc5='我們走吧，寶貝。' WHERE entry=-1230067; /* content: Let\'s go, honey. */
UPDATE script_texts SET content_loc5='%s吻她的情人。' WHERE entry=-1230068; /* content: %s kisses her lover. */
UPDATE script_texts SET content_loc5='%s親吻娜瑪拉小姐' WHERE entry=-1230069; /* content: %s kisses Mistress Nagmara */
UPDATE script_texts SET content_loc5='這毫無意義的行動讓我很厭煩。我會從上空把你們都燒成灰!' WHERE entry=-1249002; /* content: This meaningless exertion bores me. I\'ll incinerate you all from above! */
UPDATE script_texts SET content_loc5='看起來需要再給你一次教訓，凡人!' WHERE entry=-1249003; /* content: It seems you\'ll need another lesson, mortals! */
UPDATE script_texts SET content_loc5='%s深深地吸了一口氣...' WHERE entry=-1249004; /* content: %s takes in a deep breath... */
UPDATE script_texts SET content_loc5='現在開始上課!' WHERE entry=-1289000; /* content: School is in session! */
UPDATE script_texts SET content_loc5='讓仇恨的圈圈就此展開吧!' WHERE entry=-1309000; /* content: Let the coils of hate unfurl! */
UPDATE script_texts SET content_loc5='我命令你把這些入侵者燒成灰燼!' WHERE entry=-1309003; /* content: I command you to rain fire down upon these invaders! */
UPDATE script_texts SET content_loc5='沙德拉，讓我成為你的化身!' WHERE entry=-1309006; /* content: Shadra, make of me your avatar! */
UPDATE script_texts SET content_loc5='來幫助我吧，我的孩子們!' WHERE entry=-1309007; /* content: Aid me my brood! */
UPDATE script_texts SET content_loc5='希瓦拉爾，給我憤怒的力量吧!' WHERE entry=-1309009; /* content: Shirvallah, fill me with your RAGE! */
UPDATE script_texts SET content_loc5='比塞克，你的祭司在召喚你的力量!' WHERE entry=-1309011; /* content: Bethekk, your priestess calls upon your might! */
UPDATE script_texts SET content_loc5='吞噬$n的軀體吧，我的小可愛們!' WHERE entry=-1309012; /* content: Feast on $n, my pretties! */
UPDATE script_texts SET content_loc5='我終於擺脫了奪魂者!' WHERE entry=-1309013; /* content: At last, I am free of the Soulflayer! */
UPDATE script_texts SET content_loc5='升級啦!' WHERE entry=-1309016; /* content: DING! */
UPDATE script_texts SET content_loc5='$n!我正在監視你!' WHERE entry=-1309018; /* content: $N! I\'m watching you! */
UPDATE script_texts SET content_loc5='凡人，逃跑對你不會有好處的!' WHERE entry=-1309021; /* content: Fleeing will do you no good, mortals! */
UPDATE script_texts SET content_loc5='哈卡的爪牙，聽從你的神的命令。聖潔的神廟已經受到威脅。神聖的土地已經被入侵者佔領!必須保護好血神祭壇。殺死入侵者!' WHERE entry=-1309023; /* content: Minions of Hakkar, hear your God. The sanctity of this temple has been compromised. Invaders encroach upon holy ground! The Altar of Blood must be protected. Kill them all! */
UPDATE script_texts SET content_loc5='%s看到他的迅猛龍在戰鬥中喪命後勃然大怒!' WHERE entry=-1309024; /* content: %s goes into a rage after seeing his raptor fall in battle! */
UPDATE script_texts SET content_loc5='我們不會失敗的!' WHERE entry=-1309025; /* content: The brood shall not fall! */
UPDATE script_texts SET content_loc5='%s發出了震耳欲聾的尖嘯聲!' WHERE entry=-1309026; /* content: %s emits a deafening shriek! */
UPDATE script_texts SET content_loc5='%s開始施展強效治療術!' WHERE entry=-1309027; /* content: %s begins to cast a Great Heal! */
UPDATE script_texts SET content_loc5='阿莎利水晶已經被摧毀了!我的兄弟們，忠誠於巫妖王,重新召喚它!' WHERE entry=-1329004; /* content: An Ash\'ari Crystal has fallen! Stay true to the Lich King, my brethren, and attempt to resummon it. */
UPDATE script_texts SET content_loc5='阿莎利水晶已經被摧毀了!屠宰房危險!' WHERE entry=-1329007; /* content: The Ash\'ari Crystals have been destroyed! The Slaughterhouse is vulnerable! */
UPDATE script_texts SET content_loc5='入侵者到達側門了!趕快通知瑞文戴爾男爵!' WHERE entry=-1329008; /* content: Intruders at the Service Gate! Lord Rivendare must be warned! */
UPDATE script_texts SET content_loc5='你想戲耍一下巫妖王創造的生物嗎?拉姆斯登，好好教訓這些入侵者。' WHERE entry=-1329013; /* content: So you see fit to toy with the Lich King\'s creations? Ramstein, be sure to give the intruders a proper greeting. */
UPDATE script_texts SET content_loc5='是我親自動手的時候了。來吧。進入我的領域，向強大的天譴軍團挑戰吧!' WHERE entry=-1329014; /* content: Time to take matters into my own hands. Come. Enter my domain and challenge the might of the Scourge! */
UPDATE script_texts SET content_loc5='你弄壞了我耗費數年才創造出的東西!我要殺了你!' WHERE entry=-1329016; /* content: Today you have unmade what took me years to create! For this you shall all die by my hand! */
UPDATE script_texts SET content_loc5='你們這些笨蛋以為可以這麼容易就打敗我嗎?看看納斯雷茲姆的真正力量吧!' WHERE entry=-1329017; /* content: You fools think you can defeat me so easily? Face the true might of the Nathrezim! */
UPDATE script_texts SET content_loc5='我的死不算什麼...光明...將...戰勝!' WHERE entry=-1329020; /* content: My death means nothing.... light... will... prevail! */
UPDATE script_texts SET content_loc5='塵歸塵，土歸土!' WHERE entry=-1409005; /* content: Ashes to Ashes! */
UPDATE script_texts SET content_loc5='來吧，我的僕人們!保護你的主人!' WHERE entry=-1409013; /* content: COME FORTH, MY SERVANTS! DEFEND YOUR MASTER! */
UPDATE script_texts SET content_loc5='以火焰淨化你!' WHERE entry=-1409015; /* content: BY FIRE BE PURGED! */
UPDATE script_texts SET content_loc5='感受薩弗隆的烈焰吧!' WHERE entry=-1409016; /* content: TASTE THE FLAMES OF SULFURON! */
UPDATE script_texts SET content_loc5='你以為你贏了?可能你需要另一堂痛苦的課程!' WHERE entry=-1409019; /* content: You think you\'ve won already? Perhaps you\'ll need another lesson in pain! */
UPDATE script_texts SET content_loc5='很好，$n。' WHERE entry=-1409023; /* content: Very well, $N. */
UPDATE script_texts SET content_loc5='誰敢破壞埃德薩拉斯的聖潔?懦夫，面對我!' WHERE entry=-1429001; /* content: Who dares disrupt the sanctity of Eldre\'Thalas? Face me, cowards! */
UPDATE script_texts SET content_loc5='終於...從他邪惡的魔爪下逃脫了!' WHERE entry=-1429002; /* content: At last... Freed from his cursed grasp! */
UPDATE script_texts SET content_loc5='快跑!他們來了!' WHERE entry=-1469002; /* content: Run! They are coming! */
UPDATE script_texts SET content_loc5='%s因皮膚閃著微光而驚訝退縮。' WHERE entry=-1469003; /* content: %s flinches as its skin shimmers. */
UPDATE script_texts SET content_loc5='在這個世界中，時間是你的敵人，但是它是我的戰友。這段你進行遊戲的時光其實反過來在遊戲你。因此我會說...' WHERE entry=-1469004; /* content: In this world where time is your enemy, it is my greatest ally. This grand game of life that you think you play in fact plays you. To that I say... */
UPDATE script_texts SET content_loc5='讓賽事開始吧!' WHERE entry=-1469005; /* content: Let the games begin! */
UPDATE script_texts SET content_loc5='燃燒吧!你這個不幸的人!燃燒吧!' WHERE entry=-1469009; /* content: BURN! You wretches! BURN! */
UPDATE script_texts SET content_loc5='這不可能!我是這裡的主人!你們凡人對於我來說一無是處!聽到了沒有?一無是處!' WHERE entry=-1469012; /* content: This cannot be!  I am the master here!  You mortals are nothing to my kind!  Do you hear me? Nothing! */
UPDATE script_texts SET content_loc5='還有法師?你應該小心使用你的魔法...' WHERE entry=-1469013; /* content: Mages too? You should be more careful when you play with magic... */
UPDATE script_texts SET content_loc5='戰士，我知道你的力量不只如此!讓我們來見識一下吧!' WHERE entry=-1469014; /* content: Warriors, I know you can hit harder than that! Let\'s see it! */
UPDATE script_texts SET content_loc5='牧師!如果你要繼續這麼治療的話，那我們來玩點有趣的東西!' WHERE entry=-1469016; /* content: Priests! If you\'re going to keep healing like that, we might as well make it a little more interesting! */
UPDATE script_texts SET content_loc5='薩滿，讓我看看你圖騰到底是什麼用處的!' WHERE entry=-1469018; /* content: Shamans, show me what your totems can do! */
UPDATE script_texts SET content_loc5='術士，不要隨便去玩那些你不理解的法術。看看會發生什麼吧?' WHERE entry=-1469019; /* content: Warlocks, you shouldn\'t be playing with magic you don\'t understand. See what happens? */
UPDATE script_texts SET content_loc5='獵人和你那討厭的豌豆射擊!' WHERE entry=-1469020; /* content: Hunters and your annoying pea-shooters! */
UPDATE script_texts SET content_loc5='盜賊?不要躲了，面對我吧!' WHERE entry=-1469021; /* content: Rogues? Stop hiding and face me! */
UPDATE script_texts SET content_loc5='愚蠢的人!這些蛋比你知道的要珍貴的多!' WHERE entry=-1469023; /* content: Fools! These eggs are more precious than you know! */
UPDATE script_texts SET content_loc5='不─不應該再發生的!我要你的頭顱來彌補你的暴行!' WHERE entry=-1469024; /* content: No - not another one! I\'ll have your heads for this atrocity! */
UPDATE script_texts SET content_loc5='%s在寶珠的控制力消失之前逃走。' WHERE entry=-1469033; /* content: %s flee as the controlling power of the orb is drained. */
UPDATE script_texts SET content_loc5='%s因神態失常而坐立不安。' WHERE entry=-1509000; /* content: %s senses your fear. */
UPDATE script_texts SET content_loc5='%s充滿能量!' WHERE entry=-1509001; /* content: %s bristles with energy! */
UPDATE script_texts SET content_loc5='%s凝視著$n!' WHERE entry=-1509002; /* content: %s sets eyes on $N! */
UPDATE script_texts SET content_loc5='它們來了。盡量別被它們幹掉，新兵。' WHERE entry=-1509003; /* content: They come now. Try not to get yourself killed, young blood. */
UPDATE script_texts SET content_loc5='記得嗎，拉賈克斯，上次我說會殺了你?' WHERE entry=-1509004; /* content: Remember, Rajaxx, when I said I\'d kill you last? */
UPDATE script_texts SET content_loc5='我們懲罰的時刻就在眼前!讓黑暗支配敵人的內心吧!' WHERE entry=-1509005; /* content: The time of our retribution is at hand! Let darkness reign in the hearts of our enemies! */
UPDATE script_texts SET content_loc5='恐懼是給敵人的!恐懼與死亡!' WHERE entry=-1509007; /* content: Fear is for the enemy! Fear and death! */
UPDATE script_texts SET content_loc5='厚顏無恥的笨蛋!我要親手殺了你!' WHERE entry=-1509010; /* content: Impudent fool! I will kill you myself! */
UPDATE script_texts SET content_loc5='牆壁遭到破壞!' WHERE entry=-1509022; /* content: The walls have been breached! */
UPDATE script_texts SET content_loc5='你已被終止。' WHERE entry=-1509026; /* content: You are terminated. */
UPDATE script_texts SET content_loc5='%s吸取你的法力後變成了石頭。' WHERE entry=-1509028; /* content: %s drains your mana and turns to stone. */
UPDATE script_texts SET content_loc5='嚐嚐我的厲害!' WHERE entry=-1509029; /* content: Come get some! */
UPDATE script_texts SET content_loc5='先殺再說...他們來了!' WHERE entry=-1509030; /* content: Kill first, ask questions later... Incoming! */
UPDATE script_texts SET content_loc5='我說了謊...' WHERE entry=-1509031; /* content: I lied... */
UPDATE script_texts SET content_loc5='讓你的死成為先例吧!' WHERE entry=-1531003; /* content: Let your death serve as an example! */
UPDATE script_texts SET content_loc5='我判你死刑!' WHERE entry=-1531009; /* content: I sentence you to death! */
UPDATE script_texts SET content_loc5='我來服務最後一個!' WHERE entry=-1531010; /* content: I serve to the last! */
UPDATE script_texts SET content_loc5='%s變弱了!' WHERE entry=-1531011; /* content: %s is weakened! */
UPDATE script_texts SET content_loc5='漂浮在這間屋子中的巨大眼球在盯著你看。你就站在神的面前。' WHERE entry=-1531012; /* content: The massive floating eyeball in the center of the chamber turns its gaze upon you. You stand before a god. */
UPDATE script_texts SET content_loc5='只有肉跟骨頭。人類真容易淪為犧牲者...' WHERE entry=-1531013; /* content: Only flesh and bone. Mortals are such easy prey... */
UPDATE script_texts SET content_loc5='注意你的禮貌，兄弟。 讓我們好好地歡迎我們的客人。' WHERE entry=-1531014; /* content: Where are your manners, brother. Let us properly welcome our guests. */
UPDATE script_texts SET content_loc5='那會令人相當痛苦...' WHERE entry=-1531015; /* content: There will be pain... */
UPDATE script_texts SET content_loc5='哦...好痛苦啊...' WHERE entry=-1531016; /* content: Oh so much pain... */
UPDATE script_texts SET content_loc5='你不能逃避死亡!' WHERE entry=-1531023; /* content: You will not escape death! */
UPDATE script_texts SET content_loc5='你的命運已結束!' WHERE entry=-1531030; /* content: Your fate is sealed! */
UPDATE script_texts SET content_loc5='維克洛爾，我能感受你的痛苦!' WHERE entry=-1531031; /* content: Vek\'lor, I feel your pain! */
UPDATE script_texts SET content_loc5='啊...歡迎來到我的大廳。' WHERE entry=-1533000; /* content: Ahh... welcome to my parlor. */
UPDATE script_texts SET content_loc5='一些小點心...' WHERE entry=-1533001; /* content: Just a little taste... */
UPDATE script_texts SET content_loc5='你們逃不掉的。' WHERE entry=-1533002; /* content: There is no way out. */
UPDATE script_texts SET content_loc5='對，跑吧!那樣傷口出血就更多了!' WHERE entry=-1533003; /* content: Yes, Run! It makes the blood pump faster! */
UPDATE script_texts SET content_loc5='去哪裡?做什麼?太多的選擇，可這一切都會在痛苦與死亡中終結。' WHERE entry=-1533005; /* content: Where to go? What to do? So many choices that all end in pain, end in death. */
UPDATE script_texts SET content_loc5='以主人之名，殺了他們!' WHERE entry=-1533010; /* content: Slay them in the master\'s name! */
UPDATE script_texts SET content_loc5='休想從我面前逃掉!' WHERE entry=-1533011; /* content: You cannot hide from me! */
UPDATE script_texts SET content_loc5='跪下求饒吧，懦夫!' WHERE entry=-1533012; /* content: Kneel before me, worm! */
UPDATE script_texts SET content_loc5='在你還能跑時快跑吧!' WHERE entry=-1533013; /* content: Run while you still can! */
UPDATE script_texts SET content_loc5='你失敗了!' WHERE entry=-1533014; /* content: You have failed! */
UPDATE script_texts SET content_loc5='可憐蟲!' WHERE entry=-1533015; /* content: Pathetic wretch! */
