UPDATE locales_npc_text SET ,Text0_1_loc9='MISSING ENGLISH Text0:Тирт в последнее время сам не свой, и его помощник тоже.',Text1_0_loc9='Johnson?  I think he\'s around here somewhere...',Text1_1_loc9='MISSING ENGLISH Text1:Джонсон? Я думаю, он где-то здесь…' WHERE entry=781;
