UPDATE locales_npc_text SET ,Text0_1_loc9='MISSING ENGLISH Text0:Ты $Gстудент:студентка; медицинских наук?',Text1_0_loc9='MISSING ENGLISH Text1:',Text1_1_loc9='MISSING ENGLISH Text1:' WHERE entry=5815;
