UPDATE locales_npc_text SET ,Text0_1_loc9='MISSING ENGLISH Text0:Я сейчас немного занята, обсуждаю взятку. Приходите попозже.',Text1_0_loc9='MISSING ENGLISH Text1:',Text1_1_loc9='MISSING ENGLISH Text1:' WHERE entry=9989;
